css:
@-moz-document domain("bilibili.com") {
        
    /* 搜藏的视频该看了 S ***********************************/
#page-fav .fav-sidenav {
    width: 100% !important;
}
/*左边所有*/
 #page-fav .fav-sidenav {
    position: relative;
    float: left;
    width: 180px;
    margin-right: -1px;
    font-size: 14px;
    color: #222;
    border-right: 1px solid #eee;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    width: 100%;
}
/*左边收藏夹*/
 #page-fav .col-full.master .fav-container .fav-item {
    padding-bottom: 24px;
    font-size: 14px;
    color: #222;
    cursor: pointer;
    float: left;
    width: 230px;
    height: 21px;
    border-right: 1px dotted #cccccc80;
    padding: 3px 9px;
    border-bottom: 1px dotted #cccccc40;
    margin: 1px;
    line-height: 21px;
    overflow: hidden;
}
/* 标签背景色 S **/
 #page-fav .fav-sidenav .fav-list-container li:nth-child(n) {
    background: rgba(245, 239, 10, 0.08);
}
#page-fav .fav-sidenav .fav-list-container li:nth-child(2n+1) {
    background: rgba(5, 158, 158, 0.09);
}
#page-fav .fav-sidenav .fav-list-container li:nth-child(3n+1) {
    background: rgba(34, 255, 255, 0.14);
}
#page-fav .fav-sidenav .fav-list-container li:nth-child(5n+1) {
    background: rgba(153, 170, 0, 0.2);
}
#page-fav .fav-sidenav .fav-list-container li:nth-child(4n+1) {
    background: rgba(153, 170, 0, 0.22);
}
#page-fav .fav-sidenav .fav-list-container li:nth-child(8n+1) {
    background: rgba(35, 95, 249, 0.04);
}
#page-fav .fav-sidenav .fav-list-container li:nth-child(5n) {
    background: rgba(255, 152, 0, 0.14);
}
#page-fav .fav-sidenav .fav-list-container li:nth-child(3n) {
    background: rgba(111, 125, 199, 0.08);
}
/* 标签背景色 E **/
/*搜藏夹文字*/
 #page-fav .fav-sidenav .text {
    display: inline-block;
    line-height: 21px;
    width: 145px;
    margin-right: 5px;
    font-size: 14px;
    vertical-align: middle;
    overflow: hidden;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    white-space: nowrap;
    height: 21px;
}
/*单个搜藏夹*/
 #page-fav .fav-sidenav .fav-item:hover {
    background-color: rgba(100, 181, 246, 0.37);
    box-shadow: 0px 1px 10px 3px #9c27b069;
}
/*单个收藏夹 ico*/
#page-fav .fav-sidenav .fav-item.cur .icon-fav-public {
    background-position: -86px -1047px;
    color: #F00;
    box-shadow: inset 0px 1px 12px 3px;
    border-radius: 50%;
}
/*单个搜藏夹 选中文字*/
#page-fav .fav-sidenav .fav-item.cur .text, #page-fav .fav-sidenav .fav-item.cur .num {
    color: #F00;
}
/*搜藏夹*/
 #page-fav .fav-sidenav .fav-list-container {
    position: relative;
    max-height: 268px;
    margin: 10px 0 20px;
    overflow: hidden;
    float: left;
    overflow-y: auto !important;
}
/*单个搜藏夹 下拉选项*/
.be-dropdown-menu {
    position: fixed;
    top: 412px;
    z-index: 10;
    padding: 6px 0;
    background-color: #fff;
    border: 1px solid #e5e9ef;
    border-radius: 4px;
    -webkit-box-shadow: 0 2px 4px rgba(0,0,0,.14);
    box-shadow: 0 2px 4px rgba(0,0,0,.14);
    margin: -9px 7px;
}
/*单个搜藏夹 下拉选项 内容*/
.be-dropdown-item {
    height: 30px;
    padding: 0 8px;
    line-height: 25px;
    text-align: center;
    font-size: 14px;
    color: #222;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    overflow: hidden;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    white-space: nowrap;
    -webkit-transition: background-color .2s ease;
    -o-transition: background-color .2s ease;
    transition: background-color .2s ease;
    cursor: pointer;
    margin-top: 4px;
}
/*专项搜藏夹*/
.fav-list-container .fav-list .fav-item {
    float: left;
    width: 231px;
}

/*下方视频*/
#page-fav .fav-main {
    float: left;
    width: 920px;
    min-height: 600px;
    border-left: 1px solid #eee;
    width: 100% !important;
    border-top: 11px ridge rgba(196, 229, 255, 0.4);
}


/* 搜藏的视频该看了 E ***********************************/

/* func: Bilibili 更多的搜藏栏 S ********************************************************************************/

/* 更多的关注用户搜藏栏 S *****************************************/

/*边框*/
 .follow-dialog-wrap .follow-dialog-window {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    -ms-transform: translateY(-50%);
    width: 420px;
    background: #fff;
    left: 50%;
    margin-left: -210px;
    border-radius: 4px;
    width: 920px;
    margin-left: -24%;
}
/*列表*/
 .follow-dialog-wrap .follow-dialog-window .content .group-list {
    max-height: 311px;
    height: 250px;
    overflow-y: auto;
}
/*列表首行*/
 .follow-dialog-wrap .follow-dialog-window .content .group-list li.special-group {
    padding-bottom: 16px;
    width: 830px;
    height: 34px;
}
.follow-dialog-wrap .follow-dialog-window .content .group-list li {
    padding-bottom: 24px;
    list-style-type: none;
    font-size: 14px;
    color: #222;
    cursor: pointer;
    padding-bottom: 24px;
    font-size: 14px;
    color: #222;
    cursor: pointer;
    float: left;
    width: 198px;
    height:21px;
    border-right: 1px dotted #cccccc80;
    padding: 3px 5px;
    border-bottom: 1px dotted #cccccc40;
    margin: 1px;
}
/* 列表 hover*/
 .follow-dialog-wrap .follow-dialog-window .content .group-list li:hover {
    color: #00a1d6;
    border-bottom: 1px solid #00F;
}
/* 方框*/
 .follow-dialog-wrap .follow-dialog-window .content .group-list li input[type=checkbox] + i {
    width: 20px;
    height: 20px;
    display: inline-block;
    margin-right: 18px;
    vertical-align: middle;
    margin-right: 7px !important;
}
/*新建搜藏用户分组*/
 .follow-dialog-wrap .follow-dialog-window .content .group-list .add-group {
    margin-top: 20px;
    margin-bottom: 10px;
    width: 311px;
    margin-top: 94px;
    position: absolute;
}
/* 更多的关注用户搜藏栏 E*****************************************/

/* AV 搜藏界面 S *****************************************/

/* 整个搜藏界面 */
 .collection-m {
    width: 920px;
    border-radius: 4px;
    background: #fff;
    overflow: hidden;
}
/* 搜藏列表内容 上级*/
 .collection-m .content {
    padding: 0px 20px;
    overflow: auto;
    height: 350px;
}
/* 搜藏列表li */
 .collection-m .content .group-list li {
    padding-bottom: 24px;
    font-size: 14px;
    color: #222;
    cursor: pointer;
    float: left;
    width: 198px;
    height:21px;
    border-right: 1px dotted #cccccc80;
    padding: 3px 9px;
    border-bottom: 1px dotted #cccccc40;
    margin: 1px;
}
/* 鼠标放上去的颜色 */
 .collection-m .content .group-list li:hover {
    color: #00a1d6;
    border-bottom: 1px solid #00F;
}
/*搜藏列表中小方块和右侧文字的间距*/
 .collection-m .content .group-list li input[type=checkbox] + i {
    margin-right: 7px !important;
}
/* 新建搜藏夹 按钮*/
 .collection-m .bottom {
    height: 76px;
    text-align: center;
    margin: 0 36px;
    border-top: 1px solid #e5e9ef;
}
/* 标签背景色 S **/
 .group-list li:nth-child(n) {
    background: rgba(245, 239, 10, 0.08);
}
.group-list li:nth-child(2n+1) {
    background: rgba(5, 158, 158, 0.09);
}
.group-list li:nth-child(3n+1) {
    background: rgba(34, 255, 255, 0.14);
}
.group-list li:nth-child(5n+1) {
    background: rgba(153, 170, 0, 0.2);
}
.group-list li:nth-child(4n+1) {
    background: rgba(153, 170, 0, 0.22);
}
.group-list li:nth-child(8n+1) {
    background: rgba(35, 95, 249, 0.04);
}
.group-list li:nth-child(5n) {
    background: rgba(255, 152, 0, 0.14);
}
.group-list li:nth-child(3n) {
    background: rgba(111, 125, 199, 0.08);
}
/* 标签背景色 E **/

/* 新加一个按钮位置 */
 .add-group {
    margin-top: 136px;
    position: absolute;
}
/* AV 搜藏界面 E *****************************************/

/* func: Bilibili 更多的搜藏栏 E ********************************************************************************/

/* func: 去掉分p显示时的省略文字样式 改为显示全部, 如果太长则文字会贴边 S *********************************************/

/* 展开按钮 */
 a.item.v-part-toggle {
    padding: 3px;
}
.multi-page.col-7 .item {
    width: 130px;
    /*分p默认宽度更改为130px*/
}
/*列表内容*/
 .multi-page .item {
    text-align: left;
    display: block;
    color: #222;
    border-radius: 4px;
    border: 1px solid #ccd0d7;
    overflow: hidden;
    float: left;
    padding: 3px 12px;
    margin: 0 4px 12px 0;
    background: #fff;
    height: 17px;
    line-height: 17px;
    min-width: 117px;
    -o-text-overflow: unset;
    text-overflow: unset;
    white-space: nowrap;
    -o-transition: .15s;
    transition: .15s;
    cursor: pointer;
}
