css:
@-moz-document domain("villagebbs.com") {

body {
    background: #e0e0e0 !important;
    font-family: 'Roboto', sans-serif !important;
}

#topnav ul li a.active,
tr.catbg th,
div.cat_bar,
.catbg {
	background: linear-gradient(to bottom, rgba(181,189,200,1) 0%,rgba(130,140,149,1) 36%,rgba(40,52,59,1) 100%)  !important; /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */

}

td a {
    font-weight: normal !important;
    color: #808080 !important;
}

td.stats,
td.info > p,
#index_common_stats,
#button_2,
#button_33,
#button_34,
#category_1,
#category_1_boards,
#posting_icons,
#board_2_childboards,
#category_8,
#category_4,
#category_4_boards,
#category_5,
#category_5_boards,
#category_6,
#category_6_boards,
#board_45,
#header,
#footer,
#topic_icons,
.table_grid th.first_th,
td.icon1.windowbg,
td.icon2.windowbg,
td.icon1.stickybg.locked_sticky,
td.icon2.stickybg.locked_sticky,
td.icon1.stickybg,
td.icon2.stickybg,
.roundframe,
.upperframe,
.lowerframe{
    display: none !important;
}

/* Bring back the round frame in forms so we can still post messages/replies */
form .roundframe {
	display: block !important;    
}

td {
    border: none !important;
}

tbody.divider {
 display: none !important;   
}

/* #category_7_boards {
 	display:flex !important;
     flex-direction:column !important;   
} */

table.table_list tbody.content td.icon {
    width: 51px;
}

.icon {
    flex: 1 !important;
}

.info {
    flex: 1;
 	flex-grow: 1 !important;   
}

.lastpost {
    width: 275px !important;
    flex-basis: 275px !important;
}

#category_7.header {
 	display: none !important;   
}

#board_5 {
    order: 1 !important;
}

#board_2 {
    order: 3 !important;
}

#board_19 {
 	order: 2 !important;
}

.windowbg2 {
 	padding: 0px !important;
    margin: 0 0 2px 0 !important;
}

.icon.windowbg {
 padding: 0px !important;   
}

.icon > a > img[title="New Posts"] {
 	background-color: rgba(180,227,145,1);
 	content: url('https://d30y9cdsu7xlg0.cloudfront.net/png/900175-200.png');
    height: 30px;
    width: 30px;
    padding: 10px;
}

.icon > a > img[title="No New Posts"] {
    background-color: rgba(196,0,0,0.5);
    content: url('https://d30y9cdsu7xlg0.cloudfront.net/png/900185-200.png');
    height: 30px;
    width: 30px;
    padding: 10px;
}

#messageindex .table_grid tr {
 margin: 0px 0 10px 0 !important;   
    height: 50px;
}

#messageindex .table_grid th:nth-child(3) {
 display: none !important;   
}
 
.table_grid .subject.windowbg2,
.table_grid .subject.stickybg,
.table_grid .subject.stickybg2 {
    padding-left: 15px !important;
}

#personal_messages .table_grid th.first_th {
 display: inline-block !important;   
}

.topic_table .subject div span a {
/* 	text-decoration: underline !important; */
    font-size: 14px !important;
}

.topic_table .subject div p,
.topic_table .subject div p a{
	color: #a0a0a0 !important;
}

#messageindex.topic_table .table_grid tr {
	border-bottom: 1px solid #fff !important;   
}

#messageindex.topic_table .table_grid img[alt="New"] {
 	background-color: rgba(180,227,145,1);
 	content: url('https://d30y9cdsu7xlg0.cloudfront.net/png/900175-200.png');
    height: 20px;
    width: 20px;
    padding: 5px;
    float: left;
    margin-right: 10px;
}

.navigate_section a {
	font-size: 13px !important;
    color: #808080 !important;
}

.navigate_section li.last a {
	font-weight: bold !important;   
}

.navigate_section li:nth-child(1) a {
	display: inline-block;
    width: 76px;
    overflow: hidden;
}
}
