css:
@-moz-document url-prefix("https://www.brickplanet.com/forum/") {
@font-face {
    font-family: 'Hind';
    font-weight: 400;
    src: url(https://fonts.gstatic.com/s/hind/v8/5aU69_a8oxmIdGl4BA.woff2) format('woff2');
}
@font-face {
    font-family: 'Hind';
    font-weight: 500;
    src: url(https://fonts.gstatic.com/s/hind/v8/5aU19_a8oxmIfJpbERySjQ.woff2) format('woff2');
}
@font-face {
    font-family: 'Hind';
    font-weight: 600;
    src: url(https://fonts.gstatic.com/s/hind/v8/5aU19_a8oxmIfLZcERySjQ.woff2) format('woff2');
}
:root {
    --main-background-color: #EDEDED !important;
}
body {
    color: #4b4c4f !important;
    font-family: 'Hind', sans-serif;
}
.container {
    background: #fff !important;
    box-shadow: 0 2px 2px #bbb !important;
    border-radius: 0px !important;
}
.stat-left {
    color: rgba(0, 0, 0, 0.87);
    font-size: 14px;
    text-align: left;
    font-weight: 500 !important;
}
.stat-right {
    font-size: 14px !important;
}
.forum-user-favorite-group {
    background: #ffffff00 !important;
}
.forum-topic-container .thread-content-title a {
    color: #333 !important;
}
.container-header {
    background-color: #0288d1 !important;
    border-radius: 0px;
    padding: 15px 25px !important;
    font-size: 18px !important;
}
.container-header div {
    font-weight: 500 !important;
}
.topic-divider {
    background-color: #fff0;
}
.forum-topic-container:hover {
    background: #fff !important;
}
.forum-thread-header {
    background: #0a69bb00 !important;
}
.forum-thread-header a {
    color: #039be5 !important;
    font-size: 14.5px !important;
    font-weight: normal !important;
    padding: 0px 5px !important;
}
.thread-header-adjustment {
    text-align: center !important;
}
.user-online {
    width: 10px !important;
    height: 10px !important;
    background: green !important;
    margin-left: 0px;
    margin-right: 0px !important;
}
.user-offline {
    width: 10px !important;
    height: 10px !important;
    background: #c2c2c2 !important;
    margin-left: 0px;
    margin-right: 0px !important;
}
.forum-thread-title {
    background-color: #0288d1 !important;
    padding: 15px 25px !important;
    color: white !important;
}
.forum-thread-title .auto span {
    font-size: 18px !important;
}
.forum-thread-title .button-green {
    position: absolute !important;
    top: -100% !important;
    right: 16% !important;
}
.forum-thread-header {
    color: #777 !important;
}
.button-green {
    font-weight: 400 !important;
}
.container-header {
    font-weight: normal !important;
}
.thread-content-title {
    font-weight: normal !important;
    font-size: 18px !important;
}
.normal-input,
.forum-input, .top-bar input {
    background: #f7f7f7 !important;
    border: 2px solid #dcdcdc !important;
    border-radius: 0px !important;
    color: #333 !important;
}
.forum-quote {
    background: #36363e00 !important;
    box-shadow: 0px 0px #36363e00 !important;
    border-left: 5px solid #0288d1;
    border-radius: 0px;
}
.forum-quote-info {
    color: #000;
    font-weight: 400 !important;
}
.forum-user-favorite-group {
    box-shadow: 0px 0px #36363e00 !important;
}
.forum-topic:hover {
    background: #fff;
}
.grid-x .large-7 .grid-x .shrink div {
    width: 75px !important;
    height: 75px !important;
    background-color: #0000 !important;
    border: 1px solid #eee !important;
}
.thread-content-post {
    font-size: 12px !important;
}
.forum-top-links {
    font-weight: 600;
}
.report-abuse-forum-icon {
    color: #9d9d9d !important;
}
.quote-a i {
    color: #5c5c5c !important;
}
.forum-quote .forum-quote-info {
    color: #333 !important;
}
.forum-top-links div a {
    color: #323a45 !important;
}
.button-green {
    background-color: #2196F3 !important;
        box-shadow: 0 2px 5px 0 rgba(0,0,0,0.16), 0 2px 10px 0 rgba(0,0,0,0.12) !important;
    text-transform: uppercase;
        padding: 0 2rem !important;
        line-height: 36px !important;
    height: 36px !important;
    letter-spacing: .5px !important;
        border-radius: 2px !important;
            transition: box-shadow .3s ease-out, background .6s ;
      background-position: center;

   
}
.button-green:hover {
     box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .3), 0 2px 15px 0 rgba(0, 0, 0, .28) !important;
     background: #2196F3 radial-gradient(circle, transparent 1%, #47a7f5 1%) center/15000%;
}
.button-green:active {
     background-color: #6eb9f7;
  background-size: 100%;
  transition: background 0s;
}

.button-blue, .button-grey {
    background-color: #0000 !important;
    color: black !important;
}
.button-blue:hover, .button-grey:hover{
    text-decoration:underline !important;
}
.forum-topic-links a {
    color:#3d3d3d;
     transition: all .3s ease-out;
}
.forum-topic-links .clearfix:hover {
    background-color: #f2f2f2;
}
.forum-level {
    color:#393939;
}
.pagination li a {
    color: black;
}
.pagination .current a {
    color: white;
}
.side-bar li {
     float: left;
}
.side-bar::-webkit-scrollbar-thumb {
        background-color: #43444f00 !important;
}
.side-bar::-webkit-scrollbar-track {
        background-color: #43444f00 !important;
}
.side-bar::-webkit-scrollbar {
        background-color: #43444f00 !important;
}
.side-bar-inner .sbdivider {
    background-color: #43444f00 !important;
}
.side-bar-inner span {
    color: white !important;
}
.side-bar-inner i {
   display:none !important;
}
.side-bar {

    box-shadow: #0000 0px 0px 0px !important;
    background: #0288d1;
    height: 61px !important;
    width: 100% !important;
    position: absolute !important;
}
.side-bar-inner ul {
    height:100% !important;
    margin-left: 6% ;
}
.top-bar {
    pointer-events: none;
    background: #0000 !important;
     box-shadow: #0000 0px 0px 0px !important;
    position: absolute !important;
   
}
.top-bar a {
     color: white !important;
}
.top-bar input {
    width:20%;
        margin: 0 55% !important;
    pointer-events:all !important;
}
.top-bar a .show-for-large {
    display:none !important;
}
.top-bar a .hide-for-large {
    display: inherit !important;
    background-color: white;
    width: 45px;
    height: 45px !important;
    padding: 3px;
    border-radius: 50%;
}

.top-bar ul {
    background: #0288d100;
    pointer-events:all !important;
    
}
.grid-margin-x>.sidebar-shrink {
    width:auto !important;
}
.side-bar-inner {
    margin-top:-15px !important;
    height:100% !important;
}
.sbtitle {
    display: none !important;
}
.side-bar ul ul {
    display:none !important;
}
.side-bar-inner ul li {
    height: 100% !important;
    text-align: center !important;
    
}
.side-bar-inner ul li a  {
    height: 100% !important;
     border: 0px solid #0000;
    font-size: 14.5px !important;
    line-height: 64px !important;
    padding: 0px 15px !important;
    transition: background .3s ease !important;
    
}
.side-bar-inner ul li a:hover,.side-bar-inner ul li a.active:hover  {
    border: 0px solid #0000;
    background: #047aba !important;
}
.side-bar-inner ul li a.active {
    border: 0px solid #0000;
   background: #0288d1;
}
.user-dropdown, .user-notifications, .search-dropdown {
    background: #fdfdfd !important;
    border-radius: 0px;
    position:absolute;
}
#user-notifications i {
    display:none;
}
.chats-row-snippet {
    color: #4c4c4c !important;
}
.chats-row-time {
    color: #c3c3c3 !important;
    font-weight:400 !important;
}
.user-dropdown .ddivider, .user-chat-row-divider {
    background: #eee !important;
}
.dropdown-body a, .search-dropdown ul li a span.name {
    color: #424242 !important;
}
.search-dropdown ul li a span.name i {
    color: grey !important;
}
.user-dropdown .dropdown-body ul li a:hover {
    background-color: #ececec !important;
}
.menu .menu-link a .menu-avatar-preview-thumbnail, .user-dropdown .dropdown-header .avatar-preview-thumbnail, .notifications-user-thumbnail-48, .search-dropdown ul li a img {
        background-color: #fff !important;
        

}
.notifications-user-thumbnail-48, .search-dropdown ul li a img {
    
border: 1px solid #eee !important;
}
.dropdown-username a, .user-notifications-header div {
    color: white !important;
}
.user-notifications-header .shrink a {
    color: #1a147b !important;
}
.dropdown-header, .user-notifications-header {
    background: #0288d1 !important;
}
.notification-row:hover {
    background-color: #f0f0f0 !important;
}
.notification-row, .search-dropdown ul li a{
    transition: background-color .2s ease !important;
}
.search-dropdown ul li a:hover{
   background-color: #ececec !important;
}
.search-dropdown {
    width: 30% !important;
    padding: 15px 15px !important;
    left:55% !important;
}
}

@-moz-document url("https://www.brickplanet.com/forum/") {
.container {
    box-shadow: 0 2px 2px #bbb0 !important;
}
.forum-topic .topic-title a {
    color: #333 !important;
    font-weight: 500 !important;
}
.forum-topic .topic-description {
    color: #555 !important;
    font-size: 13px !important;
}
.topic-divider {
    background: #e8e8e8 !important;
}

}

@-moz-document url-prefix("https://www.brickplanet.com/forum/thread/") {
.container.md-padding {
    padding: 0px;
    width: 100% !important;
    border-radius: 0px !important;
}
.forum-content-wrapper::after {
    content: "";
    border-bottom: 1px solid #eee;
    width: 95%;
    display: block;
    margin-left: auto;
    margin-right: auto;
    margin-top: 25px;
    margin-bottom: 25px;
}
.grid-margin-x > .large-10 {
    width: 100%;
    margin: 0px;
}
.forum-main-content {
    font-size: 14.5px !important;
}
.forum-sig {
    font-size: 14px !important;
    color: #666 !important;
    border-top: #0000 !important;
}
.forum-thread-header {
    font-size: 14px !important;
}
body {
    color: rgba(0, 0, 0, 0.87) !important;
}

}
