css:
@-moz-document url("https://www.omegle.com/") {
    /* add in sliding messages */
     head {
        display: none;
    }
    html {
        overflow-x: hidden;
        overflow:hidden;
    }
  /* i really want this to be global */
    ::-webkit-scrollbar {
        width: 0;
        /* remove scrollbar space */
        background: invisible;
        /* optional: just make scrollbar invisible */
    }
    body {
        background-color:#f1f1f1;
    }
    html {
        font-family:'Helvetica';
    }
    .strangermsg, .logtopicsettings {
        font-size: 17px;
        font-weight: normal;
        float:left;
        display:inline-block;
        color: black;
        /* padding-right: 12px; */
        padding: 6px 8px;
        padding-right: 12px;
        background-color:#dedede;
        clear: both;
        height: auto;
        border-radius: 15px;
        margin: 1px;
        margin-right: 10px;
    }
    .disconnectbtn {
        color:black;
        background:white;
    }
    .statuslog {
        padding-left:14px;
        background-color:#FFAD3F;
        font-size: 17px;
        color: white;
        font-weight: bold;
        float:left;
        display:inline-block;
        /* padding-right: 12px; */
        /* Old browsers */
        background: -moz-linear-gradient(top, #FFA52F 0%, #FF9700 100%);
        /* FF3.6-15 */
        background: -webkit-linear-gradient(top, #FFA52F 0%, #FF9700 100%);
        /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(to bottom, #FFA52F 0%, #FF9700 100%);
        /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        padding: 6px 8px;
        clear: both;
        height: auto;
        border-radius: 15px;
        margin: 1px;
        margin-right: 10px;
    }
    .youmsg {
        font-size: 17px;
        color: white;
        padding-right: 12px;
        padding: 6px 8px;
        padding-right: 12px;
        background: #3abaff;
        /* Old browsers */
        background: -moz-linear-gradient(top, #3abaff 0%, #1e9dff 100%);
        /* FF3.6-15 */
        background: -webkit-linear-gradient(top, #3abaff 0%, #1e9dff 100%);
        /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(to bottom, #3abaff 0%, #1e9dff 100%);
        /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        clear: both;
        height: auto;
        margin: auto;
        border-radius: 15px;
        margin: 1px;
        margin-right:10px;
        float: right;
        display: inline-block;
    }
    .youmsg .msgsource, .strangermsg .msgsource {
        display: none;
    }
    .youmsg:before {
        content:" ";
        font-weight: bold;
    }
    .strangermsg:before {
        content:" ";
        font-weight: bold;
    }
    .chatmsgwrapper {
        border-radius:10px;
        height:auto;
        padding-left:4px;
    }
    /*.statuslog {
        color: !important;
    }
    */
     .chatmsg {
        border-radius:10px;
        height: auto;
        padding-left:2px;
        background-color: rgba(0, 0, 0, 0);
        margin-right: -20px;
    }
    .sendbtnwrapper {
        height:30px;
        border-radius: 10px;
    }
    #adwrapper {
        display:none;
    }
    }
    .logwrapper {
        margin-bottom:9px;
        margin-top:9px;
    }
    .logbox, .logwrapper {
        border-radius:10px;
    }
    .logbox {
        margin-bottom:10px;
    }
    .sendbtn {
        font-weight: bold;
        height:30px;
        color: white;
        border-radius: 100px;
        background: #3abaff;
        /* Old browsers */
        background: -moz-linear-gradient(top, #3abaff 0%, #1e9dff 100%);
        /* FF3.6-15 */
        background: -webkit-linear-gradient(top, #3abaff 0%, #1e9dff 100%);
        /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(to bottom, #3abaff 0%, #1e9dff 100%);
        /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    }
    .sendbtn:hover {
        background: rgba(39, 170, 240, 1);
        background: -moz-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(39, 170, 240, 1)), color-stop(100%, rgba(25, 145, 230, 1)));
        background: -webkit-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: -o-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: -ms-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: linear-gradient(to bottom, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
    }
    .sendbtn:active {
        background: rgba(23, 157, 230, 1);
        background: -moz-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23, 157, 230, 1)), color-stop(100%, rgba(12, 124, 209, 1)));
        background: -webkit-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: -o-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: -ms-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: linear-gradient(to bottom, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
    }
    .disconnectbtnwrapper {
        height:30px;
        border-radius: 100px;
    }
    .disconnectbtn {
        height:30px;
        border-radius: 100px;
        color: black;
    }
    #feedback, #introtext, #mobilesitenote {
        display:none;
    }
    .newbtn .disconnectbtn {
        background: #3abaff;
        /* Old browsers */
        background: -moz-linear-gradient(top, #3abaff 0%, #1e9dff 100%);
        /* FF3.6-15 */
        background: -webkit-linear-gradient(top, #3abaff 0%, #1e9dff 100%);
        /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(to bottom, #3abaff 0%, #1e9dff 100%);
        /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        color: white;
        font-weight: bold;
    }
    .newbtn .disconnectbtn:hover {
        background: rgba(39, 170, 240, 1);
        background: -moz-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(39, 170, 240, 1)), color-stop(100%, rgba(25, 145, 230, 1)));
        background: -webkit-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: -o-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: -ms-linear-gradient(top, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%);
        background: linear-gradient(to bottom, rgba(39, 170, 240, 1) 0%, rgba(25, 145, 230, 1) 100%)
    }
    .newbtn .disconnectbtn:active {
        background: rgba(23, 157, 230, 1);
        background: -moz-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(23, 157, 230, 1)), color-stop(100%, rgba(12, 124, 209, 1)));
        background: -webkit-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: -o-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: -ms-linear-gradient(top, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
        background: linear-gradient(to bottom, rgba(23, 157, 230, 1) 0%, rgba(12, 124, 209, 1) 100%);
    }
    .disconnectbtn:hover {
        background-color:#dedede
    }
    .disconnectbtn:active {
        background-color:#c4c4c4;
    }
    .btnkbshortcut {
        font-size:.1px;
        color:black;
    }
    body {
        height:100%;
        max-height:980px;
    }
    #intro {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        border: solid 1px white;
        color: whitesmoke;
        font: 24pt sans;
        width:960px;
    }
