css:
@-moz-document domain("userstyles.org") {
body, #nav-view, #navigation, #notes_textarea, #landing_page_div .top_landing_page_div, #landing_page_div .bottom_landing_page_div, #nav-view-responsive, input.DateBox, input.InputBox, input.SmallInput, textarea, .Preview {
    background-color: #000;
}
div, li, span, label, p, pre, h1, .style_title, .div, #share_div, #headline, #information_key_left, #infomation_value_left, #information_key, #information_value, #applies_link, .text_style_page, #notes_textarea, .navigation_title, .white_button, .customize_button, #send_feedback_button, .title_setting, #copyrights, #stylish-code, .style_author_long, #ads, #landing_page_div .bottom_landing_page_div .android_feature_div .android_feature_text div, #landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_subtitle, #landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_text, #landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_helpus, .overlay_background .overlay_container .overlay_content .overlay_title, .overlay_background .overlay_container .overlay_content .overlay_subtitle, .overlay_background .overlay_container .overlay_content .overlay_text, #category_style_card_title, #top_android_button .android_button_text, .no-screenshots, input.SmallInput:focus, input.InputBox:focus, textarea:focus, input.DateBox, input.InputBox, input.SmallInput, textarea, .Button, .statistic table, th, td, .EmoticonBox {
    color: #aaa;
}
dt, dd, h4 a, h4 a:visited, h4 a:hover, #title_long a, #title_long a:visited, #title_long a:hover, .header_button a {
    color: #aaa !important;
}
input, textarea {
    background-color: #080808;
    color: #aaa;
    box-shadow: none !important;
    border-style: none !important;
}
.linksBox .navigation_link:last-child, #single_discussion {
    box-shadow: 0 1px 0 0 #222, 0 0 0 0 #222;
}
#archive_style_button {
    box-shadow: 0 6px 16px 0 rgba(10,10,10, 1);
}
.navigation_link:hover, .white_button, .customize_button, .css_button, #send_feedback_button, .advancedsettings_shown, #stylish-code, .style_card, .see_more, .view_details, .div-arrow, .overlay_background .overlay_container, #style_installed_button, .style_category_card_wrapper, #top_android_button, .listing-no-screenshot, .EmoticonBox, .EmotifyDropdownActive {
    background-color: #111;
}
.white_button:hover, .customize_button:hover, .css_button:hover, #send_feedback_button:hover, .see_more:hover, .view_details:hover, #top_android_button:hover, input.SmallInput:focus, input.InputBox:focus, textarea:focus {
    background-color: #080808;
}
pre, #footer {
    background-color: #080808;
}
.white_button, .customize_button, #send_feedback_button, #stylish-code, .div-arrow {
    border: none;
}
#update_style_button, .Tag {
    color: #000;
}
.sketch-picker, .oval {
    background: #222 !important;
}
.flexbox-fix {
    border-top: 1px solid #111 !important;
}
#overlay-info, .overlay_background {
    background-color: rgba(0, 0, 0, 0.97);
}
.style_card_long, #title_div {
    box-shadow: inset 0 -1px 0 0 #333;
}
.statistic table, th, td, .EmoticonBox {
    border: 1px solid #222;
}
pre {
    border: 1px dashed #aaa;
}
#landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_seperator, .screenshot_wrapper {
    border-bottom: solid 1px #222;
}
#style_installed_button {
    border: solid 1px #185f36;
}
#header {
    background-color: #181824;
    box-shadow: 0 7px 12px 0 rgba(32,32,64, 0.13), 1px 0 0 0 #000;
}
#Head {
    background: #024;
}
.Tag {
    background: #aaa;
}
.Button {
    text-shadow: 0 1px 0 #000;
    background: #111;
    background-image: none;
    box-shadow: none;
    border: none;
}
.Button:hover {
    color: #555;
}
.Button.Active, .ButtonGroup.Open .Button.Handle, .Button:focus, .Button:active {
    background-color: #222;
}
}
