css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {

body {
    background:url("http://th02.deviantart.net/fs71/PRE/f/2013/194/7/a/quilava_by_pluivantlachance-d6dbe47.jpg") !important;
    background-color: #344B6C;
    color: #FFF;
}

    .highlighted em {
    background: #FE5A00;
}
.highlighted, .message-announce {
background: none ;
}
  
.message-announce {
    background: #186384;
    color: #FEEFA5;
    padding: 1px 4px 2px;
}

  .chat-log {
    background:#FEEFA5;
    background-size: cover; 
      font-weight: bold;
      color: #186384;
      border-width: 0;
}
    
.ps-room.ps-room-light {
background:#186384;
color:#FEEFA5;
}

    .challenge {
background: #186384;
border: 1px solid #2980b9;
color: rgba(0,0,0,.6);
padding: 1px 5px;
  color:#FEEFA5;
}

.pm-log em{
    color: #186384;
}

.userlist li button {
    font: 12px helvetica,arial,sans-serif;
    text-shadow: 1px 1px #000;
}
 
  .userlist li button:hover {
    background: #FEEFA5;
  }
  
  .userlist {
    background: #186384;
    border-right: 1px solid #186384;
  }

.menugroup {
    background:#186384;
}

.menugroup .button, .pad button {
    background: -webkit-linear-gradient(#FEEFA5, #FEEFA5);
    background: -o-linear-gradient(#FEEFA5, #FEEFA5);
    background: -moz-linear-gradient(#FEEFA5, #FEEFA5);
    background: linear-gradient(#FEEFA5, #FEEFA5);
    border: 1px solid #111133;
  color: #186384;
}

.menugroup .button:hover, .pad button:hover {
    background: -webkit-linear-gradient(#186384, #186384);
    background: -o-linear-gradient(#186384, #186384);
    background: -moz-linear-gradient(#186384, #186384);
    background: linear-gradient(#186384, #186384);
    border: 1px solid #222244;
  color: #FEEFA5;
}

.menugroup .button:active, .pad button:active {
    background: -webkit-linear-gradient(#FEEFA5, #FEEFA5);
    background: -o-linear-gradient(#FEEFA5, #FEEFA5);
    background: -moz-linear-gradient(#FEEFA5, #FEEFA5);
    background: linear-gradient(#FEEFA5, #FEEFA5);
    border: 1px solid #222244;
}

.roomlist {
    font-family: helvetica,arial,sans-serif;
    color: #FEEFA5;
  }
  
  .roomlist a.ilink {
    border: 1px solid #000;
    color: #186384;
    background: #FEEFA5;
  }
  
  .roomlist a.ilink:hover {
    border: 1px solid #000;
    color: #1D90FF;
    background: #DBE8EB;
  }
  
  .roomlist a.ilink small {
    color: #186384;
  }
  
.ladder table{
    background-color: #FEEFA5;
  color: #186384
}

.mainmenufooter a{
    text-decoration: none;
    color: #FFF;
    text-shadow: 1px 0px #000;
    font-family: helvetica,arial,sans-serif;
  }
  
  .mainmenufooter a:hover {
    color: #1D90FF;
    text-decoration: none;
  }
  
#header {
  background: #186384;
}

  .header .logo {
    opacity: 1;
  background: #186384;
  }

.userbar .username {
    font: 15px helvetica,arial,sans-serif;
    font-weight: bold;
  }
  
  .userbar .username:hover {
    border-bottom: 1px solid #1D90FF;
  }
  
 a {
    color:#FE9C00;
    text-decoration:none;
    margin-left: 3px;
    margin-right: 3px;
  }
    
  a:hover {
    color:#FE9C00;
    text-decoration: underline;
  }
  
  .ps-popup {
    background:#186384;
    font: 14px helvetica,arial,sans-serif;
    color: #FFF;
    border-color:#212121;
  }
  
  .popupmenu button {
    box-shadow: none;
    color: #FFF;
  }
  
  .popupmenu button:hover {
    box-shadow: none;
    color: #FFF;
    background:rgba(78, 78, 78, 1);
  }
  
  .popupmenu button.sel:hover {
    padding: 0px 3px;
    border: 1px solid #AAA;
    background:rgba(65, 65, 65, 1);
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.3);
  }

.pm-window h3 {
    border: 1px solid #212121;
    color: #FFF;
    background:rgba(0, 0, 0, 0.7);
    font-family: helvetica,arial,sans-serif;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    border: 1px solid #212121;
    color: #1D90FF;
    background:rgba(0, 0, 0, 0.8);
    font-family: helvetica,arial,sans-serif;
  }
  
  .message-pm {
    color: #000;
    font-style: italic;
    background: #186384;
  }
  
  .revealed {
    color: #186384;
  }
  
 a.ilink {
    /*font-style: italic;*/
    text-decoration: none;
    color: #FE9C00;
  }
  
   a.ilink:hover {
    color: #FE9C00;
  }

  .maintabbarbottom {
    background: #FEEFA5;
    border: 1px solid #000;
    box-shadow: none;

  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    color: #FFF;
    background: #186384;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button {
    color: #186384;
    background:#FEEFA5;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    color: #FEEFA5;
    background: #186384;
    border: 1px solid #000;
    font-family: helvetica,arial,sans-serif;
    text-shadow: none;
  }
  
  button {
    border: 1px solid #212121;
    background: none repeat scroll 0% 0% #186384;
    color: #FEEFA5;
    box-shadow: 0px 1px 0px 0px #5C5C5C inset, 0px 2px 3px rgba(0, 0, 0, 0.2);
    font: 12px helvetica,arial,sans-serif;
  }
  
  button:hover {
    color: #186384;
      background: #FEEFA5;
  }
  
    .tournament-bracket {
    font-size: 8pt;
    background:url("http://i.imgur.com/Q8vHT0Y.png");
    opacity:.8;
  }

  .tournament-title {
    background:#186384;
    color:#FEEFA5;
  }
  
  .tournament-status, .tournament-toggle {
  color: #186384;
    background: #FEEFA5;
  }
  
  .tournament-tools {
    padding: 10px;
    border-top: 1px solid #AAA;
    background:url("http://i.imgur.com/Q8vHT0Y.png");
  }
  
    .regular-button {
    box-shadow: 0px 1px 0px 0px #FFFFFF inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
    border: 1px solid #A8A8A8;
    background: #EEEEEE;
    color: #000;
  }

  .regular-button:hover {
    box-shadow: 0px 1px 0px 0px #FFFFFF inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
    border: 1px solid #A8A8A8;
    background: #DBDBDB;
    color: #000;
  }

  .blue-button{
    border: 1px solid #474747;
    color: #474747;
    background: #6F9BB5;
    box-shadow: 0px 1px 0px 0px #92B0C1 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }

  .blue-button:hover {
    border: 1px solid #474747;
    color: #474747;
    background: #7CACC7;
    box-shadow: 0px 1px 0px 0px #92B0C1 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }

  .LV-button{
    border: 1px solid #212121;
    color: #FFF;
    background: rgba(0, 0, 0, 0.8);
    box-shadow: 0px 1px 0px 0px #414141 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }

  .LV-button:hover {
    border: 1px solid #212121;
    color: #1D90FF;
    background: rgba(0, 0, 0, 0.8);
    box-shadow: 0px 1px 0px 0px #414141 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
  }
  
  .closebutton, .minimizebutton {
    color: #000;
  }
  
  .minimizebutton:hover {
    color: #000;
    text-decoration: none;
  }
  
  .closebutton:hover {
    color:#000;
    text-decoration: none;
  }
  
  .setchart-nickname {
    background: #FEEFA5;
  }
  
  .setmenu button {
    background:#FEEFA5;
    color: #186384;
  }
  
  .setmenu button:hover {
    background:#FEEFA5;
    color: #186384;
  }
  
  .setchart {
    background:#FEEFA5;
  }

.utilichart .movenamecol {
    color: #000;
    font: 12px helvetica,arial,sans-serif;
  }

  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: #186384;
    color: #FFF;
    border: 1px solid 212121;
    box-shadow: none;
  }

.teambuilder-results .result {
    background: #FEEFA5;
    border-color: 1px solid #FFAB02;
}
      
  .teambuilder-results .result a:hover {
    background: #186384;
    border-color: 1px solid #186384;
}

  .teambar button {
    background: #FEEFA5;
    color: #186384;
  }
  
  .teambar button:hover {
    background: #186384;
    color: #FEEFA5;
  }
  
  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    background: #186384;
    color: #FEEFA5;
  }
  
  .utilichart .namecol {
    color: #FFF;
  }
  
  .switchmenu button, .movemenu button {
    border: 3px solid #222222;
    color: #000;
  }
  
  .switchmenu button:hover, .movemenu button:hover {
    border: 3px solid #222222;
    color: #222222;
  }
  
  .switchselect button:hover {
    color: #458;
  }
  
  .moveselect button:hover {
    color: #842;
  }
  
  .ps-room.ps-room-opaque {
    background:#FEEFA5;
    color: #FFF;
  }
  
  .battle-log h2 {
    background:#186384;
    color: #FFF;
    border: 1px solid #212121;
  }
  
  .battle-log .chat strong {
    color: #186384;
  }

  .leftbar {
    background: #186384;
  color:#FFF;
  }
  
  .rightbar {
    background: #186384;
  color:#FFF;
  }

.effect-volume em, .music-volume em {
  display: block;
  margin-top: -6px;
  font-size: 9pt;
  padding-left: 5px;
  color: #FEEFA5;
  }
.infobox, .ladder, .pm-window{
    position: relative;
    border: 1px solid #181818;
      border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0,0,0,.2);
    margin-top: 5px;
    color: #000000;
    background-color: #186384;
}
}
