@-moz-document url-prefix("http://localhost:8888/") {
  :root {
    --main-font-family: Consolas, monaco, monospace;
    --main-bg-color: #282c34;

    --atom-one-dark-cyan: #56b6c2;
    --atom-one-dark-blue: #61afef;
    --atom-one-dark-bg-1: #121417;
    --atom-one-dark-mono-2: #828997;
    --atom-one-dark-red-1: #e06c75;
    --atom-one-dark-mono-1: #abb2bf;
    --atom-one-dark-silver: #aaaaaa;
    --atom-one-dark-purple: #c678dd;
    --atom-one-dark-orange-2: #e5c07b;
    --atom-one-dark-bg-hl: #2f343d;
    --atom-one-dark-black: #0f1011;
    --atom-one-dark-gutter: #666d7a;
    --atom-one-dark-fg: #abb2bf;
    --atom-one-dark-green: #98c379;
    --atom-one-dark-red-2: #be5046;
    --atom-one-dark-orange-1: #d19a66;
    --atom-one-dark-mono-3: #5c6370;
    --atom-one-dark-accent: #aeb9f5;
    --atom-one-dark-bg: #282c34;
    --atom-one-dark-gray: #3e4451;
  }
  /* Intro page  */
  body {
    background-color: #282c34 !important;
    color: var(--atom-one-dark-silver);
  }

  .list_header {
    background-color: var(--atom-one-dark-bg);
  }

  div#notebook {
    font-size: 16px;
    background-color: var(--main-bg-color);
  }

  /* Dropdown menus */

  .dropdown-menu {
    background: rgba(50, 50, 50, 0.95);
    color: #b7b7b7;
  }

  .dropdown-menu > li > a,
  .dropdown-submenu > a:after,
  .dropdown-menu > li > a:visited:after {
    color: #b7b7b7;
  }

  .dropdown-menu .divider {
    background-color: #444;
  }

  .dropdown-menu > li > a:hover,
  .dropdown-menu > li > a:focus {
    color: #fff;
    background-color: #444;
  }

  .dropdown-menu > li > a:hover:after,
  .dropdown-menu > li > a:focus:after,
  .dropdown-menu > li > a:active:after {
    color: #fff;
  }

  div#header {
    background-color: var(--main-bg-color);
  }

  div#menus {
    background-color: var(--main-bg-color);
  }

  div#maintoolbar {
    background-color: var(--main-bg-color);
  }

  div#notebook-container {
    background-color: var(--main-bg-color);
  }

  body > #header .header-bar {
    background: #757575;
  }

  /* Help panel */
  div#pager {
    background-color: #282c34;
  }

  div#pager .ui-resizable-handle {
    top: 0px;
    height: 8px;
    background: #282c34;
    border-top: 1px solid #757575;
    border-bottom: 1px solid #757575;
  }
  div#pager pre {
    color: #9ac2c9;
    background-color: #282c34;
  }

  .btn-default {
    color: var(--atom-one-dark-silver);
    background-color: var(--atom-one-dark-bg);
    border-color: var(--atom-one-dark-gray);
  }

  .notification_widget {
    color: #777;
    background: rgba(240, 240, 240, 0.5);
    color: #333;
    background-color: #aaa;
    border-color: #757575;
  }

  span#login_widget > .button,
  #logout {
    color: #333;
    background-color: #aaa;
    border-color: #757575;
  }

  .form-control {
    color: #333;
    background-color: #aaa;
    border: 1px solid #757575;
  }

  .input_area {
    border: 1px solid #757575 !important;
  }

  .notebook_app div.input_prompt {
    color: var(--atom-one-dark-green);
  }

  .notebook_app div.output_prompt {
    color: var(--atom-one-dark-red-1);
  }

  .notebok_app div.output_area pre {
    /*         color: whitesmoke !important; */
    color: var(--atom-one-dark-silver) !important;
  }

  .notebook_app div.output_subarea pre {
    /*         color: whitesmoke !important; */
    color: var(--atom-one-dark-silver) !important;
  }

  .notebook_app div.text_cell_render,
  .notebook_app div.output_html pre {
    color: #979797;
  }

  /* Tooltips */
  .ipython_tooltip {
    background-color: var(--main-bg-color) !important;
  }
  .ipython_tooltip .tooltiptext pre {
    font-size: 100%;
    color: lightgrey;
    background-color: var(--main-bg-color) !important;
  }

  /*Terminal text coloring*/

  span.ansiblack {
    color: #323537;
  }

  span.ansiblue {
    color: #afc4db;
  }

  span.ansigray {
    color: #838184;
  }

  span.ansigreen {
    color: #8f9d6a;
  }

  span.ansipurple {
    color: #9b859d;
  }

  span.ansired {
    color: #cf6a4c;
  }

  span.ansiyellow {
    color: #c2bc15;
  }

  /*INPUT CELLS*/

  .CodeMirror {
    /*Background color of input cell*/
    /*       background: #141414; */
    background-color: #33333;
    color: var(--atom-one-dark-mono-1);
    /*       border-radius: .35em; */
    /*box-shadow: inset 10px 10px 16px -4px rgba(0, 0, 0, 0.44);*/
  }

  div.CodeMirror-selected {
    background: rgba(221, 240, 255, 0.19) !important;
    /*       background: beige!important; */
  }

  #texteditor-backdrop #texteditor-container .CodeMirror-gutter,
  .CodeMirror-gutters {
    background: #444;
    color: #777;
  }

  .CodeMirror-linenumber {
    color: #7c7c7c;
    font-size: 12px !important;
  }

  .CodeMirror-cursor {
    border-left: 1px solid #a7a7a7 !important;
    margin-top: -2px;
    min-height: 18px;
  }

  .cm-s-ipython.CodeMirror {
    background-color: var(--main-bg-color);
    /*background: #282C34;*/
    /*background: #2c303c;*/
    /* The color below is all of the text not spoken for by the code below. */
    color: #b5c2d9;
  }

  .cm-s-ipython div.CodeMirror-selected {
    background: #343d46 !important;
  }

  .cm-s-ipython .CodeMirror-gutters {
    background: #2b303b;
    border-right: 0px;
  }

  .cm-s-ipython .CodeMirror-linenumber {
    color: #7c7c7c;
    font-size: 10px;
  }

  .cm-s-ipython .CodeMirror-cursor {
    border-left: 1px solid #a7adba !important;
  }

  .cm-s-ipython span.cm-comment,
  .cm-s-default span.cm-comment {
    color: #7c7c7c;
  }

  .cm-s-ipython span.cm-atom,
  .cm-s-default span.cm-atom {
    color: #61afef;
  }

  .cm-s-ipython span.cm-number,
  .cm-s-default .cm-number {
    color: #e2a271;
  }

  .cm-s-ipython span.cm-property,
  .cm-s-default .cm-property {
    color: #b5c2d9;
  }

  .cm-s-ipython span.cm-attribute,
  .cm-s-default span.cm-attribute {
    color: #ea8080;
  }

  .cm-s-ipython span.cm-keyword,
  .cm-s-default .cm-keyword {
    color: #cc89e0;
  }

  .cm-s-ipython span.cm-string,
  .cm-s-default .cm-string {
    color: #94c273;
  }

  .cm-s-ipython span.cm-meta,
  .cm-s-default span.cm-meta {
    color: #51b6c3;
  }

  .cm-s-ipython span.cm-operator,
  .cm-s-default span.cm-operator {
    color: #5cadf1;
  }

  .cm-s-ipython span.cm-builtin,
  .cm-s-default .cm-builtin {
    color: #61afef;
  }

  .cm-s-ipython span.cm-variable,
  .cm-s-default span.cm-variable {
    color: #b5c2d9;
  }

  .cm-s-ipython span.cm-variable-2,
  .cm-s-default span.cm-variable-2 {
    color: #ea8080;
  }

  .cm-s-ipython span.cm-variable-3,
  .cm-s-default span.cm-variable-3 {
    color: #51b6c3;
  }

  .cm-s-ipython span.cm-def,
  .cm-s-default span.cm-def {
    /*       color: #e2a271; */
    color: var(--atom-one-dark-orange-2);
    /*color: var(--atom-one-dark-blue);*/
  }

  .cm-s-ipython span.cm-error {
    background: #bf616a;
    color: #a7adba;
  }

  .cm-s-ipython span.cm-bracket {
    color: #b5c2d9;
    font-weight: bold;
  }

  .cm-s-ipython span.cm-tag {
    color: #61afef;
  }

  .cm-s-ipython span.cm-link {
    color: #e2a271;
  }

  .cm-s-ipython span.cm-storage {
    color: #61afef;
  }

  .cm-s-ipython span.cm-entity {
    color: #61afef;
  }

  .cm-s-ipython span.cm-latex {
    color: #8fb36a;
  }

  .cm-s-ipython .CodeMirror-matchingbracket {
    text-decoration: underline;
    -moz-text-decoration-color: #37d6f2;
    text-decoration-color: #37d6f2;
    color: #b5c2d9 !important;
  }

  /*Markdown editing*/

  .CodeMirror.cm-s-default {
    background: #2c2c2c;
    color: #787474;
    border-radius: 0.35em;
    box-shadow: inset 10px 10px 16px -4px rgba(0, 0, 0, 0.44);
  }

  .cm-s-default span.cm-bracket {
    color: #c0c5ce;
  }

  .cm-s-default span.cm-header {
    color: #cf4a4c;
  }

  .cm-s-default span.cm-variable-2 {
    color: #7587a6;
  }

  .cm-s-default span.cm-link {
    color: #ffa0a0;
  }

  /*Markdown code block*/
  .rendered_html pre code {
    background: #1e1d1d;
  }

  .rendered_html pre,
  .rendered_html code {
    background: #1e1d1d;
    color: #87ceeb;
  }
}
