css:
@-moz-document domain(mediavida.com) {
	/* Deleting the advertising from the webpage.  */
 ._p-tp, ._p-ft {
	display: none;
}
/* Darker color to the webpage. */
 #header, .hero-menu {
	background-color: #121415;
}
#content, #footer, #usermenu, #title, header#title {
	background-color: #000;
}
header#title {
	border-bottom: 0;
}
.block .lv2, .block .lv2-t, .block.lv2 {
	background-color: #121415;
}
.news, .post, .post.z, #news-hero {
	background-color: #1c2225;
}
#side-nav a, #side-nav span.current, #topic, .post, .news-item, #splash-3, .block {
	border: 1px solid grey;
	border-radius: 0;
}
#side-nav a .fid {
	opacity: 1;
}
body {
	color: white;
}
#header {
	border-color:grey;
}
.btn {
	background-color: #303334;
	color: white;
	border-color: grey;
}
.btn-primary {
	background-color: #9b4008;
	border-color: #5a2505;
	color: white;
}
#sections > li > a, .fa, .rd {
	color: white;
}
.replies .rep {
	background-color: #14181a;
	border: 0;
}
.post.quote-shown {
	opacity: 1;
}
.splash-1, .splash-2 {
	border-radius: 0;
	border-width: 0;
}
.splash-2:last-child {
	border-width: 0;
}
}
