css:
@-moz-document url-prefix("https://my.bethlehem.church/ministryplatform") {
#appHeader {
    background-color: #659624;
}
#appHeader .organization-name {
    color: white;
}
#appHeader .headerLink, #appHeader .header-button {
    color: white;
}
#appHeader .header-button .fa {
    color: #f1efec
}
#quickAddToolbar {
    background-color: #999999;
}
#navTreePanel, .dijitToolbar.primary-toolbar {
    background-color: #dbdbdb;
}
.claro .dijitTreeRowHover, .claro .dijitTreeRowSelected {
    background-color: #999999 !important;
    border-color: #999999 !important;
    color: #dbdbdb;
}
.top-node {
    font-size: 13px;
}
.parent-node {
    font-size: 13px;
}
.dijitTreeNodeLoaded .parent-node .dijitTreeNodeContainer {
    padding-left: 20px;
    border-top: none !important;
    border-bottom: none !important;
}
.dijitTreeNodeLoaded .parent-node .dijitTreeNodeContainer .dijitTreeNode {
    border-left: 1px solid #545454;
}
.dijitTreeNodeLoaded .parent-node {
    background-color: #f2f1ee !important;
}
.top-node .fa {
    font-size: 18px;
}
i.fa, span.fa, .foreignKeyLink {
    color: #81c12d;
}
#navTreePanel .fa {
    color: #545454;
}
.claro .dijitTreeRowHover .fa, .claro .dijitTreeRowSelected .fa {
    color: #f1efec !important;
}
i.fa, span.fa {
    font-size: 18px;
}
.claro .dijitSelectLabel {
    height: 10px;
}
.dijitIcon .fa {
    margin-top: 0px;
}
.dp_fieldLabel {
    opacity: 0.75;
    padding-top: 2px;
    font-size: 13px;
    margin-right: 30px;
}
.record-type-title {
    opacity: 0.75
}
.parent-node .parent-node-icon {
    color: #704939;
    font-size: 18px;
}
.dijitTreeRow .fa {
    font-size: 18px;
}
.dp_formRow {
    min-height: 40px;
}
.form_edit {
    margin: 0;
    margin-top: -5px;
    margin-bottom: 5px;
}
.dp_fieldControl {
    margin-top: 2px;
    font-size: 13px;
}
.search-text-container {
    margin-right: 5px;
}
#mainGridToolbar, .claro .dijitToolbar {
    background-color: #f7f6f4;
    border-bottom: 1px solid #dde6dd;
}
.gridToolbarWidget {
    border-bottom: 1px solid #d3d3d3 !important;
}
.claro .dijitToolbar .dijitButton .dijitButtonNode, .claro .dijitToolbar .dijitDropDownButton .dijitButtonNode, .claro .dijitToolbar .dijitComboButton .dijitButtonNode, .claro .dijitToolbar .dijitToggleButton .dijitButtonNode, .claro .dijitToolbar .dijitComboBox .dijitButtonNode {
    background-color: #dbdbdb;
}
.claro .dijitToolbar .dijitButtonHover .dijitButtonNode, .claro .dijitToolbar .dijitDropDownButtonHover .dijitButtonNode {
    background-color: #999999;
    color: #dbdbdb;
}
.claro .dijitToolbar .dijitHover .fa {
    color: #f1efec;
}
.dijitDisabled .fa {
    color: grey;
    opacity: 0.5;
}
.dijitDisabled {
    opacity: 0.5;
}
.slick-row.selected, .slick-cell.selected {
    background: #f1efec;
}
.claro .dijitMenu .dijitMenuItemSelected td {
    background-color: #f1efec;
    border-color: #999999;
}
.dijitTitlePane, .claro .dijitTitlePaneTitle {
    background-color: #EBEDE9;
}
.dp_FormHeader {
    background-color: gainsboro;
    padding-right: 10px;
}
.form-header-border {
    border-bottom: none !important;
}
.slick-row.odd:hover {
    background-color: #d9fcc9;
}
.slick-row.even:hover {
    background-color: #d9fcc9;
}
li.token-input-selected-token span {
    color: #9e9e9e;
}
.claro .dijitTreeRowHover .fa, .claro .dijitTreeRowSelected .fa {
    color: #c0d6b8 !important;
}
.slick-row.selected, .slick-cell.selected {
    background: #c0d6b8;
}
.claro .dijitMenu .dijitMenuItemSelected td {
    background-color: #c0d6b8;
    border-color: #999999;
}
.drop-down-chevron .fa {
    margin-top: -5px;
    color: #81c12d;
}
.attached-files .attached-file:first-child {
    padding-top: 5px;
    border-top: 1px solid #81c12d;
}
.message-dialog .message-header .to-block ul.token-input-list-facebook {
    width: 99% !important;
    border: 1px solid #81c12d;
}
.message-dialog .message-tabs-container .dijitTabContainer .dijitTabPane {
    border: 1px solid #81c12d !important;
}
.task-dialog .task-tabs-container .dijitTabContainer .dijitTabPane {
    border: 1px solid #81c12d !important;
}
}
