css:
@-moz-document url-prefix("https://blog.roblox.com/") {
.hero-block {
    max-height: calc(30vh - 96px);
}
.control {
    padding: 23px 10px 19px;
}
.control p {
    font-size: 18px;
    margin-bottom: 14px;
}
.news-list.blog .logo {
    max-width: 200px;
    width: 200px;
}
.news-list .info {
    height: 0px;
    padding: 20px 0; /* */
}
.news-list .blurb {
    padding: 30px 50px 50px 8px;
}
.footer {
    padding: 0 0;
}
.footer-contents {
    padding: 19px 0 20px 0;
}
.has-limit.news-detail {
    margin: 0 50px;
}
.news-detail {
    padding: 0 70px 60px;
}
.news-detail .detail-heading {
    margin-bottom: 20px;
    padding-bottom: 28px;
}
footer .f-center ul {
    height: 60px;
}
}
