css:
@-moz-document domain("dynalist.io") {


/* Set shape for the item text */
.Node-contentContainer span:first-child {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
    padding-left:5px;
}

.Node-contentContainer span:last-child {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
    padding-right: 5px;
}


/* Reset items full-width background color */
.mod-color-label-1,
.mod-color-label-2,
.mod-color-label-3,
.mod-color-label-4,
.mod-color-label-5,
.mod-color-label-6{
    background-color: transparent !important;
}

/* Prepend a space to display a color hint when editing an item */
div.needsclick::before,
div.needsclick::before,
div.needsclick::before,
div.needsclick::before,
div.needsclick::before,
div.needsclick::before,
div.needsclick::before {
    content: " ";
}

/* Set the background color of the item, the color hint, and the item action menu */
.mod-color-label-1 span, .mod-color-label-1 a, .mod-color-label-1 div.needsclick::before, .set-color-label[data-color="1"] { background-color: #f44; color: white; }
.mod-color-label-2 span, .mod-color-label-2 a, .mod-color-label-2 div.needsclick::before, .set-color-label[data-color="2"] { background-color: #f84; color: white; }
.mod-color-label-3 span, .mod-color-label-3 a, .mod-color-label-3 div.needsclick::before, .set-color-label[data-color="3"] { background-color: #ff4; color: black; }
.mod-color-label-4 span, .mod-color-label-4 a, .mod-color-label-4 div.needsclick::before, .set-color-label[data-color="4"] { background-color: #ae4; color: black; }
.mod-color-label-5 span, .mod-color-label-5 a, .mod-color-label-5 div.needsclick::before, .set-color-label[data-color="5"] { background-color: #4af; color: black; }
.mod-color-label-6 span, .mod-color-label-6 a, .mod-color-label-6 div.needsclick::before, .set-color-label[data-color="6"] { background-color: #a0f; color: white; }

}
