css:
@-moz-document url-prefix("https://www.branah.com") {

div.main_content {
    position: relative;
    padding-left: 0;
}



#editor {
    width: 100%;
}


#branah-keyboard {
    width: 100%;
}


#page {
    width: 1500px;
    margin: 0 auto;
}


#branah-keyboard .branah-key, #branah-k25 {
    width: 100px;
    height: 100px !important;
}


#branah-backspace, #branah-tab, #branah-caps-lock, #branah-enter, #branah-right-shift, #branah-left-shift, #branah-left-ctrl, #branah-left-alt, #branah, #branah-right-ctrl, #branah-right-alt, #branah-escape {
    height: 100px !important;
    width: 10%;
}



#branah-space {
    width: 402px;
    height: 100px !important;
}


#branah-keyboard .branah-label-natural {
    color: #ffbdd8;
    font-size: 33px !important;
    font-family: arial !important;
}


#branah-keyboard .branah-label-shift{
         color: #ffbdd8;
    font-size: 33px !important;
    font-family: arial !important;
}
     

#branah-keyboard .branah-recessed-hover span, #branah-keyboard .branah-recessed span {
    color: #6fddff;
}

#branah-keyboard .branah-label-reference {
  color: #c7c7c7;
    font-size: 22px;
    line-height: 12px;
    margin-left: 8px;
    font-family: source sans pro;
    font-weight: 800;
}


textarea#editor {
     outline-width: 0;
    background: #000002 !important;
    border: none !important;
    FONT-size: 40px !important;
    color: #7ad3ff !important;
}


button {
    border-radius: 10px;
    background: white !important;
    border: 1px solid #efefef;
    box-shadow: 2px 2px 0px 0px #efefef;
    margin: 5px !important;
}

textarea#editor {
    background: #f7f7f7 !important;
    border: none !important;
    border-radius: 10px !important;
    FONT-size: 40px !important;
    color: #c5c5c5 !important;
      outline-width: 0;
    border: 1px solid whitesmoke !important;
         width: 1173px;
}

#header {
    margin-bottom: 0px;
    display: none;
}


#action button, a.soft_button {
    vertical-align: middle;
    line-height: 18px;
    color: #808080 !important;
    font-size: 15px !important;
    font-family: karla;
    background: #ffffff !important;
    padding: 10px !important;
    text-transform: uppercase;
    font-weight: 400;
     border-radius:5px !important;
     border: 1px solid #efefef !important;
    box-shadow: 2px 2px 0px 0px #efefef !important;
}


#page {
    width: 1600px;
    margin: 20px auto;
    padding: 15px;
    background: white;
}


h4 {
    text-align: left;
    /* color: red !important; */
    display: none;
}


#branah-keyboard button {
    font-size: 18px;
    color: #ffc1a2;
    font-family: karla;
    font-weight: 900;
    text-transform: uppercase;
}



h1, h3 {
    font-size: 40px;
    color: #a2a2a2;
    font-family: lato;
    font-style: italic;
    text-transform: uppercase;
    text-decoration: wavy !important;
    font-weight: 100;
}

p {
    text-align: justify;
    color: black;
    font-family: source sans pro;
    font-weight: 100;
}



.button {
    display: inline-block;
    padding: 10px 15px;
    background-color: #f0f0f0;
    border: 0px;
    border-radius: 5px;
}



#fcomments {
    display: block;
    text-align: center;
    background-color: transparent;
    font-family: source sans pro;
    font-weight: 100;
    font-size: 25px;
}

a:link, a:visited, a:active {
    color: #696969;
    text-decoration: none;
}


#share-buttons, #share-buttons:visited {

    display: none;
}

button#shrink, button#enlarge {
    width: 30px !important;
}


button#shrink, button#enlarge {
    width: 30px !important;
    background: #ffdcc6 !important;
    height: 41px !important;
    margin-top: 7px !important;
    border: 0px !important;
    color: white !important;
    box-shadow: 0px 0px !important;
    font-size: 20px;
    font-weight: 800;
}


div#keyboard {
    margin-bottom: 100px;
}



#page {
   width: 1196px;
    margin: 20px auto;
    padding: 15px;
    background: white;
}

#branah-keyboard .branah-key, #branah-k25 {
    width: 72px;
    height: 72px !important;
}

#branah-backspace, #branah-tab, #branah-caps-lock, #branah-enter, #branah-right-shift, #branah-left-shift, #branah-left-ctrl, #branah-left-alt, #branah, #branah-right-ctrl, #branah-right-alt, #branah-escape {
    height: 71px !important;
    width: 10%;
}

#branah-space {
    width: 402px;
    height: 71px !important;
}

button#branah-enter, button#branah-right-shift {
    width: 153px;
}
}
