css:
@-moz-document regexp("^https\\:\\/\\/docs\\.google\\.com\\/presentation\\/d\\/.+edit.*$") {
body {
    background-color: #222;
    color: #ddd;
}
#docs-chrome {
    background: #222;
}
.docs-titlebar-buttons {
    background-color: #222 !important;
}
#punch-start-presentation-left {
    color: #333;
    background-color: #eee !important;
}
#punch-start-presentation-right {
    color: #333;
    background-color: #eee !important;
}
.docs-icon-img::before {
    filter: invert(100%);
}
.sketchy-icon-accessible-img::before {
    filter: invert(100%);
}
.menu-button {
    color: #ddd !important;
    background: #222 !important;
}
#docs-toolbar-wrapper {
    background: #333 !important;
    border-top: #282828 !important; 
    border-bottom: #282828 !important;
}
.goog-toolbar-button {
    background-color: #222 !important;
    color: #ddd !important;
}
.goog-toolbar-combo-button {
    background-color: #222 !important;
    color: #ddd !important;
}
.docs-material .goog-toolbar-combo-button-input {
	color: #ddd;
}
.goog-toolbar-menu-button {
    background-color: #222 !important;
    border-left: #282828 !important;
}
.goog-toolbar-separator {
    border-left: 1px solid #222 !important;
}
.docs-material .goog-toolbar-button, .docs-material .goog-toolbar-menu-button {
	margin: 3px 1px 0;
	height: 26px;
	line-height: 26px;
	color: #ddd !important;
}
.filmstrip {
    background: #222 !important;
}
.punch-filmstrip-thumbnail-background[fill='#d4d4d4'] {
    fill: #383838 !important;
}
.punch-filmstrip-thumbnail-pagenumber {
    fill: #eee !important;
}
.punch-filmstrip-thumbnail-border {
	stroke: #282828 !important;
}
.punch-filmstrip-thumbnail-border-inner {
	stroke: #111 !important;
}
.punch-filmstrip-scroll {
    border-right: #333 !important;
}
#filmstrip-controls {
    background: #333;
    border-top: #282828;
    border-right: #282828;
}
.punch-filmstrip-controls .punch-filmstrip-controls-icon {
	filter: invert(100);
}
#workspace-container {
    background-color: #282828 !important;
}
.canvas {
    box-shadow: 0 0 2px #000 !important;
    border: 1px solid #222;
}
#speakernotes-dragger {
    background-color: #222;
    border-color: #242424;
}
#speakernotes {
    background: #242424;
}
.docs-explore-widget {
    display: none;
}
#speakernotes-workspace > svg {
    filter: invert(100);
}
}
