css:
@-moz-document url-prefix("https://www.youtube.com") {

html{
    --tonystr-black: #1E2026;
    --tonystr-gray: #2D313D;
    --tonystr-red: #F14165;
    --tonystr-blue: #7588FF;
    --tonystr-green: #43EF7F;
    --tonystr-pink: #FFC7FF;
    --tonystr-yellow: #FFF23D;
    --tonystr-txt-dark: #575a77;
    --tonystr-txt-bright: #878aa7;
    
}

.content.ytd-metadata-row-renderer{
    --yt-endpoint-color: var(--tonystr-blue);
    --yt-endpoint-hover-color: var(--tonystr-pink);
	--yt-endpoint-visited-color: var(--tonystr-pink);
}
    
a.yt-simple-endpoint.yt-formatted-string:visited,
a.yt-simple-endpoint.yt-formatted-string{
 	color:var(--tonystr-blue);   
}

html[dark]:not([style-scope]):not(.style-scope), [dark]:not([style-scope]):not(.style-scope) {
	--yt-main-app-background-tmp: var(--tonystr-black);
    --yt-main-app-background: var(--yt-main-app-background-tmp);
    --yt-spec-brand-button-background: var(--tonystr-red);
    --yt-spec-icon-active-button-link: var(--tonystr-pink);
    --ytd-video-publish-date-color: var(--tonystr-txt-dark);
	--yt-primary-text-color: var(--tonystr-txt-bright);
    --yt-icon-color: var(--tonystr-txt-bright);
    --yt-expand-color: var(--tonystr-blue);
    --yt-guide-background: transparent;
}

#autoplay.ytd-compact-autoplay-renderer{
 	color: var(--tonystr-txt-bright);   
}

ytd-guide-renderer#guide-renderer.style-scope.ytd-app{
 	background-color:var(--yt-main-app-background);   
}

#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child),
#guide-links-primary.ytd-guide-renderer{
 	border:none;   
}

#title.ytd-metadata-row-renderer,
#byline.ytd-video-meta-block,
#metadata-line.ytd-video-meta-block{
 	color:var(--tonystr-txt-dark);   
}

#related img.yt-img-shadow{
 	filter:grayscale(50%) brightness(90%);
}

.content.ytd-video-secondary-info-renderer{
 	color:var(--tonystr-txt-bright);   
}

.badge-style-type-simple.ytd-badge-supported-renderer{
 	color:var(--tonystr-green);
    background-color:transparent;
}

paper-button.ytd-subscribe-button-renderer{
 	background-color:transparent;
    border: 2px solid var(--tonystr-red);
    border-radius: 6px;
    transition: .4s all;
}
paper-button.ytd-subscribe-button-renderer[subscribed]{
    border-color: var(--tonystr-green);
 	background-color:transparent;
    color:#ffffff;
}
paper-button.ytd-subscribe-button-renderer[subscribed] span{
    color:#ffffff;
}
paper-button.ytd-subscribe-button-renderer:hover{
    
    border-color:var(--tonystr-green);
}

ytd-searchbox[mode=legacy] #container.ytd-searchbox, ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox{
 	background-color:transparent;
    border:none;
    border-bottom: 1px solid var(--tonystr-gray);
}

#search-input .gstl_50.sbib_a #gs_st50{
 	display:none;   
}

ytd-searchbox#search.style-scope.ytd-masthead[has-focus] from>div{
 	outline:none;
    border:none;
}

ytd-video-primary-info-renderer{
 	border:none;   
}

#search-icon-legacy.ytd-searchbox{
 	background-color:transparent;
    border-color:transparent;
    border-bottom: 1px solid var(--tonystr-gray);
}

#container.style-scope.ytd-masthead{
 	background-color: var(--tonystr-black);
    box-shadow: 0 -6px 19px #000000;
}

#player-container{
    box-shadow: 0 1px 19px rgba(0,0,0,.4);
}

span.yt-view-count-renderer,
#author-text.yt-simple-endpoint.ytd-comment-renderer{
 	color:var(--tonystr-txt-dark);   
}

#expander.ytd-comment-replies-renderer,
.more-button.ytd-comment-renderer, .less-button.ytd-comment-renderer{
    color:var(--tonystr-blue);
}

#like-bar.ytd-sentiment-bar-renderer{
 	background-color:var(--tonystr-blue);   
}

yt-icon.ytd-badge-supported-renderer{
 	color:var(--tonystr-yellow);   
}

ytd-toggle-button-renderer.style-text[is-icon-button]{
 	color:var(--tonystr-blue);   
}
ytd-toggle-button-renderer.style-default-active[is-icon-button]{
 	color:var(--tonystr-yellow);   
}

.ytd-video-primary-info-renderer,
#owner-name>a,
#content-text.ytd-comment-renderer{
 	color:var(--tonystr-txt-bright);   
}

.ytp-swatch-background-color{
 	background-color:var(--tonystr-red);   
}

#progress.ytd-thumbnail-overlay-resume-playback-renderer{
 	background-color:var(--tonystr-red);  
}
ytd-thumbnail-overlay-resume-playback-renderer{
 	background-color:var(--tonystr-txt-dark);   
}

.ytp-hover-progress-light{
 	background-color:rgba(117,136,255,.5);
}

.ytd-page-manager{
 	/* border-top: 1px solid var(--tonystr-gray); */
}
}
