/* ==UserStyle==
@name           Jenkins Dark Ocean
@namespace      github.com/openstyles/stylus
@version        1.2.0
@description    A dark theme for Jenkins, covering the Blue Ocean view
@author         DaemonExMachina
==/UserStyle== */

@-moz-document regexp("https?:\\/\\/jenkins(?!\\.io).*?") {
:root {
    --text1: #ddd;
    --textblue: #68b;
    --textpurple: #a8b;
    --bgbody: #222;
    --bglog: #111;
    --bggrey: #444;
    --bgdarkgrey: #333;
    --bgred: #500;
    --bggreen: #050;
    --bgblue: #037;
    --border-colour: #666;
    --border: 1px solid var(--border-colour);
}

body,
table,
form,
td,
th,
p,
.pane.build-name a,
.pane.build-name a:visited,
.pane.build-details a,
.pane.build-details a:visited,
.JTable-header,
.yui-skin-sam .yuimenuitemlabel,
.yui-skin-sam .yuimenuitemlabel:visited,
.ContentPageHeader,
.ResultPageHeader,
.ContentPageHeader a,
.ResultPageHeader a,
.ContentPageHeader a:hover,
.ResultPageHeader a:hover,
#breadcrumbs LI A:link:link,
#breadcrumbs LI A:visited:visited,
.pane-header,
.pane-footer,
table.sortable a.sortheader {
    color: var(--text1);
    caret-color: var(--text1);
}

div[style*="float:right"][style*="background-color:white"] {
    background-color: var(--bgbody) !important;
}

a:link {
    color: var(--textblue);
}

a:visited {
    color: var(--textpurple);
}

body,
.FullScreen-contents,
.yui-skin-sam .yuimenu .bd {
    background-color: var(--bgbody);
}

.TestSummary {
    background-color: var(--bggrey);
}

.tabBar .tab a {
    border: var(--border);
    border-bottom: none;
}

.tabBar .tab a:hover,
.tabBar [type=radio]:checked ~ a {
    color: var(--text1);
    background: var(--bggrey);
}

.tabBar .tab a.addTab:hover {
    background: #456;
}

.JTable,
.pane-frame,
.result-item-head,
#jenkins .jenkins-config,
#pipeline-box .table-box .table-viewPort,
.jobsTable .stage-start-time,
.jobsTable .stage-wrapper {
    border: var(--border);
}

.JTable-row {
    border-top: var(--border);
}

#projectstatus .header,
.pane-header,
.pane-footer {
    border-bottom: var(--border);
}

.jenkins-config-widgets,
#search-box-completion .yui-ac-content {
    color: var(--text1);
    background: var(--bgdarkgrey);
    border: var(--border);
}

#breadcrumbs,
footer {
    border: none;
}

#breadcrumbBar,
#footer-container,
#jenkins .jenkins-config,
.top-sticker-inner,
.JTable-row--rollOver:hover,
.result-item-head:hover,
.bigtable th,
footer,
.pane-header,
.pane-footer,
.build-row.model-link-active,
table.stripped tr:hover,
table.stripped-even tr:hover,
table.stripped-odd tr:hover {
    background: var(--bggrey) !important;
}

#breadcrumbs LI:hover:hover,
A.breadcrumbBarAnchor.mouseIsOverMenuSelector,
table.stripped tr:nth-child(even),
table.stripped-odd tr:nth-child(odd) {
    background-color: var(--bgdarkgrey);
}

div#menuSelector {
    filter: invert(1);
}

/* #breadcrumbs LI.children:hover {
    background-image: url(menu_right_arrow2.png);
} */

.yui-skin-sam .yuimenuitem-selected {
    background-color: var(--bgblue);
}

.log-body p,
#jenkins .jenkins-config table .CodeMirror {
    background-color: var(--bglog);
}

#jenkins .jenkins-config table .CodeMirror-scroll {
    caret-color: var(--text1);
}

table.parameters > tbody:hover,
.log-body p:hover {
    background-color: var(--bggrey);
}

#jenkins .jenkins-config table td .help {
    background-color: var(--bgdarkgrey);
    border: var(--border);
}

#pipeline-box .table-box tr td,
#pipeline-box .table-box .table-viewPort tr.totals .stage-start .cell-color {
    color: var(--text1);
    text-shadow: none;
}

#pipeline-box .table-box .table-viewPort .stage-start .cell-box .stage-start-time {
    border: var(--border);
    background: var(--bgdarkgrey);
}

.stackedBarChart .stackedBarChart.inner .bar {
    background: var(--bggrey);
}

.jobsTable .stage-start-time .date {
    background: var(--bggrey);
}

.running-bg-lite,
.result-bg.running,
.BasicHeader--default,
.BasicHeader--running {
    background: var(--bgblue);
}

.BasicHeader--aborted {
    background: var(--bggrey);
}

.failure-bg-lite,
.result-bg.failure,
.BasicHeader--failure {
    background: var(--bgred);
}

.svgResultStatus circle.failure {
    fill: var(--bgred);
}

.success-bg-lite,
.result-bg.success,
.BasicHeader--success {
    background: var(--bggreen);
}

.svgResultStatus circle.success {
    fill: var(--bggreen);
}

img[src="/static/d6e42daa/plugin/git/icons/git-48x48.png"] {
    -webkit-filter: brightness(3);
    filter: brightness(3);
}


/* New Item */
#add-item-panel form > .header {
    background-color: var(--bggrey);
    border: var(--border);
}

#add-item-panel .categories {
    color: var(--text1);
    background-color: var(--bgbody);
    border: var(--border);
}

.j-item-options li:hover,
.j-item-options li:focus {
    background-color: var(--bggrey);
}

.j-item-options li.active {
    background-color: var(--bgdarkgrey);
    box-shadow: none;
}

.j-item-options li label {
    color: var(--text1);
}

#add-item-panel .item-copy,
#add-item-panel form > .footer {
    background-color: var(--bggrey);
    border: var(--border);
}

#add-item-panel input {
    color: var(--text1);
    background-color: var(--bgdarkgrey);
}

.yui-skin-sam .yui-ac-content {
    background-color: var(--bgdarkgrey);
}

.yui-skin-sam .yui-ac-content li.yui-ac-prehighlight {
    background-color: var(--bgblue);
}

#add-item-panel form > .footer .btn-decorator {
    background-color: var(--bgdarkgrey);
    border-top: var(--border);
    border-right: var(--border);
    border-bottom: none;
    border-left: none;
}


/* Job config */
input,
input.setting-input,
#jenkins .jenkins-config table td select,
.setting-input, .ace-tomorrow {
    color: var(--text1);
    background-color: var(--bgdarkgrey);
    border: var(--border);
}

DIV.textarea-handle {
    background-color: var(--bgdarkgrey);
    border: var(--border);
    border-top: none;
}

#jenkins .jenkins-config table .CodeMirror-scroll {
    border: var(--border) !important;
}

#jenkins .jenkins-config table .bottom-sticker-inner {
    background-color: var(--bgdarkgrey);
    border-top: var(--border);
    border-right: var(--border);
    border-bottom: none;
    border-left: none;
}

.ace-tomorrow .ace_print-margin {
    background-color: var(--border-colour);
}

.ace-tomorrow .ace_marker-layer .ace_active-line {
    background-color: var(--bggrey);
}

.ace-tomorrow .ace_gutter,
.ace-tomorrow .ace_gutter-active-line {
    color: var(--text1);
    background: var(--bggrey);
}

/* Renaming projects */
.bottom-sticker-inner {
    background-color: var(--bgdarkgrey);
}

.bottom-sticker-edge {
    background-image: linear-gradient(to bottom, var(--bgbody), var(--bgdarkgrey));
    background-repeat: no-repeat;
}


/* Placeholders and alerts */
#hoverNotification {
    background-color: var(--bggrey);
    border: var(--border);
}

.cbwf-dialog .header {
    background-color: var(--bgdarkgrey);
}

.Alert,
.cbwf-dialog {
    background-color: var(--bggrey);
    border: var(--border);
}

.PlaceholderDialog,
.Placeholder-text,
.cbwf-stage-logs .node-name {
    background: var(--bggrey);
}

.Placeholder-icon {
    fill: var(--bggrey);
}

.PlaceholderContent.u-fade-bottom::after {
    background-image: linear-gradient(rgba(255, 255, 255, 0) 0%, var(--bggrey) 100%);
}

.cbwf-widget .alert-info {
    color: var(--text1);
    background-color: var(--bgdarkgrey);
    border-color: var(--bgblue);
}
}
