css:
@-moz-document domain('9gag.com') {/**
 * GLOBAL WIDTH
 */
/**
 * GLOBAL WIDTH
 */
.page, #page, section#list-view-2 .post-container a img, .list-stream > div > article { width: 716px !important; }

/* stuff */
.post-view { width:auto !important; }
body.theme-dark section#list-view-2 .post-container .post-view { background:#000 !important; }
img[loading="lazy"] { padding: 0 !important; }
.drawer-container { opacity:0; transition:opacity .6s; }
.drawer-container:hover { opacity:1 }
.drawer-container header, .stealthy-scroll-container > section:not(:first-child):not(.footer) { display:none; }
#sidebar, .featured-tag, nav.nav-menu .secondary, .topBannerAd-container { display:none !important; }
.post-read-more { width:100% !important; }
article {
 text-align:center   
}


.sticky-navbar { display:none !important }

.main-wrap { width:auto !important; }
.post-tag { width:100% !important; }
.post-container { width:auto !important; }
.post-container a { width:auto !important; }
section#list-view-2 header { margin-bottom:22px !important; }
.share { display:none }
.post-afterbar-a.in-post-bot.full-width { display:none }
.post-comment { text-align:left }
.post-afterbar-a { width:auto !important; }
.post-afterbar-a .btn-vote { display:none; }
.post-afterbar-a > ul.btn-vote:first-child { display:block }
.btn-vote { width: 100% !important; }
.btn-vote a { border-radius:50px; height:44px; border-color:rgba(0,0,0,.05); }
.badge-track { padding:0 !important; }
section#list-view-2 article { padding-top:30px !important; border-top:none !important; }
.btn-vote > li { float:none; display:inline-block; }
.youtube-post iframe { width:100% !important; }

.drawer-container section { display:none !important }
.drawer-container section:nth-child(1) { display:block !important }
}
