css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#fbStatusBar:focus {
/*outline: 2px solid #FF9933;*/
outline: 0 !important;}

/*---- ARROW CURSOR ----*/
#fbStatusBar {
cursor: auto !important;
padding: 0 3px !important;}

/*---- GRAYED ----*/
#fbStatusIcon,
#fbStatusIcon[suspended="suspended"] {
list-style-image: url("data:image/png;base64,") !important;}

/*---- LIT FIRE ON BUG ----*/
/*#firebug-button*/
#fbStatusIcon[jsd="on"], #fbStatusIcon[errors="on"], /***/
#fbStatusIcon[script="on"],
#fbStatusIcon[net="on"],
#fbStatusIcon[console="on"] {
list-style-image: url("data:image/png;base64,") !important;}

/*---- ERROR ----*/
#fbStatusBar[errors] #fbStatusIcon {
list-style-image: url("data:image/png;base64,") !important;}
