css:
@-moz-document domain(crystals-dsa-foren.de) {

body { 
	background: #161c11;
        color: #ffffff;
	font-family: Verdana, Arial, Sans-Serif;
	font-size: small; 
}

a:link {
	color: #a7ee73;
	text-decoration: none;
}

a:visited {
	color: #a7ee73;
	text-decoration: none;
}

a:hover, a:active {
	color: #5f9b00;
	text-decoration: underline;
}

li {
    color: #ffffff;
}

#container {
        background-image: none !important;
	width: 85%; 
	border: 1px solid #161c11;
	color: #ffffff;
	margin: auto auto;
	padding: 20px;
}

#content {
	width: auto !important;
}

#content a:hover, #content a:active {
	text-decoration: underline;
}

.menu ul {
	color: #E6E6E6;
	font-weight: bold;
	text-align: center;
	padding: 4px;
}

.menu ul a:link {
	color: #5f9b00;
	text-decoration: none;
}

.menu ul a:visited {
	color: #5f9b00;
	text-decoration: none;
}

.menu ul a:hover, .menu ul a:active {
	color: #5f9b00;
	text-decoration: underline;
}

#panel {
	background: #fff2d2;
	color: #000000;
	font-size: 11px;
	border: 1px solid #161c11;
	padding: 8px;
}

#panel a:link {
	color: #5f9b00;
}

#panel a:visited {
	color: #5f9b00;
}

#panel a:hover, #panel a:active {
	color: #5f9b00;
	text-decoration: underline;
}

table {
	color: #fefefe;
	font-family: Verdana, Arial, Sans-Serif;
	font-size: small;
	width: 100%;
}

.tborder {
	background: #406111;
	margin: auto auto;
	border: 1px solid #161c11;
}

.thead { 
  background-image: url(http://www.crystals-dsa-foren.de/images/darksun/thread_bg.gif);
    background-image: none !important;
	color: #E6E6E6;
}

.thead a:link {
	color: #ffffff;
	text-decoration: none;
}

.thead a:visited {
	color: #ffffff;
	text-decoration: none;
}

.thead a:hover, .thead a:active {
	color: #ffffff;
	text-decoration: underline;
}

.tcat {
	background: #000000;
	color: #fefefe;
	font-size: 11px;
}

.tcat a:link {
	color: #428000;
}

.tcat a:visited {
	color: #428000;
}

.tcat a:hover, .tcat a:active {
	color: #E6E6E6;
}

.trow1 {
	background: #1f3203;
}

.trow2 {
	background: #1f3203;
}

.trow_shaded {
	background: #000000;
}

.trow_sep {
	background: #CC6600;
	color: #000000;
	font-size: 11px;
	font-weight: bold;
}

.tfoot { 
        background-image: url(http://www.crystals-dsa-foren.de/images/darksun/thread_bg.gif);
        background-image: none !important;
	color: #000000;
}

.tfoot a:link {
	color: #6fd800;
	text-decoration: none;
}

.tfoot a:visited {
	color: #6fd800;
	text-decoration: none;
}

.tfoot a:hover, .tfoot a:active {
	color: #E6E6E6;
	text-decoration: underline;
}

.bottommenu {
	background: #d8c894;
	color: #E6E6E6;
	border: 1px solid #2d2d2d;
	padding: 10px;
}

.navigation {
	color: #E6E6E6;
	font-family: Verdana, Arial, Sans-Serif;
	font-size: small;
	font-weight: bold;
}

.navigation a:link {
	text-decoration: none;
}

.navigation a:visited {
	text-decoration: none;
}

.navigation a:hover, .navigation a:active {
	text-decoration: none;
}

.navigation .active {
	color: #E6E6E6;
	font-family: Verdana, Arial, Sans-Serif;
	font-size: 13px;
	font-weight: bold;
}

.smalltext {
	font-size: 11px;
}

.largetext {
	font-size: 16px;
	font-weight: bold;
        color: #fc9900;

}

input.textbox {
	background: #ffffff;
	color: #000000;
	border: 1px solid #2d2d2d;
	padding: 1px;
}

textarea {
	background: #ffffff;
	color: #000000;
	border: 1px solid #2d2d2d;
	padding: 2px;
	font-family: Verdana, Arial, Sans-Serif;
	line-height: 1.4;
	font-size: 13px;
}

input.radio {
	background: #000000;
	color: #ffffff;
}

input.checkbox {
	background: #ffffff;
	color: #000000;
}

select {
	background: #ffffff;
	color: #000000;
	border: 1px solid #2d2d2d;
}

.editor {
	background: #f1f1f1;
	border: 1px solid #2d2d2d;
}

.editor_control_bar {
	background: #ff0000;
        color: #ffffff;
	border: 1px solid #2d2d2d;
        font-weight: bold;
}

.autocomplete {
	background: #fff;
	border: 1px solid #2d2d2d;
	color: black;
}

.autocomplete_selected {
	background: #adcee7;
	color: #000;
}

.popup_menu {
	background: #ccc;
	border: 1px solid #2d2d2d;
}

.popup_menu .popup_item {
	background: #fff;
	color: #000;
}

.popup_menu .popup_item:hover {
	background: #C7DBEE;
	color: #000;
}

.trow_reputation_positive {
	background: #153a03;
	color: #ffffff;
}

.trow_reputation_neutral {
	background: #353b31;
	color: #ffffff;
}

.trow_reputation_negative {
	background: #7c221c;
	color: #ffffff;
}

.reputation_positive {
	color: #008800;
}

.reputation_neutral {
	color: #ababab;
}

.reputation_negative {
	color: red;
}

img {
	border: none;
}

.clear {
	clear: both;
}

.hidden {
	display: none;
	float: none;
	width: 1%;
}

.float_left {
	float: left;
}

.float_right {
	float: right;
}

.menu ul {
	list-style: none;
	margin: 0;
}

.menu li {
	display: inline;
	padding-left: 5px;
}

.menu img {
	padding-right: 5px;
	vertical-align: top;
}

#panel .links {
	margin: 0;
	float: right;
}

.expcolimage {
	float: right;
	width: auto;
	vertical-align: middle;
	margin-top: 2px;
}

img.attachment {
	border: 1px solid #2d2d2d;
	padding: 2px;
}

hr {
	background-color: #000000;
	color: #000000;
	height: 1px;
	border: 0px;
}

#copyright {
	font: 11px Verdana, Arial, Sans-Serif;
	margin: 0;
	padding: 10px 0 0 0;
}

#debug {
	float: right;
	text-align: right;
	margin-top: 0;
}

.subforumicon {
	border: 0;
	vertical-align: middle;
}

.separator {
	margin: 5px;
	padding: 0;
	height: 0px;
	font-size: 1px;
	list-style-type: none;
}

form {
	margin: 0;
	padding: 0;

}

.popup_menu .popup_item_container {
	margin: 1px;
	text-align: left;
}

.popup_menu .popup_item {
	display: block;
	padding: 3px;
	text-decoration: none;
	white-space: nowrap;
}

.popup_menu a.popup_item:hover {
	text-decoration: none;
}

.autocomplete {
	text-align: left;
}

.logo {
	text-align: center;
}

.modnotice {
	background: #ADCBE7;
	padding: 8px;
	margin: auto auto;
	border: 1px solid #2d2d2d;
	color: #000000;
	font-size: 12px;
}

.highlight {
	background: #FFFFCC;
	padding-top: 3px;
	padding-bottom: 3px;
}

.logo {
	text-align: center;
}

.modnotice {
	background: #ADCBE7;
	padding: 8px;
	margin: auto auto;
	border: 1px solid #2d2d2d;
	color: #000000;
	font-size: 12px;
}

.highlight {
	background-color: #FF0000;
color: #428000;
}

.trow_selected td {
	background: #666666;
}

blockquote {
	border: 1px solid #ccc;
	margin: 0;
        margin-top: 10px;
        margin-bottom: 10px;
	background: #e7e7d6;
	padding: 4px;
}

blockquote cite {
	font-weight: bold;
	border-bottom: 1px solid #ccc;
	color: #FF9900;
	font-style: normal;
	display: block;
	margin: 4px 0;
}

blockquote cite span {
	float: right;
	font-weight: normal;
        color: #000000;
}

.codeblock {
	background: #4c4c4c;
	border: 1px solid #ccc;
	padding: 4px;
        margin-top: 10px;
        margin-bottom: 10px;
}

.codeblock .title {
	border-bottom: 1px solid #ccc;
	font-weight: bold;
	margin: 4px 0;
}

.codeblock code {
	overflow: auto;
	height: auto;
	max-height: 200px;
	display: block;
	
	font-family: Consolas, "Lucida Console", "Andale Mono", "Bitstream Vera Sans Mono", monospace;
	font-size: 13px;
}

.subject_new {
	font-weight: bold;
}

.pm_alert {
	background: #666666;
	border: 1px solid #2d2d2d;
	text-align: center;
	padding: 5px 20px;
	
	font-size: 11px;
	margin-bottom: 15px;
}

.red_alert {
	background: #d8c894;
	border: 1px solid #2d2d2d;
	color: #A5161A;
	text-align: center;
	padding: 5px 20px;
	font-size: 11px;
	margin-bottom: 15px;
}

.high_warning {
	color: #CC0000;
}

.moderate_warning {
	color: #F3611B;
}

.low_warning {
	color: #AE5700;
}

div.error {
	padding: 5px 10px;
	border-top: 2px solid #2d2d2d;
	border-bottom: 2px solid #2d2d2d;
	background: #666666;
	font-size: 12px;
}

div.error p {
	margin: 0;
	color: #000;
	font-weight: normal;
}

div.error p em {
	font-style: normal;
	font-weight: bold;
	padding-left: 24px;
	display: block;
	color: #FF3333; 
}

div.error.ul {
	margin-left: 24px;
}

.online {
	color: #15A018;
}

.offline {
	color: #C7C7C7;
}

.pagination {
	font-size: 11px;
	padding-top: 10px;
	margin-bottom: 5px;
}

.tfoot .pagination, .tcat .pagination {
	padding-top: 0;
}

.pagination .pages {
	font-weight: bold;
}

.pagination .pagination_current, .pagination a {
	padding: 2px 6px;
	margin-bottom: 3px;
}

.pagination a {
	border: 1px solid #81A2C4;
}

.pagination .pagination_current {
	background: #F5F5F5;
	border: 1px solid #2d2d2d;
	font-weight: bold;
	color: #000000;
	color: #000;
}

.pagination a:hover {
	background: #F5F5F5;
	text-decoration: none;
	color: #000000;
}

.thread_legend, .thread_legend dd {
	margin: 0;
	padding: 0;
}

.thread_legend dd {
	padding-bottom: 4px;
	margin-right: 15px;
}

.thread_legend img {
	margin-right: 4px;
	vertical-align: bottom;
}

.forum_legend, .forum_legend dt, .forum_legend dd {
	margin: 0;
	padding: 0;
}

.forum_legend dd {
	float: left;
	margin-right: 10px;
}

.forum_legend dt {
	margin-right: 10px;
	float: left;
}

.success_message {
	color: #00b200;
	font-weight: bold;
	font-size: 10px;
	margin-bottom: 10px;
}

.error_message {
	color: #C00;
	font-weight: bold;
	font-size: 10px;
	margin-bottom: 10px;
}

.post_head a {
        color: #ff8000;
}
  
.post_body {
	padding: 5px;
        color: #000000;
}

.post_body a {
	color: #E57F00;
	text-decoration: none;
}

.post_body a:hover, .post_body a:active {
	color: #E57F00;
	text-decoration: underline;
}

.post_content {
	padding: 5px 10px;
        color: #000000;
}

.invalid_field {
	border: 1px solid #2d2d2d;
	color: #f30;
}

.valid_field {
	border: 1px solid #2d2d2d;
}

.validation_error { 
	color: #f30;
	margin: 5px 0;
	padding: 5px;
	font-weight: bold;
	font-size: 11px;
	padding-left: 22px;
}

.validation_success { 
	color: #00b200;
	margin: 5px 0;
	padding: 5px;
	font-weight: bold;
	font-size: 11px;
	padding-left: 22px;
}

.validation_loading { 
	color: #555;
	margin: 5px 0;
	padding: 5px;
	font-weight: bold;
	font-size: 11px;
	padding-left: 22px;
}

.spoiler_header {
	background: #666666;
    border: 1px solid #CCC;
    padding: 4px;
    margin: 4px 0 0 0;
    color: #FFFFFF;
}

.spoiler_body {
	background: #99B299;
    padding: 4px;
    border: 1px solid #CCC;
    border-top: 0;
    color: #000000;
    margin: 0 0 4px 0;
}

.spoiler_body blockquote {
	background: #667F66;
    padding: 4px;
    border: 1px solid #CCC;
    border-top: 0;
    color: #000000;
    margin: 0 0 4px 0;
}

.messageEditor .toolbar_dropdown {
	color: #000;
}

#panel .remember_me input {
	vertical-align: middle;
	margin-top: -1px;
}

.hiddenrow {
	display: none;
}

.selectall {
	background-color: #FFFBD9;
	font-weight: bold;
	text-align: center;
}

.repbox {
	font-size:16px;
	font-weight: bold;
	padding:5px 7px 5px 7px;
}

._neutral {
	background-color:#FAFAFA;
	color: #999999;
	border:1px solid #232323;
}

._minus {
	background-color: #FDD2D1;
	color: #CB0200;
	border:1px solid #232323;
}

._plus {
	background-color:#E8FCDC;
	color: #008800;
	border:1px solid #232323;
}

.pagination_breadcrumb {
	padding: 5px;
	margin-top: 5px;
	
	font-weight: normal;
}

.pagination_breadcrumb_link {
	vertical-align: middle;
	cursor: pointer;
}

/* Rabenaas */

.subforum_minion, .subforum_minioff {
        display: inline;
}

.post {
        position: relative;
        min-height: 20em;
        margin-bottom: 1em;
}

.post_head {
        padding-bottom: 1em;
        border-bottom: solid 1px black;
}

.post_controls {
        margin-top: 1px;
        margin-bottom: 10px;
        padding-top: 0.9em;
        padding-bottom: 1.9em;
        background-color: #222f07;
}

.post_content {
       flex-grow: 1;
       min-height: 20em;
       padding-left: 16em;
}

.post_author {
        padding: 1em;
        margin-right: 1px;
        position: absolute;
        width: 14em;
        padding-bottom: 5px;
}

author_information a {
        color: #ff8000;
}

.post_content, .post_author {
        background-color: #ded7a5;
        margin-bottom: 1px;
        padding-bottom: 1px;
        display: flex;
        flex-direction: column;
}

.post_author {
        color: #000000;
}

.post_body {
        padding: 1em;
        padding-bottom: 3.5em;
}

.post_controls .float_right {
        margin-right: 1em;
}

.post_controls .float_left {
        margin-left: 1em;
}

.signature {
        bottom: 3.5em;
        margin-right: 1em;
        padding-top: 1em;
        padding-bottom: 1em;
        border-top: solid 1px black;
        margin-top: auto;
}

.signature a {
        color: #ff8000;
}

.postbit_email, .postbit_website, .postbit_pm, .postbit_find, .postbit_reputation_add, .postbit_edit, .postbit_qdelete, .postbit_qrestore, .postbit_quote, .postbit_multiquote, .postbit_report, .new_reply_button, .new_thread_button { 
background-color: #355000;
background-position: center left; 
padding: 2pt;
padding-left: 30px;
padding-right: 10px;
padding-bottom: 3px;
margin-bottom: 5px;
}

 .new_reply_button, .new_thread_button {
    padding: 3pt;
    padding-left: 35px;
    margin: 2pt;
    margin-top: 5pt;
    background-position: 2pt;
}

.wrapper > table {
        margin-bottom: 1em;
        margin-top: 1em;
}

.forum_off, .forum_offlock, .forum_on {
    padding: 1em;
    background-repeat: no-repeat;
} 

#logo img {
    margin-left: auto;
    max-width: 100%;
    margin-right: auto;
   display: block;
}
#panel {
    font-size: small;
    border-radius: 7px;
    margin-bottom: 1em;
}
ul.top_links {
    float: right;
    margin-right: 2em;
}
#search {
    float:right;
    clear: right;
}
.welcome {
    float: left;
    font-size: medium;
    color: #ffffff;
}
.user_links {
    float: right;
    margin-right: 2em;
}
.panel_links {
    float: left;
}
.lower .wrapper {
    float: left;
    font-size: medium;
}
.panel-body {
    display: flex;
    padding-bottom: 3pt;
    border-bottom-left-radius: 7px;
    background-color: black;
    border-bottom-right-radius: 7px;
}
.panel-body > .col-lg-6 {
    width: 50%;
    display: inline;
}
.panel-body > .hidden-xs {
}
.panel-body .table {
    border-collapse: collapse;
}
.panel-body .table th, .panel-body .table td {
    border: 1px solid black;
    padding: 3pt;
}
.panel-body .table.tcat th, .panel-body .table .tcat {
    height: 2.5em;
    border: 1px solid black;
}
.panel-title { 
    background-image: url(http://www.crystals-dsa-foren.de/images/darksun/thread_bg.gif);
    background-image: none !important;
    background-color: #406111;
    padding: 3pt;
    border-top-left-radius: 7px;
    border-top-right-radius: 7px;
}

.container-fluid > .container-fluid {
    border: 1px solid #161c11;
    border-radius: 7px;
    margin-bottom: 1em;
}
#panel{
    min-height: 2em;
    background-color: unset;
}
.modal {
    position: fixed;
    top: 0px;
    left: 0px;
    z-index: 1;
}

#document body {
    background-color: #ffffff;
}

}
