css:
@-moz-document url("http://jw.ncepu.edu.cn/jiaowuchu"), url("http://jw.ncepu.edu.cn/jiaowuchu/Account/Login"), url("http://jw.ncepu.edu.cn/jiaowuchu/") {

/* 首页主导航栏绿色修改开始  */

.primary .navbar-inverse {

    background-color:#f3f3f3;

    border-color:#030303;

}

.primary .navbar-nav>li>a {

    color: #4b4b4b;

    font-weight:600;

    letter-spacing:2px;

}

/* 首页主导航栏绿色修改结束  */

/* 首页左、中导航栏宽度修改开始  */

.col-lg-7 {

    width:90%;

}

.col-lg-pull-7 {

    right:80%;

}

.col-md-2 {

    width:14%;

}

.col-sm-push-3 {

    left:15%;

}

/* 首页左、中导航栏宽度修改结束  */

/* 取消显示右侧导航栏开始  */

.col-md-3 {

    width:0%;

}

.mainrightmenu {

    display:none;

}

.col-lg-6 {

    display:none;

}

.carousel-inner {

    display:none;

}

.carousel-control.left {

    display:none;

}

.carousel-control.right {

    display:none;

}

.carousel-indicators {

    display:none;

}

.col-xs-offset-2 {

    display:none;

}

/* 取消显示右侧导航栏结束  */

/* 左导航栏标题样式居中开始  */

.nav-header h2 {

    text-align:center;

}

.nav>li>a {

    text-align:center;

}

/* 左导航栏标题样式居中结束  */

/* 左导航栏栏目样式居中开始  */

.nav-list>li>a.leftmenuitem {

    padding:4px 1px 1px 1px;

}

/* 左导航栏栏目样式居中结束  */

/* 左导航栏标题样式子栏目间距调整开始  */

.nav-list {

    margin-bottom:8px;

}

/* 左导航栏标题样式子栏目间距调整结束  */


}

@-moz-document url-prefix("http://jw.ncepu.edu.cn/jiaowuchu/Depart"), url-prefix("http://jw.ncepu.edu.cn/jiaowuchu/Account/"), url-prefix("http://jw.ncepu.edu.cn/jiaowuchu/Page/MajorList"), url-prefix("http://jw.ncepu.edu.cn/jiaowuchu/Graduate"), url-prefix("http://jw.ncepu.edu.cn/jiaowuchu/Public/CourseList"), url-prefix("http://jw.ncepu.edu.cn/jiaowuchu/Common/"), url-prefix("http://jw.ncepu.edu.cn/jiaowuchu/") {
.primary .navbar-inverse {
    background-color:#f3f3f3;
    border-color:#ffffff;
}
.primary .navbar-nav>li>a {
    color: #4b4b4b;
    font-weight:600;
    letter-spacing:2px;
}
.col-lg-3 {
    width:0%;
}
.col-lg-7 {
    width:80%;
}
.nav-header h2 {
    text-align:center;
}
.nav>li>a {
    text-align:center;
}
}
