css:
@-moz-document domain("gamestar.de") {
*{
    color: #a7a7a7 !important
}


/*HELLSTES DESIGN*/
#header, .pollteaser .buttons a, #header-brand, #header-navi, #header-trending, .article .technikcheck .td, .article .technikcheck td, .taglist
{
    background-color: #253447
}
/*DUNKLES DESIGN*/
body, #header > div > div.search > form > input, a.tckr_lnk, .pollteaser .answers, .info-box, .link-box, .timeline-Widget, #footer-bold, #foot, .article blockquote, textarea, #content .tag, .btn-tab.active, .comment .answers .answer, .toc .btn, .kasten, .plus-teaser-paid, .profilePage .tabs.mainTabs li a, .sidebar.sidebar-left.open, .sidebar.sidebar-right.open, #header-navi > div > ul > li> div > table > tbody > tr > td > ul > li > a, #header-navi > div > ul > li > div > table > tbody > tr > td > div > a, #header-navi > div > ul > li > div > table > tbody > tr > td > ul, .dropdown-menu, .dropdown-push .dropdown-menu, .twitter-typeahead .tt-menu, .dropdown-menu-table td.second, .btn-toc, .productTab li
{
    background-color: #141d26 !important;
    background: none
}
/*HELLES DESIGN*/
.homeHighlight, body > div.centeredDiv > div, .subnavicontent div, #header .login .avatarmenu, .subnavicontent, #content .container, #breadcrumbs > ul, .box .box-title a, .box .box-title span, .box .box-link-top a, #content .pageContent, .breadBoxTop, .breadBoxBottom, .sectionMain, .primaryContent, .secondaryContent, #ProfilePanes, .profilePage .tabs.mainTabs, .columnContent, .columnTeaser, .teaserRightHead>span, .teaserRightHead>h1, .teaserHead>h2, .teaserHead>h3, .teaserHead>h4, .teaserHead>span, .btnFilter, .contentTabSpacer, .titleHead>span, .article .technikcheck .th, .article .technikcheck th, .home [class*=" content-item-"], .home [class^=content-item-]
{
    background-color: #1b2836 !important;
    border-color: #1c1c1c !important;
    background: none
}
/* BLUE TEXT */
#layout_footer > ul > li > a, #barBottomLeft > div, #barBottomright > ul > li > a, a:link, a:visited, #tabs li.current a, #tabs li.current span.quiet, .smiley-tabs li.current a, .smiley-tabs li.current span.quiet, a.button, button.button, a:visited.tree, a.tree, a:link.tree, #barBottommiddle, #teaserSmallVerticalSlider2 .item .sliderTextLink strong, #teaserSmallVerticalSlider .item .sliderTextLink strong, body > div.centeredDiv > div > div.homeContRegCol3.fxAuHht > div > div.article > p > strong
{
    color: #1ea1f2 !important
}
/*WHITE Text*/
.titleHead>h4, h1, h2, h3, h4, h5, h6, .newsteaser_right strong, #teaserSmallVerticalSlider2 .item .sliderTextLink, #teaserSmallVerticalSlider .item .sliderTextLink
{
    color: #a7a7a7 !important
}
img, .hlImg a, .table-blanko td, .btn-toc
{
    border-radius: 10px
}
}

@-moz-document domain("gamestar.de") {
#header{
    background: #253447
}
#header > div > div.service, body > div.centeredDiv > div > div.homeContRegCol1.fxAuHht > div > div > div > span, div > div.bottomline, #textTickerBox > div.ads_brace, #footBtm, .paid-content-spacer, .ads_row, .ads_brace, .ellipsize2:after, .h4.ellipsize3:after
{
    display: none
}
body > div.centeredDiv > div.homeContHl.homeHighlight div, body > div.centeredDiv > div div, .pollteaser .answers, .pollteaser .buttons a, #header-trending, .article blockquote, .comment textarea, .toc .btn, .kasten, .profilePage .mainProfileColumn, #content > div > div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div > div.teaser > div.contentteaser.row.box.offerteaser-box.is-flex > div.box-content.m-b-1 > div > div > div > div, #content > div> div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div > div.teaser > div.contentteaser.row.box.offerteaser-box.is-flex > div.box-content.m-b-1 > div > div > div, #content > div > div > div > div.col-xs-12.col-md-2.col-lg-2.col-xl-2 > div > div.box-content.m-b-1 > div > div > div > div, .sidebar.sidebar-left.open, .sidebar.sidebar-right.open, .dropdown-menu-table td.second, .article td
{
    border-color: #141d26
}
.titleHead>h4, h1, h2, h3, h4, h5, h6{
    background-color: transparent;
    margin-top: 0px !important;
}
.newsteaser_right strong{
    color: #1ea1f2 !important
}
.newsteaser_right a{
    color: #a7a7a7 !important
}
a.tckr_lnk{
    border-color: #1ea1f2 !important;
    border-radius: 100px;
    color: #1ea1f2 !important;
    text-decoration: none
}
body > div.centeredDiv > div > div.homeContRegCol3.fxAuHht > div> div.pollteaser > div.buttons > a:nth-child(1){
    border-bottom-left-radius: 20px;
}
body > div.centeredDiv > div > div.homeContRegCol3.fxAuHht > div > div.pollteaser > div.buttons > a:nth-child(2)
{   
    border-bottom-right-radius: 20px
}
#content > div > div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div.poll-chart-container.col-xs-12 > div > div > div > div{
    margin-left: 1px !important
}
#content > div > div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div.poll-chart-container.col-xs-12 > div > div > div{
    border-top: 1px solid #a7a7a7
}
#header .navi .menulist li .sub, #header .service .menulist li .sub{
    box-shadow: none;
    -webkit-box-shadow: none
}
li.special a{
    background: #2ac61c;
    color: white !important;
    text-transform: uppercase;
    border: 1px solid transparent !important
}
#header .navi .home a.active, #n303 > a, .titleHead>h3, .titleHeadBig>h3{
    background-color: transparent !important;
    background-image: none !important
}
#content > div > div > div > div.col-xs-12.col-lg-4.pull-xs-right.teaser > div > div{
    border-left: solid 10px transparent !important;
    border-right: solid 10px transparent !important
}
.icon-thumb{
    color: #2ac61c !important
}
.icon-share{
    color: #1ea1f2 !important
}
.icon-block{
    color: red !important
}
.homeHighlight .typetitle, .homeTopThemen .typetitle{
    color: white !important;
    border-radius: 100px
}
#content > div > div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div > p> span.imagecontainer.responsive.m-b-1 > a > span > span.onimage-info.topleft.grn{
    margin-left: 25px;
    color: white !important
}
a.linkFinishM, .teaserSmallVerticalSlider2Nav #btnReleasesNext a, .teaserSmallVerticalSliderNav #btnReleasesNext a, .linkProductM, .linkProductS{
    text-decoration: none !important;
}
#header-trending .taglist li{
    padding-left: 5px
}
.h3.ellipsize3:after, h3 .ellipsize3:after, h3.ellipsize3:after{
    visibility: hidden
}
#teaserSmallVerticalSlider .item .sliderImgLink, #teaserSmallVerticalSlider2 .item .sliderImgLink, .hlImg img, .hlImg a{
    border: 1px solid transparent
}
.homeTopThemen #top_themen>div:not(:last-child){
    border-bottom: 1px solid black
}
}

@-moz-document regexp("(?=.*?\\gamestar.de/artikel).*") {
/*Dieser Part ist der Widescreen Part, der dafür sogt, dass die Artikel größer werden können und 80% anstatt 66% */
 @media(min-width: 976px) {
    .container {
        max-width: 1300px;
    }
    .contentteaser.row.box.contentmediaitem-box {
        display: none;
    }
    .col-xs-12.col-lg-4.pull-xs-right.teaser {
        width: 20%;
    }
    .col-xs-12.col-lg-8.pull-xs-left.maincontent {
        width: 80%;
    }
}
#socialshare {
    display: none;
}
#content>div:nth-child(1)>div {
    display: none;
}
#content>div:nth-child(1)>hr {
    display: none;
}
#footer-bold {
    display: none;
}
#footer-brand > div.container.p-b-0.p-t-2.hidden-md-down > div {
    display: none;
}
#content > div > div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div > p > span.imagecontainer.responsive.m-b-1 > a > img, #content > div> div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div > p> span.imagecontainer.responsive.m-b-1 > a > span, #content > div > div > div > div.col-xs-12.col-lg-8.pull-xs-left.maincontent > div > div > p > span.imagecontainer.responsive.m-b-1 > a > span{
    border-radius: 30px !important
}
a, a:link{
    color: white !important
}
#content .btn-link{
    color: #1ea1f2 !important
}
}

@-moz-document domain("gamestar.de") {
/*In diesem Part nehme ich kleine Veränderungen vor um die Kopfzeile des alten Designs dem neuen Design anzunähern */
#header > div > div.navi > div.main.cornerSTop {
    background: none !important;
    border: 0 !important;
}
#header > div > div.navi > div.home.cornerSTop {
    background: none !important;
}
#header .navi .main a.menuitem, #header .navi .home a.menuitem {
    border: 0
}
#header .navi .main li:hover a.menuitem, #header .navi .main a.menuitem:hover, #header .navi .main a.active, #header .navi .misc li:hover a.menuitem, #header .navi .misc a.menuitem:hover, #header .navi .misc a.active, #header .navi .home li:hover a.menuitem, #header .navi .home a.menuitem:hover {
    background-color : #003b7a !important;
    color: #a7a7a7;
}
#header .searchInput {
    background: #a7a7a7 !important;
}
#header .navi .main li:hover a.menuitem, #header .navi .main a.menuitem:hover, #header .navi .main a.active, #header .navi .misc li:hover a.menuitem, #header .navi .misc a.menuitem:hover, #header .navi .misc a.active, #header .navi .home li:hover a.menuitem, #header .navi .home a.menuitem:hover, #header .navi .home a.active {
    border-top: 0;
}
}
