css:
@-moz-document domain("www.converto.io"), domain("s1.converto.io"), domain("s2.converto.io"), domain("s3.converto.io"), domain("s4.converto.io"), domain("s7.converto.io"), domain("s9.converto.io"), domain("s10.converto.io"), domain("s11.converto.io") {
/*Remove ad sections*/
.ab,
.native_index_page,
.native_dl_page {
    display: none!important;
}

html, body {
    background: #242424;
    color: #fff!important;
}

.header {
    background: #191919;
}

.queue-wrapper .add-to-queue input[type="text"] {
    background: #353535!important;
    border: 1px solid #000!important;
    color: #fff;
    padding-left: 2rem!important;
}

.queue-wrapper .queue-list li {
    background: #353535;
    border: 1px solid #000;
    border-radius: 4px 4px 0 0!important;
    border-bottom: none;
    margin: 0;
}

.queue-wrapper .convert-btn {
    font-size: 2rem;
    height: auto;
    padding: .5rem 1.5rem!important;
    right: 1rem;
    top: 50%;
    transform: translateY(-50%);
}

.modal-content {
    background: #242424;
}

#settings-modal .player-controls {
    background: #242424;
    border-bottom: none;
}

#settings-modal .select-part-box {
    border: 2px dashed #fff;
    box-shadow: none;
}

/*Slider thumbs*/
.noUi-target {
    border-color: #fff;
}

.noUi-handle:after, .noUi-handle:before {
    background: #000;
}

input[type="text"], input[type="password"], textarea {
    color: #fff!important;
}

#settings-modal .cover-select li.selected {
    border-color: #007bff;
}

.queue-wrapper .tos-agree {
    color: #fff;
}

.like-and-share-btns {
    display: none!important;
}

.queue-wrapper .tip,
.queue-wrapper .news {
    background: #fff5c5;
    margin-left: 0;
    margin-right: 0;
    width: 100%;
}

.queue-wrapper .tip p:last-child,
.queue-wrapper .news p:last-child {
    color: #000;
}

.about-converto p {
    color: #fff;
}

.share-block-xl {
    display: none;
}

.footer {
    border: none;
    margin-top: 0;
}

.footer p {
    color: #fff;
}

.footer .contact-us {
    background: rgba(0,0,0,.3);
    box-shadow: 0 3px 12px 0 rgba(0,0,0,.5);
}

.footer .contact-us .mail {
    border: 2px solid #fff;
    color: #fff;
    transition: all 200ms ease-in;
}

.footer .contact-us .mail:hover {
    background: #fff;
    color: #000;
}

h3.underline {
    border-color: #fff;
}

a.draggable {
    border-style: solid;
    transition: all 200ms ease-in;
}

a.draggable:hover {
    background: #fb5151;
    color: #fff;
}

.content video {
    border-radius: 4px;
    box-shadow: 0 5px 25px rgba(0,0,0,.20);
}

.btn {
    box-shadow: 0 5px 25px rgba(0,0,0,.20);
}

.settings-btn {
    filter: drop-shadow(0 3px 12px rgb(0,0,0,.5));
}

.btn.lg {
    border: none;
    border-radius: 4px;
}
}
