css:
@-moz-document domain("wiki.project1999.com") {
html, body, label, h1, h2, h3, h4, h5, h6, #column-content > #content {
	background-color: #333333;
	background-image: none;
	color: #ffffff;
}
a, a:visited, a:active {
	background-color: transparent;
	color: #999999;
}
a:hover {
	background-color: transparent;
	color: #ffffff;
	text-decoration: none;
}
#bodyContent a.external {
	background-color: transparent;
	color: #cc6666;
}
input {
	border-radius: 3px;
	border: 1px solid #cccccc;
	padding: 4px 8px;
}
#searchInput {
	margin-bottom: 5px;
}
.pBody {
	background-color: #333333;
	border: none;
	padding: 1px;
}
#p-cactions[role="navigation"] a {
	background-color: #666666;
	color: #999999;
}
#p-cactions[role="navigation"] .selected a {
	background-color: #cccc66;
	color: #222222;
	padding-bottom: 1px;
}
#p-personal[role="navigation"] a {
	color: #999999;
}
#p-personal[role="navigation"] a:hover {
	background-color: transparent;
	color: #ffffff;
}
.auctrackerbox {
	background-color: #333333;
	border: none;
}
.mainPageAnnounce, .questTopTable th, .eoTable, .eoTable2, .eoTable3, .catlinks, .thumb > .thumbinner, .toc, .facblock, .zoneTopTable, .mobStatsBox {
	background-color: #666666;
	border: 1px solid #222222;
	border-spacing: 0;
	border-collapse: collapse;
}
.mainPageOuterBox1, .mainPageOuterBox2 {
	background-color: #333333;
	padding: 4px 0;
	border: 1px solid #222222;
}
.mainPageInnerBox1, .mainPageInnerBox2 {
	background-color: transparent;
}
.mainPageAnnounce td, .questTopTable th, .zoneTopTable th {
	padding: 5px;
}
.eoTable th, .eoTable2 th, .eoTable3 th, .toc th, .mobStatsBox th {
	background-color: #666666;
	border-bottom: 3px solid #222222;
}
.eoTable td, .eoTable2 td, .eoTable3 td, .toc td, .zoneTopTable th, .zoneTopTable td {
	background-color: #666666;
}
.mobStatsBox td {
	background-color: #222222;
}
.toc h2 {
	background-color: transparent;
}
.eoTable tr:not(:last-child) td,
.eoTable2 tr:not(:last-child) td,
.eoTable3 tr:not(:last-child) td,
.zoneTopTable tr:not(:last-child) th,
.zoneTopTable tr:not(:last-child) td {
	border-bottom: 1px solid #222222;
}
.drare {
	color: #cc9999;
}
.profac {
	color: #cc9933;
}
.oppfac {
	color: #33cc99;
}
.mw-content-ltr table[cellspacing="0"],
.mw-content-ltr table[cellpadding="1"] {
	background-color: #666666;
	border: 1px solid #222222 !important;
	border-spacing: 0;
	border-collapse: collapse;
}
.mw-content-ltr h2 + table td,
.mw-content-ltr table[cellspacing="0"] td,
.mw-content-ltr table[cellpadding="1"] td {
	background-color: #666666 !important;
	border-bottom: 1px solid #222222;
}
div#footer,
.mw-search-formheader {
	background-color: #333333;
	border-color: #ff9999;
}
.mw-search-formheader li.current a {
	color: #ffffff !important;
}
}
