css:
@-moz-document domain("github.com") {
body {
    background-color: #343a40;
    color: #d7d7d7;
}

/*Containers*/
.Box-header,
.menu-heading,
.select-menu-header,
.orghead {
    background-color: #2d3238!important;
    border-color: #000!important;
    color: #fff;
}

.org-name {
    color: #fff;
}

.select-menu-title {
    color: #fff!important;
}

.Box,
.menu,
.select-menu-filters,
.select-menu-modal {
    background-color: #24292e!important;
    border-color: #000!important;
}

.select-menu-modal {
    box-shadow: none;
}

.Box-body {
    border-color: #000;
}

.bg-white {
    background-color: #24292e!important;
}

.menu-heading {
    color: #fff;
}

.menu-item {
    border-color: #1d2125;
}

.menu-item.selected,
.menu-item:hover {
    background-color: #3f454a;
    color: #fff;
}

.repohead.experiment-repo-nav {
    background-color: #1d2125;
    border-color: #000;
}

.reponav-item {
    color: #d7d7d7;
}

.reponav-item:hover {
    color: #fff;
}

.reponav-item.selected {
    background-color: #3f454a;
    color: #fff;
    border-color: #e36209 #000 transparent;
}

.overall-summary,
.repository-lang-stats-graph {
    border-color: #000;
}

.numbers-summary {
    background-color: #24292e;
}

.text-emphasized {
    color: #d7d7d7;
}

.numbers-summary a:hover {
    background-color: #4a525a;
    color: #d7d7d7;
}

.branch-infobar {
    background-color: #24292e;
    border-color: transparent;
    color: #d7d7d7;
}

.commit-tease {
    background-color: #2d3238;
    border-color: #24292e;
    color: #fff;
}

.commit-tease-sha {
    color: #d7d7d7;
}

.file-wrap {
    border-color: #24292e;
}

table.files{
    background-color: #2a2d2f;
}

table.files tr[aria-selected="true"] td, table.files tr.navigation-focus td {
    background-color: #4a525a;
}

table.files td {
    border-color: #1d2125;
}

table.files td.icon,
table.files td.icon .octicon-file-directory {
    color: #0076ff;
}

table.files td.message a {
    color: #d7d7d7;
}

.boxed-group>h3, .boxed-group .heading {
    background-color: #24292e;
}

.readme .markdown-body, .readme .plain {
    background-color: #2d3238;
    border-color: #24292e;
}

.markdown-body hr {
    background-color: #1d2125;
}

.markdown-body img {
    border-radius: 4px;
}

.markdown-body .highlight pre,
.markdown-body pre {
    color: #24292e;
}

.blankslate {
    background-color: #3f454a;
    border-color: #3f454a;
    box-shadow: none;
}

/*Links*/
.UnderlineNav-item {
    color: #d7d7d7;
}

.UnderlineNav-item.selected {
    color: #fff;
}

.UnderlineNav-item:hover,
.UnderlineNav-item:focus {
    color: #fff;
}

.link-gray,
.link-gray-dark {
    color: #dbe1e8!important;
}

.muted-link {
    color: #d7d7d7!important;
}

.muted-link:hover {
    color: #3d9aff!important;
}

.numbers-summary a,
.numbers-summary .nolink,
.numbers-summary .octicon {
    color: #d7d7d7;
}

.btn-link {
    color: #3d9aff;
}

.topic-tag {
    background-color: #24292e;
    padding: .125rem .75rem!important;
}

.topic-tag:hover {
    background-color: #2d3238;
}

/*Borders*/
.border,
.border-bottom,
.UnderlineNav {
    border-color: #000!important;
}

.border-gray,
.border-gray-light,
hr,
.rule {
    border-color: #1d2125!important;
}

/*Input Boxes*/
.form-control,
.form-select {
    background-color: #3f454a!important;
    border-color: #000!important;
    color: #fff!important;
}

.form-control::placeholder,
.form-select::placeholder {
    color: #d7d7d7;
}

/*Text*/
a {
    color: #3d9aff;
}

.text-gray {
    color: #dbe1e8!important;
}

.text-gray-light,
.select-menu-item.selected {
    color: #d7d7d7!important;
}

.text-gray-dark {
    color: #d7d7d7!important;
}

.reponame-suggestion {
    color: #59ca1b;
}

.Subhead-heading {
    color: #fff;
}

.Subhead-description,
.form-checkbox .note,
.note {
    color: #d7d7d7!important;
}

.vcard-fullname {
    color: #fff;
}

.vcard-username,
.user-profile-bio {
    color: #d7d7d7;
}

.user-mention,
.team-mention {
    color: #3d9aff;
}

/*Buttons*/
.btn {
    background-color: transparent;
    
}

.ajax-pagination-form .ajax-pagination-btn {
    background-color: #2d3238;
    border-color: #000!important;
    cursor: pointer;
}

.ajax-pagination-form .ajax-pagination-btn:hover {
    background-color: #343a40;
}

/*Menus*/
.user-profile-nav,
.user-profile-sticky-bar::after {
    background-color: #343a40;
    border-color: #000;
}

.dropdown-menu {
    background-color: #3f454a;
    border-color: #000;
}

.dropdown-menu-sw::before {
    border-bottom-color: #000;
    right: 9px;
}

.dropdown-menu::after {
    border-bottom-color: #3f454a;
}

.dropdown-header {
    color: #fff;
}

.dropdown-divider {
    background-color: #1d2125;
}

.dropdown-item {
    color: #d7d7d7;
}

.select-menu-text-filter:first-child:last-child {
    border-color: #000;
}

.select-menu-item {
    background: #24292e;
    border-color: #1d2125;
    color: #fff;
}

.subnav-item {
    border-color: #000;
    color: #d7d7d7;
}

.subnav-item:hover,
.subnav-item:focus {
    background: #24292e;
}

.dropdown-item:hover,
.dropdown-item.zeroclipboard-is-hover {
    background: rgba(255,255,255,.2);
}

/*Logout button*/
.dropdown-menu li:last-child .dropdown-item:hover {
    background-color: #f14b4b!important;
}

.pinned-repos-list {
    margin-bottom: .5rem!important;
}

/*Pinned repos*/
.pinned-repo-item {
    background-color: #24292e;
    margin-bottom: .5rem!important;
}

/*Timeline*/
.profile-timeline-month-heading,
.profile-timeline-year-list {
    background-color: #343a40!important;
}

.discussion-timeline::before {
    background-color: #1d2125!important;
    width: 1px;
}

.profile-timeline.discussion-timeline .profile-timeline-month-heading::after {
    background-color: #1d2125!important;
}

.discussion-item-icon {
    background-color: #24292e;
    border: 1px solid #000;
    color: #fff;
}

.profile-rollup-wrapper+.profile-rollup-wrapper,
.profile-rollup-summarized+.profile-rollup-summarized {
    border-color: #1d2125;
}

.profile-timeline-card-wrapper {
    border: none;
}

.filter-item {
    color: #bebfce;
}

.filter-item.selected {
    color: #fff;
}

/*Calender*/
.js-contribution-graph > div {
    background-color: #24292e!important;
}

rect.day[fill="#ebedf0"] {
    fill: #343a40;
}

/*Marketplace*/
.MarketplaceSideNav {
    background-color: #343a40;
}

/*New Repo*/
.Subhead {
    border-color: #1d2125;
}

.page-new-repo .octicon-lock {
    color: rgba(255, 197, 2, 0.5);
}

/*Alert message*/
.octofication .message p {
    color: #d7d7d7;
}

/*Repo Code*/
.file {
    border-color: #000;
}

.file-header {
    background-color: #24292e;
    border-color: #000;
}

.commit-tease-contributors {
    background-color: inherit;
    border-color: inherit;
}

.breadcrumb strong.final-path {
    color: #d7d7d7;
}

.blob-wrapper {
    background-color: #2d3238;
}

.blob-num,
.blob-code-inner {
    color: #d7d7d7;
}

/*Keywords, HTML Tags*/
.pl-k, .pl-ent, .pl-v, .pl-smw {
    color: #D86C74;
}

/*Variables*/
.pl-smi, .pl-s .pl-s1 {
    color: #6CAFF2;
}

/*Attributes*/
.pl-c1, .pl-s .pl-v {
    color: #3d9aff;
}

/*Strings, Quotes*/
.pl-s,
.pl-pds {
	color: #98C379;
}

/*Called Functions, PHP tags, Template Strings*/
.pl-en, .pl-pse {
    color: #C678DD;
}

/*HTML Attributes*/
.pl-e {
    color: #D19A66;
}
}
