css:
@-moz-document url-prefix("https://manual.calibre-ebook.com") {
div.body {
    width: 100em;
}

div.body p, div.body dd, div.body li {
    line-height: 1.4em;
}

html.cye-disabled.cye-nm body {
    background-color: #000;
}

div.body {
    background-color: #0f0f0f;
}

div.contents.local.topic {
    background-color: #222138;
    border-color: #ebff78;
}

div.section {
    color: #b3b3b3;
    font-size: 15px;
    font-family: Roboto;
}

a.toc-backref {
    color: #f09595;
    font-variant: small-caps;
}

a.reference.internal, .sphinxsidebarwrapper a {
    color: #7975f5 !important;
        padding: 2px;
    padding-left:4px;
    padding-right: 4px;

}

#sidebar-collapsible-toc li label {
    color: #c12be6;
    text-shadow: 10px 0px 0px #90830c;
    font-size: 20px;
    margin-left: -12px;
}

div.bodywrapper {
    margin: 0 0 0 250px;
}

 div.sphinxsidebar {
    overflow-y: visible;
}

div.sphinxsidebarwrapper {
    padding: 0px;
    width: 250px;
}

div.sphinxsidebar #searchbox input[type="text"] {
    float: left;
    width: 75%;
}

tt.xref, code.xref, a tt {  
    border-bottom: 0px;
}

div.sphinxsidebar a:hover, a.reference:hover, code.xref.py.py-meth.docutils.literal.notranslate:hover,
code.xref.std.std-option.docutils.literal.notranslate:hover {
    background-color: #046569 !important;
    color: #3bff3b !important;
    border-radius: 4px;
    padding: 2px;
    padding-left:4px;
    padding-right: 4px;
    padding-top: 3px;
}

div.admonition.note {
    background-color: #304f3f;
    color: #77ba73;
    border-color: #18ff03;
    border-radius: 4px;
    border-style: dotted;
    border-width: 1px;
    font-family: monospace;
    font-size: 13px;
}
div.admonition.note p.admonition-title{
    font-weight: bold;
    color: #3bcc33;
}

 div.admonition.seealso {
    background-color: #2c0d31;
    color: #aa60b7;
    border-color: #9c27b0;
    border-radius: 4px;
    border-style: dotted;
    border-width: 1px;
    font-family: monospace;
    font-size: 13px;
}
div.admonition.seealso p.admonition-title {
    color: #dc19fd;
    font-weight: bold;
}

 div.admonition.warning {
background-color: #52561f;
    color: #daac22;
    border-color: #ffe500;
    border-radius: 4px;
    border-style: dotted;
    border-width: 1px;
    font-family: monospace;
    font-size: 13px;
}
div.admonition.warning p.admonition-title {
    color: #ffe924;;
    font-weight: bold;
}


a.reference.external {
    color: #18ba1d;
        padding: 2px;
    padding-left: 3px;
    padding-right: 3px;
}

div.highlight pre {
    background-color: #091921;
    border-style: dashed;
    border-color: #3bd8ff;
    border-width: 1px;
}

code.docutils.literal.notranslate {
    background-color: #162c6d; /* #25593d; */
    color: #4be8db;
    border-radius: 4px;
    padding: 2px;
    padding-left:4px;
    padding-right: 4px;
    padding-top: 3px;
}

div.section h2 {
    color: #f09595;
    text-decoration: underline;
}

th.head {
    color: #c74040;
}

kbd.kbd.docutils.literal.notranslate {
    color: #abc250;
}

tr.row-even td {
    color: #429953;
    background-color: #0b1717;
}

tr.row-odd td {
    color: #468b94;
    
    background-color: #0a0a0a;
}








p.topic-title.first {
    font-weight: bold;
    text-decoration: underline;
    color: #90a85b;
}

.highlight .ch {
    color: #674133;
}
.highlight .mi {
    color: #3de35f;
}

.highlight .s1, .highlight .s2, .highlight .si {
    color: #cfdb74;
    font-style: italic;
}

.highlight .kc {
    color: #ff5e5e;
    font-weight: bold;
}

.highlight .sd {
    color: #575757;
    font-style: italic;
}



.highlight .k, .highlight .ow, .highlight .bp {
    color: #ff7d7d;
}
.highlight .nc {
    color: #3f5ac4;
    text-decoration: overline;
}

.highlight .mf {
    text-decoration: underline;
    color: #8a56ad;
}

.highlight .nf {
    color: #d574ed;
}

.highlight .n {
    color: #25adde;
}

.highlight .nb {
    color: #e068e6;
}



span.caption-text {
    color: #7873f5;
}

.highlight .p, .highlight .o {
    color: #ff9800;
    font-weight: bold;
    font-style: italic;
    
}

/*
.highlight .o::after {
    content: ' ';
}
.highlight .o::before {
    content: ' ';
}
*/

.highlight .nt {
    color: #ff9999;
}

.highlight .c1 {
    color: #575757 !important;
    font-style: italic;
}
}
