css:
@-moz-document domain("www.showmyhomework.co.uk") {
body {
    background-color: #343a40;
    color: #d7d7d7;
}

a {
    color: #007bff!important;
}

.blue-nav-wrapper,
.blue-nav-wrapper .menu-toggle {
    background-color: #1c7ac7;
}

.blue-nav-wrapper .menu-toggle {
    border-color: #000;
    width: 269px;
}

.blue-nav-wrapper.toggled .menu-toggle {
    width: 44.5px;
}

/*Nav Search bar*/
.__global-search-box__638d8 input {
    background-color: #3f454a;
    border: 1px solid #000;
    color: #fff;
}

.__global-search-box__638d8 input::placeholder {
    color: #d7d7d7;
}

.__global-search-box__638d8::after {
    color: #fff;
    margin-top: -1px;
    transform: translateY(-50%);
}

.__event__dropdown-menu__28494 .smhw-dropdown-btn {
    background-color: #0B5C99!important;
}

.__event__dropdown-menu__28494 .smhw-dropdown-menu {
    border: none;
    border-top: 1px solid #000;
    box-shadow: 0 5px 25px rgba(0,0,0,.5);
}

.__event__dropdown-menu__28494 .arrow {
    border-bottom-color: #000;
    position: relative;
}

.__event__dropdown-menu__28494 .arrow::before {
    border-width: 10px;
    border-style: solid;
    border-color: transparent;
    border-bottom-color: #343a40;
    content: '';
    position: absolute;
    top: -9px;
    left: -10px;
}

.event-row {
    background-color: #343a40;
    border-color: #000;
}

.profile {
    border-color: #000;
}

.toggled .profile {
    padding: 1.5rem 0!important;
}

.btn {
    color: #fff!important;
    box-shadow: none;
    overflow: hidden; /*Fixes To-do list button's text overflowing when toggling the menu*/
    transition: all 200ms ease-in;
}

.btn.btn-primary {
    background-color: #1570d2;
    border-color: #007bff;
}

.btn.btn-primary:hover {
    background-color: #007bff;
}

.btn.btn-success {
    background-color: #6fa734;
    border-color: #7dbd37;
}

.btn.btn-success:hover {
    background-color: #7dbd37;
}

#sidebar-wrapper {
    background-color: #24292e;
}

.primary-action {
    border-color: #000;
}

.sidebar-nav {
    background-color: inherit;
}

.sidebar-nav li a {
    border-color: #1d2125;
    color: #9ba3af!important;
}

.sidebar-nav li a:hover,
.sidebar-nav li a.active {
    background-color: rgba(255,255,255,.2);
    color: #fff;
}

.sidebar-nav li a i {
    color: #007bff;
}

#page-content-wrapper .main-content {
    border-color: #000;
}

#page-content-wrapper .main-header,
.main-header {
    background-color: #24292e;
    border-color: #000;
}

.breadcrumb,
.breadcrumb a:not(.brand-primary-text) {
    color: #d7d7d7;
}

.tab-container {
    background-color: #3f454a;
    border-color: #000;
}

.tab-container .tabs a {
    color: #9ba3af!important;
}

.tab-container .tabs a.active,
.tab-container .tabs a:focus,
.tab-container .tabs a:hover {
    background-color: #1d2125!important;
    color: #fff!important;
}

.form-control {
    background-color: #3f454a;
    border-color: #000;
    color: #fff;
}

.form-control:focus {
    border-color: #007bff;
    box-shadow: none;
}

/*Dates above active homework*/
.hr[title]:after,
hr[title]:after {
    background-color: #24292e;
    border-color: #1d2125;
    color: #fff;
}

.__segmented-todos__6a33a .todo-divider {
    border-color: #1d2125;
}

/*No items to show*/
.well {
    background-color: transparent;
    border: none;
}

/*Homework Items*/
.__todo-item__b724d {
    background-color: #24292e;
    border-left-width: 2px!important;
    border-color: #000;
    padding: 1rem;
}

/*Types of tasks*/
/*General Homework*/
.__todo-item__b724d.todo-item-homework {border-left-color: #90e039;}
/*Spelling test*/
.__todo-item__b724d.todo-item-spelling-test {border-left-color: #1ce4b3;}
/*Quiz*/
.__todo-item__b724d.todo-item-quiz {border-left-color: #ffa900;}
/*Differentiated Homework*/
.__todo-item__b724d.todo-item-differentiated-homework {border-left-color: #007bff;}
/*Class Test*/
.__todo-item__b724d.todo-item-class-test {border-left-color: #8b5eff;}

.__todo-item__b724d .date, /*Date*/
.__todo-item__b724d.completed .summary a { /*Homework Title*/
    color: #fff;
}

/*Checkbox*/
.checkbox label::before {
    border-radius: 4px;
}

.checkbox.checked label::before {
    background-color: #007bff;
}

/*Account Settings*/
.account-settings .api-toggle,
.account-settings .calendar-export,
.account-settings .change-password-container,
.account-settings .my-classes,
.account-settings .notifications-container,
.account-settings .profile-invite-code,
.account-settings .simple-border {
    border-color: #000;
}

.account-settings .header-container {
    border-color: #000;
}

.account-settings .header-container {
    margin: 0;
}

.header-content {
    margin: 0;
}

#ember11843,
#ember1926,
#change-password-form,
.settings-container {
    background-color: #2d3238;
    padding-top: 1rem!important;
}

.form {
    border-radius: 4px 4px 0 0;
}

.account-settings .header-container {
    background-color: #24292e;
}

.mobile-device-container {
    background-color: #24292e;
    border-radius: 0 0 4px 4px;
}

.change-password-container,
.notifications-container,
.avatar-container,
.my-classes,
.calendar-export,
.profile-invite-code {
    border-radius: 4px;
}

.settings-container .sub-header {
    margin: 0!important;
}

.avatar-container,
.my-classes,
.calendar-export,
.profile-invite-code {
    background-color: #24292e;
}

.img-responsive {
    border-radius: 4px;
}

label {
    color: #fff;
    font-size: 1.5rem;
}

.__validated-input__b212c .form-control[disabled] {
    background-color: #3f454a;
    border-color: #000;
    color: #fff;
}

/*Mobile Number*/
.mobile-registration .flex-grid {
    justify-content: space-between;
}

.mobile-registration .flex-cell {
    display: flex;
	flex: auto;
    justify-content: center;
    width: 300px;
}

.mobile-registration .flex-cell > * {
    display: inline-block;
    margin: 0 1rem;
    max-width: 100%;
    width: 100%;
}

.flex-cell .ember-view:not(#ember12332):not(#ember2011) {
    display: none;
}

/*Homework Calender*/
.__filter-bar__7a335 {
    background-color: #2d3238;
    border-color: #000;
}

.legend-sample.homework::before {background-color: #90e039;}
.legend-sample.spelling-test::before {background-color: #1ce4b3;}
.legend-sample.quiz::before {background-color: #ffa900;}
.legend-sample.differentiated-homeworks::before {background-color: #007bff;}
.legend-sample.class-test::before {background-color: #8b5eff;}

.__personal-calendar__dff51 .cal-week-box,
.__personal-calendar-header__f5bad,
.__personal-calendar__dff51 .cal-column {
    border-color: #000;
}

.__personal-calendar__dff51 .cal-week-box {
    background-color: #3f454a;
}

.__personal-calendar-header__f5bad.today {
    background: #24292e;
}

.__personal-calendar-header__f5bad {
    background-color: #2d3238;
}

.__personal-calendar-task__77f6d {
	color: #fff!important;
}

.__personal-calendar-task__77f6d.calendar-item-homework {
    background-color: #6fa734;
    border-color: #7dbd37;
}

.__personal-calendar-task__77f6d.calendar-item-spelling-test {
    background-color: #31bd9b;
    border-color: #29cca4;
}

.__personal-calendar-task__77f6d.calendar-item-quiz {
    background-color: #dc9a17;
    border-color: #ffa900;
}

.__personal-calendar-task__77f6d.calendar-item-differentiated-homework {
    background-color: #1570d2;
    border-color: #007bff;
}

.__personal-calendar-task__77f6d.calendar-item-class-test {
    background-color: #7a5acc;
    border-color: #8b5eff;
}

/*Gradebook*/
.table-bordered,
.table-bordered>tbody>tr>td,
.table-bordered>tbody>tr>th,
.table-bordered>tfoot>tr>td,
.table-bordered>tfoot>tr>th,
.table-bordered>thead>tr>td,
.table-bordered>thead>tr>th {
    border-color: #000;
}

#student-gradebook .table {
    background-color: #3f454a;
}

#student-gradebook .table th {
    background-color: #2d3238;
    color: #fff;
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #474e54;
}

/*Pagination*/
.pagination a,
.pagination li > span {
    background: #2d3238!important;
    border-color: #000!important;
    transition: all 150ms ease-in;
}

.pagination a:hover {
    color: #094e81!important;
}

.pagination a.active {
    background: #007bff!important;
    border-color: #007bff!important;
    color: #fff!important;
}

/*Page loader*/

/*Notice board*/
.__notice-board__990b1 .notice-board-wrapper,
.__notice-board__990b1 .notice-board-day,
.__notice-board__990b1 .notice-board-header {
    border-color: #000;
}

.__notice-board__990b1 .notice-board-header {
    background-color: #2d3238;
}

.__notice-board__990b1 .notice-board-wrapper {
    background-color: #3f454a;
}

.__notice-board__990b1 .notice-board-day:hover {
    background: #5d646b;
}

/*Today*/
.__notice-board-month-day__ca39c.today,
.__notice-board-month-day__ca39c.today:hover {
    background-color: #1c7ac7;
}
}
