css:
@-moz-document url-prefix("https://www.ptt.cc/bbs/") {
    .bbs-content {
        font-family:  'Sarasa Term TC', monospace;
    }
    /*看板條*/
    
    #topbar-container {
        background-color: #5c3566;
        height: 40px;
        overflow-x: auto;
        overflow-y: hidden;
        position: fixed;
        top: 0;
        width: 100%;
        z-index: 99;
    }
    #topbar a:hover {
        background-color: #e9b96e;
        color: #204a87;
    }
    #logo:link,
    #logo:visited,
    #logo:hover {
        color: #e2e2e2;
    }
    /*標頭*/
    /*非文字區*/
    
    .article-metaline {
        background-color: #2b2b2b;
        margin: 0;
        padding: 0;
    }
    /*文字區*/
    
    .article-meta-value {
        background-color: #2b2b2b;
        color: #ce5c00;
        padding: 0 1ex;
    }
    .article-meta-tag {
        background-color: #2b2b2b;
        color: #fcaf87;
        padding: 0 1ex;
    }
    /*內文*/
    /*非文字區*/
    
    #main-container {
        background-color: #202020;
        margin: 0;
        padding: 0 0 3ex 0;
        position: relative;
    }
    /* 文字區*/
    /*基礎字色與背景色*/
    
    .bbs-screen {
        background-color: #2b2b2b;
        color: #ffffff;
        line-height: 100%;
        margin: 0 auto;
        padding: 0;
        width: 100%;
    }
    .f0 {
        color: #2e3436;
    }
    .f1 {
        color: #CC0000;
    }
    .f2 {
        color: #73d216;
    }
    .f3 {
        color: #c4a000;
    }
    .f4 {
        color: #3465a2;
    }
    .f5 {
        color: #75507b;
    }
    .f6 {
        color: #0e7a7a;
    }
    .f7 {
        color: #555753;
    }
    .hl {
        color: #eeeeec;
    }
    .hl.f0 {
        color: #888a85;
    }
    .hl.f1 {
        color: #ef2929;
    }
    .hl.f2 {
        color: #8ae234;
    }
    .hl.f3 {
        color: #fce94f;
    }
    .hl.f4 {
        color: #729fcf;
    }
    .hl.f5 {
        color: #ad7da8;
    }
    .hl.f6 {
        color: #75efef;
    }
    .hl.f7 {
        color: #d3d7cf;
    }
}
