css:
@-moz-document domain("netflix.com") {
@font-face {font-family: ''}
body, html {background: #000; color: #fff}
.pinning-header .main-header.has-billboard {background-color: #000}   
.progress-completed {background-color: #fff}
.progress-bar {background-color: #101010}
.jawBonePane .content-warning, .jawBonePane .overview {color: #fff}    
.jawBonePane .overview .evidence .evidence-text {color: #fff}    
.jawBonePane .overview .inline-list .list-label {color: #fff}
.jawBonePane .overview .inline-list a {color: #fff}
.nf-flat-button-primary {background-color: #000; border-color: #000; box-shadow: none}
.nf-flat-button-primary:hover {background-color: #fff; border-color: #fff; color:#000; box-shadow: none}
.nf-flat-button {color: #fff; border: 1px solid #000; box-shadow: 0 1px 2px #000; background-image: none; background-color: #000; box-shadow: none}
.nf-flat-button:hover {color: #000; border: 1px solid #fff; box-shadow: 0 1px 2px #fff; background-image: none; background-color: #fff; box-shadow: none}
.jawBoneContainer .menu li.current span {background-color: #fff}
.meta .match-score {color: #fff; text-transform: uppercase}
.star.default {color: #fff}
.jawBoneContainer .jawBonePanes .jawBonePane {color: #fff}
.akira-button.akira-button-red {background-color: #000; border-color: #000}
.akira-button.akira-button-red:hover {background-color: #fff; border-color: #fff; color: #000}
.reviews-modal .reviewsModalBody .memberReviews {color: #fff}
.pinning-header .pinning-header-container .main-header {background: #000}
.boxart-image-in-padded-container {opacity: 0.3}
.boxart-image-in-padded-container:hover {opacity: 1}
.fallback-text {display: none}
.title-card .title-card-focus-ring {border: solid 1px #505050}
.title-card .title-card-play:focus .play, .title-card .title-card-play:hover .play {color: #fff; opacity: 1}
.title-card .title-card-play .play, .title-card .title-card-play .play {color: #fff; opacity: 0.3}
.site-footer {display: none}
.footer-divider {display: none}
.account-section-link {color: #fff}
.responsive-account-container {color: #fff}
.account-section-item-disabled {color: #fff}
.account-section-heading {color: #fff}
h1 {color: #fff}
.profiles-gate-container {background: #000}
.list-profiles {background: #000}
.profile-button.preferred-action a, .profile-button.preferred-action span {background: #000; color: #fff; border: 1px solid #000}
.profile-button.preferred-action a:hover, .profile-button.preferred-action span:hover {background: #fff; color: #000; border: 1px solid #fff}
.pinning-header .main-header .logo {color: #fff}
.pinning-header .sub-header {background: #000}
.progress .summary {color: #fff; text-transform: lowercase}
.pinning-header .tabbed-primary-navigation .navigation-tab a {color: #505050}
.pinning-header .tabbed-primary-navigation .navigation-tab a:hover {color: #fff; text-decoration: underline}
.pinning-header .secondary-navigation:not(.menu-navigation) .show-dvd, .pinning-header .secondary-navigation:not(.menu-navigation) .show-kids {display: none}
.empty-notification {color: #fff; border:none}
.notifications .notification {background: #000; border:none; box-shadow: none}
.sub-menu.theme-lakira > .topbar {top: 0}
.billboard-row .billboard-originals.full-bleed-billboard .hero-vignette {background-image: linear-gradient(to bottom,rgba(0, 0, 0, 0) 0,rgba(0, 0, 0, 0) 15%,rgba(20, 20, 20, 0) 29%,rgba(0, 0, 0, 0.22) 44%,#000 68%,#000 100%)}




}
