css:
@-moz-document url-prefix("https://enrive.org") {
*
{
  font-family: Verdana,Geneva,sans-serif !important;
}
 
body {
    background-color: #0D0E0E;
        background: #0D0E0E!important;
    color: #E7F1FB;
    white-space: pre-wrap;
}
a:link, a:active  {
    color: #B4D0E8;
}
 
a:visited {
    color: #B4D0E8;
}
 
 
a:hover {
    color: #DDE489!important;
}
 
.OPpost {
        background: #0D0E0E!important;
    display:block;
    margin-left: 0.625rem;
    margin-bottom: 0.188rem;   
}
 
.faq {
    width: 43.750rem;
    height: auto;
    margin: 0 auto;
    font-size:1.02rem;
}
h2 {
font-size: 1rem;
font-weight: bold;
color: #B8CDEC;
border-bottom: 0.063rem dotted #0A1526;
}
 
.replyPost{
background-color:#282a2e!important;
border:1px solid #282a2e;
box-shadow: 5px 5px 10px #0D0E0E;
margin-bottom:4px;
margin-left:16px;
margin-top:4px;
}
 
.padding {
display:block;
margin-right: 0.188rem;
float: left;
color: #94A9D7;
}
.moderationForm input {
    border: 0;
    padding: 0.188rem;
    margin: 0.313rem;
    border-radius: 0.313rem;
    color: #527FDE;
    background: #E2E8EA;
    text-shadow: 0.063rem 0.063rem #1F7CE8;
}
.smallAnonAccBoxes {
    font-size: 0.8rem;
    width:6rem;
}
.anonForm {
    width: 43.750rem;
    margin: 0 auto;
    border: 0.188rem solid white;
    padding: 0.063rem 0.313rem 0.313rem 0.313rem;
    border-radius: 0.313rem;
    background: #333333;   
}
.formHeader {
    font-size: 1rem;
    color: #D3DBE6;
    text-align: center;
    background: #3E4146;
    padding: 0.250rem;
    margin-top: 0.188rem;
    margin-bottom: 0.438rem;
    font-weight: bold;
    margin-left: auto;
    margin-right: auto;
}
 
.anonForm input {
    border: 0;
    padding: 0.188rem;
    margin: 0.313rem;
    border-radius: 0.313rem;
    color: #242A3E;
    background: #E6E6E6;
    text-shadow: 0.063rem 0.063rem white;  
}
 
.anonForm input[type="text"]{
    width:7rem;
}
.anonForm input[type="password"]{
    width:7rem;
}
 
.anonForm label {
margin-left: 0.313rem;
}
.footer input {
    border: 0;
    padding: 0.188rem;
    margin: 0.313rem;
    border-radius: 0.313rem;
    color: #257EE0;
    background: #FFF6ED;
    text-shadow: 0.063rem 0.063rem #004EDD;
}
.footer a:link {
     text-decoration: none;
}
.moderationForm input:hover {
    background: #FFE0C7;
}
.footer input:hover {
    background: #FFE0C7;
}
.pagelink {
    text-decoration: none;
    color: #364D5A;
    padding: 0.625rem;
}
.selectedPage {
    font-weight:bold;
    margin-top: 0.625rem;
    text-decoration: none;
    color: #364D5A;
    padding: 0.625rem;
}
.divide {
    font-size: 1.2rem;
    font-weight: lighter;
    top: -0.063rem;
    color: #364D5A;
    position: relative;
    margin-left:0.4rem;
}
.hyperlinkCatalog {
    margin-top: 0.625rem;
    display:inline-block;
    margin-bottom: 0.625rem;
}
.pagelink:hover {
    background: #313131;
}
.paginationBar {
    float:left;
    display: inline-block;
    border: 0.063rem solid white;
    border-radius: 0.313rem;
    background: #4D4D4E;
    margin-left: 0.313rem;
    padding-left: 0.313rem;
    margin-bottom:0.313rem;
    vertical-align: center;
}
.selfModerationBar {
    float:right;   
}
.adminBorder {
    display: inline-block;
    border: 0.063rem solid white;
    border-radius: 0.313rem;
    background: #4D4D4E;
    margin-left: 0.313rem;
    padding-left: 0.313rem;
}
 
.hyperlinkCatalog {
    padding-right: 0.625rem;
    padding-left: 0.625rem;
}
.lockedImg, .stickyImg, .reportedImg {
    margin-bottom:-10.250rem;position:relative;bottom:10.125rem;
    margin-left: 0.3rem;
}
.reportedRed{
    margin-bottom:-11.450rem;position:relative;bottom:11.125rem;
    margin-left: 0.3rem;
}
.globalThreadContainer{
   
    border-top: 0.13rem dashed #798DB0;
    border-bottom: 0.13rem dashed #798DB0;
    margin-top: 0.625rem;
    margin-bottom: 0.625rem;
    padding-top: 0.625rem;
    padding-bottom: 0.625rem;
    background: none;
    padding-right:1rem;
    min-height:18.5rem;    
}
 
.offTopic {
    text-transform: uppercase;
    background: #DBDEE6;
    font-size: 0.6rem;
    font-family: Tahoma;
    border: 0.063rem dashed #B3B3B3;
    padding: 0.188rem 0.25rem 0.188rem 0.25rem;
    font-weight: bold;
    border-radius: 0.188rem;
    letter-spacing: 0.063rem;
    color: #6F6F6F;
    margin-left: 0.188rem;
    margin-right: 0.5rem;
}
.offTopic:hover {
    color: #313131;
}
.onTopicContainer {
    padding-right:1rem;
    min-height:18.5rem;
}
.offTopicContainer {
    background: none;  
    padding-right:1rem;
    min-height:18.5rem;
}
.offtopicThread a:link {
    color: #68808E;
}
.offTopicUsername{
    color: #FD3755;
    font-weight: bold;
}
.postTable{
    border: 0.063rem solid #536292;
    border-radius: 0.625rem;
    width: 30rem;
    margin: 0 auto;
}
.postTable table {
    padding: 0.188rem;
}
.postTitle{
    background: #424242;
    padding: 0.188rem;
    border-top-left-radius: 0.625rem;
    border-top-right-radius: 0.625rem;
    text-align: center;
    font-size: 1rem;
    font-weight: bold;
}
.num {
    text-align: center;
    padding-left: 9rem;    
}
.globalThread {
text-transform: uppercase;
background: white;
font-size: 0.6rem;
font-family: Tahoma;
border: 0.063rem dashed red;
padding: 0.188rem 0.25rem 0.188rem 0.25rem;
font-weight: bold;
border-radius: 0.188rem;
letter-spacing: 0.063rem;
margin-left: 0.188rem;
margin-right: 0.5rem;
}
.globalThread:hover {
    background: #F6F7FC;
}
.username {
    padding-right: 0.125rem;
}
.moderated {
margin-top:0.625rem;
}
 
.moderated span {
border-radius: 0.438rem;
border: 0.063rem solid #9AA5D7;
padding: 0.375rem;
background:white;
color: red;
font-size: 0.8rem;
font-family: Verdana;
margin-right:0.3rem;
}
.moderated span:hover {
background:#F5DDDE;
color: #511719;
}
.postInfo{
display:inline-block;margin-top:0.188rem;
}
 
.imageThumb, .mediaFile {
margin-right:0.7rem;float:left;clear:left;padding-top: 0.313rem;margin-bottom: 0.313rem;
}
.postCheckBox {
float:left;display: inline-block;margin-right:0.438rem;
}
.postTextBox {
padding-top: 0.313rem;
}
 
.replyForm {
font-size:0.7rem;
padding: 1rem;
margin: 0 auto;
background: none;
}
 
 
.postTextContainer {
margin-left: 0.625rem;
}
.threadEndHR {
clear:both;width:100%;border: none; height: 0; border-top: 0.063rem solid #B9C2D0;
}
.mediaFileAttrb {
display:inline-block;margin-right:0.3rem;
}
.username {
color: #DCDCDC;font-weight:bold;
padding-left:0.6rem;
padding-right:0.6rem;
}
.postNumber {
padding-left:0.6rem;
padding-right:0.6rem;
}
 
.greenText {
    color:green;
}
.spoiler>.greenText {
    color:transparent;
}
 
.quotedPostNumber {
    color:red;
}
.spoiler >.quotedPostNumber {
    color:black;
}
 
.spoiler {
    background-color:black;
    color:black;
}
.spoiler:hover {
    color: white;
    background-color:black;
}
.spoiler:hover > .greenText{
    color: white;
    background-color:black;
}
.spoiler:hover > .quotedPostNumber{
    color: white;
    background-color:black;
}
 
.spoiler >.userHyperlink {
    color:black;
}
.spoiler >.greenText >.userHyperlink {
    color:black;
}
.spoiler:hover > .userHyperlink{
    color: white;
    background-color:black;
}
 
.spoiler:hover >.greenText >.userHyperlink {
    color: white;
    background-color:black;
}
 
.omitted {
    color: #223463;
}
 
.header {
    padding: 0.313rem;
    background: #282a2e!important;
    box-shadow: 0 0.250rem 0.125rem -0.125rem gray;
    margin-bottom: 1.250rem;   
}
 
.header a:link {
    text-decoration:none;
    color:#B4D0E8;
}
.header a:visited {
    text-decoration:none;
    color:#B4D0E8;
}
 
 
 
.header a:hover {
    color: #FD3755!important;
}
 
.headerLoginForm input[type="text"]{
    width: 6rem;
    width: 6rem;
}
.headerLoginForm input[type="password"]{
    width: 6rem;
    width: 6rem;
}
 
.frontPageLoginForm input[type="text"]{
    width: 8rem;
    width: 8rem;
}
.frontPageLoginForm input[type="password"]{
    width: 8rem;
    width: 8rem;
}
 
.loginFormFrame input {
border: 0;
padding: 0.188em;
margin: 0.313em;
border-radius: 0.313em;
color: #6F7DD6;
background: #D8D8D7;
text-shadow: 0.063em 0.063em #8386DA;
}
 
 
.newThreadExpander {   
    width: 100%;
    text-align:center; 
    font-size: 1rem;
    color: #213A79;
    text-decoration: none;
    font-weight: bold;
    cursor: pointer;
}
.masterTitle {
    margin: 0 auto;
    text-align: center;
    font-size:1.2rem;
    margin-right:0.5rem;
}
 
.masterTitle span {
    color: #F3353E;
    font-weight:bold;
    font-size: 2rem;
}
 
.masterTitle a {
    text-decoration: none;
    font-size:2rem;
}
.postForm {
    text-align: center;
    margin: 0 auto;
}
 
.postFormContainer {
    width:100%;
    text-align: center;
}
.underBar {
    float:left;
    margin-left: 0.15rem;
}
.headerProfile {
    float: right;
}
.headerProfile input {
    display: inline-block;
}
.headerLeft {
    float: left;
}
.headerLoginForm {
    float: right;
}
.helpfulLinks {
    float: right;
    clear: right;
    margin-top: 0.188rem;  
}
.boardLink {
    margin-right: 1rem;
}
.postForm input, textarea {
    border: 0;
    padding: 0.188rem;
    margin: 0.313rem;
    border-radius: 0.313rem;
    color: #242A3E;
    background: #D6D5D3;   
    font: 0.813rem Ariel, sans-serif;  
}
 
.postContainer {
    border: 0.125rem solid white;
    border-radius: 0.313rem;
    padding: 0.313rem;
    width: 29.375rem;
    margin: 0 auto;
    color: #D2D7E2;
    background: #4D4D4E;
}
.anonACCName {
height: 1.250rem;width:8.125rem;margin-top:0.625rem
}
.anonACCPassword {
    width:8.125rem;
    height: 1.250rem;
}
.catalogContainer {
    padding-left: 35px;
    margin-bottom: 10px;
}
.catalogThread {
    float:left;
    width: 9.375rem;
    overflow:hidden;
    text-align: center;
    height: 250px;
    margin-top:1.6rem;
    border:2px solid #889CEE;
    background: #1F1F1F;
    margin-right: 15px;
}
.catalogMedia {
    padding-top: 0.3rem;   
}
 
.postTextBox {
    padding-top: 0.313rem; 
}
 
.postFormPostTextBox {
    width: 25.938rem;
}
 
.postTextContainer {
margin-left: 0.625rem;
}
 
.newThreadExpander {;
    display: none;
    width: 100%;
    text-align:center; 
}
 
.reportedWarning {
    clear:both;
    border: 1px dashed red;
    width: 50rem;
    text-align: center;
    padding:1rem;
    background: white;
    margin: 10px auto 10px auto;
        border-radius: 0.3rem;
}
 
.repliedPosts {
    margin-right:0.2rem;
    font-size:0.7rem;
}
}
