css:
@-moz-document url-prefix("https://monine.github.io/gitstars/") {

    /* a {
        color: #1d6fa5; / * #3498db from Cpp Reference * /
        color: #3b6e55; / * #56a07b from Astral App * /
    } */

    /***** Left-side Panel *****/

    /* Top Image */
    .app-name-img[data-v-6c8c68c1] {
        background-color: #202020;
        opacity: 0;
    }

    /** Body **/
    #sidebar[data-v-6c8c68c1] {
        background-color: #202020;
    }

    /* 'All stars' */
    .nav-item-badge[data-v-f953bdf2] {
        background-color: #282828;
    }

    /* footer */
    .sidebar-footer[data-v-6c8c68c1] {
        background-color: #202020;
    }

    /***** Right-side *****/

    /** Header 'X's starred Repositories' **/
    #header[data-v-472201d3] {
        background-color: #202020;
        border-bottom-color: #333333;
    }

    .user-avatar[data-v-472201d3] {
        opacity: 0.3;
    }

    .user-name a {
        color: #666;
    }

    /** 中文/English button **/
    .el-radio-button__inner {
        background-color: #333333;
        border-color: #333333;
    }

    .el-radio-button:first-child .el-radio-button__inner {
        border-left-color: #333333;
    }

    .el-radio-button__orig-radio:checked + .el-radio-button__inner {
        color: #aaa;
        background-color: #222;
        border-color: #333333;
        box-shadow: -1px 0 0 0 #222;
    }

    /** Github Fork button on top-right **/
    .octocat[data-v-472201d3] {
        color: #ccc;
        fill: #333;
    }

    /**** Middle Repos List ****/

    #subsidebar[data-v-178e8b70] {
        background-color: #202020;
        border-right-color: #333333;
        /* width: calc(409px + 350px); */
    }

    /* .content[data-v-413a6d93] {
        left: calc(410px + 350px);
    } */

    /** Developer/Repository Search **/

    #subsidebar-header[data-v-550871c0] {
        background-color: #202020;
        border-bottom-color: #333333;
    }

    .search-input[data-v-550871c0] {
        background-color: #202020;
        border-color: #333333;
        color: #999;
    }

    /* Search symbol */
    .fa-search[data-v-550871c0] {
        color: #333;
    }

    /** Sort dropdown **/
    .el-dropdown-menu {
        background-color: #202020;
        border-color: #333333;
    }

    .el-popper[x-placement^=bottom] .popper__arrow:after {
        border-bottom-color: #202020;
    }

    .el-popper[x-placement^=bottom] .popper__arrow {
        border-bottom-color: #444;
    }

    /* Row */
    .el-dropdown-menu__item:focus, .el-dropdown-menu__item:not(.is-disabled):hover {
        background-color: #333333;
    }

    /** Repo Row **/

    .repo-item a {
        color: #447f62;
    }

    /* Stars */
    .repo-footer[data-v-9199e5f0] {
        color: #1d6fa5;
    }

    /* Non-active */
    .repo-item[data-v-9199e5f0] {
        background-color: #161616;
        border-bottom-color: #333333;
    }

    .repo-item[data-v-9199e5f0]:hover {
        background-color: #222;
    }

    /* Active */
    .repo-item.active[data-v-9199e5f0] {
        background-color: #191c22; /* from #272c35; */
    }

    /* Tag */

    .el-tag {
        color: #999;
        background-color: #202020;
        border-color: #282828;
    }

    .tag-delete-btn[data-v-35ee93da] {
        background-color: #202020;
        border-color: #333333;
    }

    .tag-delete-btn.active[data-v-35ee93da], .tag-delete-btn[data-v-35ee93da]:hover {
        background-color: #333333;
        color: #aaa;
    }

    /* 'Are you Sure' Pop-up Panel */
    .el-popover {
        background-color: #202020;
        border-color: #333333;
        color: #aaa;
    }

    .el-popover__title {
        color: #666;
    }

    /* 'Yes', 'No' buttons */
    .el-button {
        background-color: #333333;
        color: #999;
    }

    .el-button:focus, .el-button:hover {
        background-color: #3f3f3f;
        border-color: #333;
    }

    .el-button--default {
        border-color: #333333;
    }

    .el-popper[x-placement^="right"] .popper__arrow {
        border-right-color: #444;
    }

    .el-popper[x-placement^="right"] .popper__arrow::after {
        border-right-color: #202020;
    }

    /**** Right Repo Content ****/

    /* while Loading */
    .repo-readme[data-v-413a6d93] {
        background-color: #161616;
    }

    .waiting[data-v-413a6d93] {
        color: #333;
    }

    /* Header */
    .repo-readme__header[data-v-4552ec5a] {
        background-color: #161616;
        border-bottom-color: #333333;
    }

    /** 'Add New Tag' field **/
    .el-input__inner {
        background-color: #161616;
        border-color: #333333;
        color: #aaa;
    }

    

    .el-input-group__append, .el-input-group__prepend {
        background-color: #333333;
        border-color: #333333;
    }

    .el-input__prefix, .el-input__suffix {
        color: #333;
    }

    /* Tag Suggestions dropdown */
    .el-autocomplete-suggestion__wrap {
        background-color: #202020;
        border-color: #333333;
    }

    .el-autocomplete-suggestion li {
        color: #aaa;
    }

    .el-autocomplete-suggestion li.highlighted, .el-autocomplete-suggestion li:hover {
        background-color: #333333;
    }

    /*** Body ***/
    .markdown-body[data-v-413a6d93] {
        background-color: #161616;
        color: #aaa;
    }

    .markdown-body h1,
    .markdown-body h2 {
        border-bottom-color: #666;
    }

    .markdown-body hr {
        background-color: #333;
        border-bottom-color: #333;
    }

    /** Syntax Highlighting **/

    .markdown-body[data-v-413a6d93] {
        color: #c0c0c0;
    }

    /* Link */
    .markdown-body a {
        color: #0366d6; /* alternative: #107ffc */
    }

    /* Image */
    .markdown-body img {
        background-color: #161616;
        opacity: 0.4;
    }

    /* Code */
    .markdown-body .highlight pre, .markdown-body pre {
        background-color: #202020;
    }

    /* Command */
    .markdown-body .pl-c1, .markdown-body .pl-s .pl-v {
        color: #1381ff; /* default #005cc5 */
    }

    /* keyword */
    .markdown-body .pl-k {
        color: #cd064d; /* #f92672 from Github Dark */
    }

    /* Comments */
    .markdown-body .pl-c {
        color: grey;
    }

    /* struct, class Var */
    .markdown-body .pl-e, .markdown-body .pl-en {
        color: #7aac18; /* #a6e22e from Github Dark */
    }

    /* method, function names */
    .markdown-body .pl-c1, .markdown-body .pl-s .pl-v {
        color: #14a6c2; /* from #66d9ef; */
    }

    /* String */
    .markdown-body .pl-pds, .markdown-body .pl-s, .markdown-body .pl-s .pl-pse .pl-s1, .markdown-body .pl-sr, .markdown-body .pl-sr .pl-cce, .markdown-body .pl-sr .pl-sra, .markdown-body .pl-sr .pl-sre {
        color: #008000;
    }

    /* Bash Variables */
    .markdown-body .pl-s .pl-s1, .markdown-body .pl-smi {
        color: #4fa2b3;
    }

    /* Block quote */
    .markdown-body blockquote {
        border-left-color: #333;
    }

    /* Table */
    .markdown-body table td, .markdown-body table th {
        border-color: #333;
    }

    .markdown-body table tr {
        background-color: #202020;
        border-top-color: #333;
    }

    .markdown-body table tr:nth-child(2n) {
        background-color: #282828;
    }

    /**** Notification Overlay ****/
    .el-notification {
        background-color: #202020;
        border-color: #333;
    }

    .el-notification__title {
        color: #999;
    }
}
