/* ==UserStyle==
@name           Dark Mode | E-Learning
@namespace      Cortex
@version        2.5.0
@description    Dark Mode 
@author         Cortex
@preprocessor   default
@var            color       Dark        "Dark Color"        #253447
@var            color       Darker      "Darker Color"      #1b2836
@var            color       Darkest     "Darkest Color"     #141d26
@var            color       Text-White  "Text White"        white
@var            color       Text-Grey   "Text Grey"         #8899a6
@var            color       Text-Link   "Text Hyperlinks"   #1ea1f2
==/UserStyle== */
@-moz-document domain("e-learning.tuhh.de")
{
    /*
  :root
  {
    --Dark: #253447;
    --Darker: #1b2836;
    --Darkest: #141d26;
    --Text-White: white;
    --Text-Grey: #8899a6;
    --Text-Link: #1ea1f2;
  }
    */
    *
    {
        font-family: "Montserrat" !important;
    }

    /* -- Backgrounds -- */
    #layout_wrapper #flex-header, #layout_wrapper > div.sticky-wrapper div, .sidebar .sidebar-widget-header, #layout_footer, select, #tabs a, td.toolbar, td.printhead, textarea, section.course-statusgroups article section table tfoot tr td, table.blank, td.blank, td.onlineinfo, td.blanksmall, #layout_container, .message_body, .studip-widget .widget-header, #notification_marker, article.studip header, table.default > tfoot > tr > td, .action-menu .action-menu-content, #blubber_threads > .thread > .contextinfo, #blubber_threads .comments .posting, #blubber_threads .comments, #blubber_threads li.more, form.default fieldset > legend, .grid-stack-item-content .widget-content, #globalsearch-searchbar #globalsearch-results article > header, #globalsearch-searchbar #globalsearch-results article mark, #tabss, #barBottomContainer, #tabs li
    {
        background: var(--Dark) !important;
    }

    th, div.messagebox_info, section.contentbox header, table td.printcontent, #layout_content > div, footer, blockquote, .ui-widget-content, .content_seperator, .content_seperator td, #index .messagebox, #notification_container *, .grid-stack-item-content .widget-header, .grid-stack-item-content .widget-actions, #forum .posting
    {
        background: var(--Darker) !important;
    }

    body, #layout_page > div, #layout_page > div > div, input, .sidebar, html, #tabs li.current a, blockquote > .author, .ui-state-default, .ui-menu-item .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default, table.dates .nextdate, table#schedule_data thead tr td, table#schedule_data tbody td:first-child, #page_title_container, .helpbar, #layout_page, .tooltip .tooltip-content, #globalsearch-searchbar #globalsearch-list, #tabs li.current
    {
        background: var(--Darkest) !important;
    }

    /* -- Text -- */
    *, body, table.default > caption
    {
        color: var(--Text-Grey) !important;
    }

    a, a:link, a:visited
    {
        color: var(--Text-Link) !important;
    }

    select, input
    {
        color: var(--Text-White) !important;
    }

    /* -- Buttons -- */
    button, a.button, button.accept.button, .opengraph
    {
        border: 1px solid #1da1f2 !important;
        background: var(--Darkest) !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 4px 12px !important;
        -webkit-transition: background 200ms linear, border 200ms linear !important;
        -ms-transition: background 200ms linear, border 200ms linear !important;
        transition: background 200ms linear, border 200ms linear !important;
    }

    button:hover, a.button:hover, button.accept.button:hover, .opengraph:hover
    {
        background: var(--Dark) !important;
        border: 1px solid black !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 4px 12px !important;
    }

    select, #globalsearch-searchbar #globalsearch-input
    {
        border-color: var(--Text-Link);
        border-radius: 10px;
        padding-left: 10px;
    }
}

@-moz-document domain("e-learning.tuhh.de")
{
    /* - Individual - */
    div.messagebox_info, div.messagebox_success
    {
        background-color: var(--Dark);
    }

    #tabs
    {
        padding: 0;
    }

    #tabs li a img
    {
        display: inline-block;
        list-style-type: none;
    }

    #tabs li.current a img
    {
        margin-left: 2px;
    }

    #tabs, #layout_wrapper #flex-header #barTopMenu, div.index_main
    {
        text-align: center !important;
        margin-left: auto !important;
        margin-right: auto !important;
        background: var(--Dark);
    }

    #tabs li
    {
        padding: 0 3px 0 3px;
    }

    #layout_wrapper #flex-header #barTopMenu li
    {
        margin: 0 6px 3px 6px;
    }

    #tabs2 > li.current:after, #tabs > li.current:after
    {
        background-color: red;
    }

    .quicksearchbutton
    {
        z-index: 1;
    }

    table.default > caption
    {
        text-align: center !important;
        font-weight: 700;
    }

    input
    {
        height: 20px !important;
        font-size: 13px !important;
        padding-left: 5px !important;
    }

    .quicksearchbutton
    {
        border: 1px solid #1da1f2 !important;
        background: var(--Darkest) !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 1px 5px !important;
        -webkit-transition: background 200ms linear, border 200ms linear !important;
        -ms-transition: background 200ms linear, border 200ms linear !important;
        transition: background 200ms linear, border 200ms linear !important;
    }

    .quicksearchbutton:hover
    {
        background: var(--Dark) !important;
        border: 1px solid black !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
    }

    tr:nth-child(odd), #MediaLinks > ul > li:nth-child(odd)
    {
        background: var(--Dark);
    }

    tr:nth-child(odd), #MediaLinks > ul > li:nth-child(even)
    {
        background: var(--Darker);
    }

    tr *, #layout_content > div, #layout_content > div p, section.contentbox, section.contentbox *, .postprofile, .ui-widget-content, textarea, .ui-state-default, .ui-menu-item .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default, #layout_wrapper #layout_page .tabs_wrapper
    {
        border: 0px solid transparent !important;
    }

    #tabs li.current a img
    {
        background: red;
        border-radius: 5px;
    }

    .icon-shape-door-enter
    {
        background-color: red;
        border-radius: 3px;
        transform: scale(1.3);
    }

    table td.printhead2, table td.printhead3, table td.printhead4, table td.printhead5
    {
        background: #ff7f50;
    }

    table td.printhead1, table td.printhead4, table td.printhead3, table td.printhead2
    {
        background: linear-gradient(rgba(15, 84, 128, 1), rgba(15, 84, 128, 1)), url(https://e-learning.tu-harburg.de/studip/assets/images/content_object_arr-down.png);
        border: 0;
        border-radius: 5px;
    }

    #MediaLinks > ul > li > div
    {
        padding: 0px 0px 0px 20px !important;
        line-height: 18px;
    }

    #notification_marker, #header_avatar_menu .action-menu-icon, #page_title_container, #tabs, #barBottomContainer, #globalsearch-searchbar #globalsearch-input
    {
        border-color: transparent;
        border: transparent;
    }

    #globalsearch-searchbar
    {
        color: white !important;
    }

    body:not(.fixed) #barTopMenu > li > a .navtitle, body:not(.fixed) #barTopMenu > li > label .navtitle
    {
        opacity: 1 !important;
    }

    body:not(.fixed) #barTopMenu > li:nth-child(2)
    {
        padding-left: 15px;
        padding-right: 15px;
    }

    body:not(.fixed) #barTopMenu > li:nth-child(3)
    {
        padding-left: 20px;
        padding-right: 20px;
    }

    article.studip
    {
        border-color: transparent;
    }

    #layout_content
    {
        max-width: 2400px;
    }

    /* Sidebar */
    .sidebar, .sidebar:before
    {
        border: 0px solid transparent;
    }

    .sidebar .sidebar-widget
    {
        border: 1px solid transparent !important;
        background: transparent !important;
        padding: 10px 5px !important;
    }

    .sidebar .sidebar-widget-header
    {
        text-align: center;
        border-radius: 20px 20px 0 0;
        color: var(--Text-White) !important;
    }

    #layout-sidebar > section > div > div.sidebar-widget-content > ul > li
    {
        border: 1px solid #1da1f2 !important;
        background: var(--Darkest) !important;
        color: #1da1f2 !important;
        border-radius: 100px !important;
        padding: 2px 12px !important;
        margin: 5px 0px !important;
        text-align: center;
        -webkit-transition: background 200ms linear, border 200ms linear !important;
        -ms-transition: background 200ms linear, border 200ms linear !important;
        transition: background 200ms linear, border 200ms linear !important;
    }

    #layout-sidebar > section > div > div.sidebar-widget-content > ul > li:hover
    {
        background: var(--Dark) !important;
        border: 1px solid black !important;
    }

    .widget-links.sidebar-views > li.active, .widget-links.sidebar-navigation > li.active
    {
        box-shadow: none;
    }

    .widget-links.sidebar-views > li.active:after
    {
        border: 9px solid rgba(255, 255, 255, 0);
        border-left-color: #fff;
    }

    .widget-links.sidebar-navigation > li.active:after
    {
        border: 9px solid rgba(255, 255, 255, 0);
        border-left-color: #fff;
    }

    #layout_content > section
    {
        border: transparent;
    }

    /* Login Screen */
    #layout_wrapper > div:nth-child(3) > div > table > tbody > tr > td
    {
        text-align: center;
    }

    #layout_wrapper > div:nth-child(3) > div > table > tbody > tr > td > form > table
    {
        padding: 0px 0px 0px 220px;
    }

    /* Das Help Bild Dark */
    a.helpbar .helpbar-title
    {
        background: /* top, transparent black, faked with gradient */
        linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), /* bottom, image */
        url(https://e-learning.tu-harburg.de/studip/assets/images/icons/white/question-circle.svg);
        -webkit-background-size: 24px 24px;
        -moz-background-size: 24px 24px;
        -o-background-size: 24px 24px;
        background-position: right top;
        background-repeat: no-repeat;
        margin: 4px 4px -2px;
        overflow: hidden;
        padding: 2px 0 10px 6px;
    }

    /* Startsetite Bild Dark*/
    div.index_container div.index_main
    {
        background: /* top, transparent black, faked with gradient */
        linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), /* bottom, image */
        url(https://e-learning.tu-harburg.de/studip/assets/images/startseite.jpg);
    }

    div.index_container div.index_main footer, div.index_container div.index_main nav
    {
        background-color: #ffffff00;
    }

    /* - Do not Display - */
    a.helpbar-container
    {
        visibility: hidden;
    }

    div#layout-sidebar div.sidebar-image img, .sidebar-title, .sidebar-context, .helpbar, #layout_wrapper #flex-header #barTopStudip, #barBottomLeft, #barTopFont
    {
        display: none;
    }

    .unread
    {
        color: red !important;
        background: #ff00002e !important;
    }

    .js .drag-and-drop
    {
        background-color: var(--darkest);
        margin-left: 10px;
    }

    dl dt
    {
        padding-bottom: 1px;
        padding-top: 1px;
    }

    dl dt:nth-child(1)
    {
        padding-bottom: 1px;
        padding-top: 10px;
    }

    #blubber_threads > .thread, #blubber_threads .posting, #blubber_threads .posting *, #blubber_threads .posting > .writer, #threadwriter > .row, form.default fieldset > legend, form.default fieldset, .grid-stack-item-content
    {
        border-color: var(--Darker);
    }

    #layout_wrapper #layout_page #layout_container
    {
        margin: 0 auto;
    }

    #current_page_title
    {
        text-align: center;
        font-weight: 800;
    }

    img[src*="https://e-learning.tuhh.de/studip/assets/images/icons/black/staple.svg"]
    {
        filter: /* for demonstration purposes; originals not entirely black */
        contrast(1000%)/* black to white */
        invert(100%)/* white to off-white */
        sepia(100%)/* off-white to yellow */
        saturate(10000%)/* do whatever you want with yellow */
        hue-rotate(10deg);
    }

    img[src*="https://e-learning.tuhh.de/studip/assets/images/icons/black/outbox.svg"]
    {
        filter: /* for demonstration purposes; originals not entirely black */
        contrast(1000%)/* black to white */
        invert(100%)/* white to off-white */
        sepia(100%)/* off-white to yellow */
        saturate(100000000%)/* do whatever you want with yellow */
        hue-rotate(290deg);
    }

    .opengraph-area
    {
        max-width: 400px;
        max-height: 100px !important;
    }

    .opengraph .image
    {
        width: 60px;
        height: 60px;
        margin-left: 20px;
    }

    #forum .posting
    {
        margin-left: 10px;
    }

    #header_avatar_menu .action-menu-content
    {
        right: -124px;
    }

    #globalsearch-list
    {
        border: 50px !important;
        border-color: black !important;
    }

    #globalsearch-list *
    {
        border: 0px !important;
    }

    #barBottomContainer
    {
        top: 0;
        order: 1;
        position: relative;
        justify-content: center;
    }

    #layout_wrapper
    {
        padding-top: 0;
    }

    #layout_wrapper #flex-header
    {
        order: 0;
    }

    #layout_page
    {
        order: 1;
    }

    #layout_footer
    {
        order: 3;
    }
}
