css:
@-moz-document domain(ololo.to) {

:root
  {
    --color: rgba(0, 0, 0, 0.96);
    --accent: blueviolet;
    --layer: rgba(40,40,40,0.8);
    --text: white;
  }

.results-filter .label,.article-container{color:var(--accent);text-align: center;opacity: 1}

body,.header-inner,.header-hp,.results .item  {background: var(--color) !important;background-color: var(--color) !important;}

.jotform-form,.supernova,supernova body,.form-all {background-color: var(--layer) !important;}

.results-filter-nav li a {background-color: var(--layer) !important;box-shadow: 0 0 5px 1px var(--accent)}
.results-filter-nav li a:hover,.results .item:hover {background-color: var(--black) !important;box-shadow: 0 0 10px 2px var(--accent)}
.results .item,.results-filter-nav li a{border:none}

.header-hp,.results .item,.header  {box-shadow: 0 0 5px 1px var(--accent)}

h3,h1,li,p,h2,.text-center,.results-filter .label,.results .item  {color: var(--accent) !important;}

a  {color: #43a3db !important;}

.search-btn,.results .item  {
    background-color: var(--layer);
    border: none;
    box-shadow: 0 0 8px 1px var(--accent);  }
.search-btn:hover  {background-color: var(--layer);box-shadow: 0 0 15px 2px var(--accent);}

.search-input  {
    background-color: var(--layer);
    border: none;
    box-shadow: 0 0 10px 1px var(--accent);
    color: var(--text);  }
.search-input:hover  {box-shadow: 0 0 15px 2px var(--accent);  }

.article-container  {
    background-color: var(--layer);
    box-shadow: 0 0 8px 1px var(--accent);
    border: none;  }
.article-container h1,p,li  {text-align: center;}

.video-detail {
    background-color: inherit;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
    border: 2px solid var(--color);
}
.form-textbox, .form-textarea {background: var(--layer);border: 1px solid var(--accent);color: var(--accent);}
}
