css:
@-moz-document domain("okcupid.com") {
body.fullwidth #page #main_content, #p_profile .redux-true, body {
    background: #232323;
}
/* NAVIGATION */
 #navigation, #navigation .nav-right {
    background: #f30ac5;
}
.actionbar2015 {
    background-color: #34bef7;
}
.flatbutton.teal {
    background-color: #000;
    color:#4fc5d0;
}
.flatbutton.teal:hover {
    background-color: #4fc5d0;
    color:#f30ac5;
}
.userinfo2015-basics-edit-icon {
    color:#000;
}
.nav-item li>a:hover {
    color: #000;
}
.nav-notifications .badge.pink, .nav-notifications .nav-notifications--attention .badge {
    background: #34bef7;
}
.nav-notifications .badge {
    box-shadow: 0 0 0 2px #f30ac5;
}
.nav-user-menu {
    background: #f30ac5;
    color:#fff;
}
.nav-user-menu li .nav-user-menu-item:hover {
    color: #fff;
}
.nav-user-menu li .nav-user-menu-item {
    color: #000;
}
/* HOMEPAGE */
.discovery-section--interests .discovery-row .sliding-pagination-fade.right {
    background-color: rgba(52,190,147,0);
    background-image: -webkit-linear-gradient(-1turn,rgba(52,190,147,0),#34bef7);
    background-image: linear-gradient(90deg,rgba(52,190,147,0),#34bef7);
}

.discovery-row .sliding-pagination-fade.right {
    background-color: rgba(42,47,53,0);
    background-image: -webkit-linear-gradient(-1turn,rgba(42,47,53,0),#2a2f35);
    background-image: linear-gradient(90deg,rgba(42,47,53,0),#2a2f35);
}

.interest-search-title, .discovery-section-content-rows .discovery-row-title {color:#fff !important;}
.user-photoAndText.isBlueTheme .user-photoAndText-details, .user-photoAndText.isBlueTheme:hover .user-photoAndText-details, .user-photoAndText.isBlueTheme:hover .user-photoAndText-username {color:#f30ac5;}
.discovery-section-title-text {
    color: #f30ac5;
    padding-top:20px;
    padding-bottom:10px;
}
.user-photoAndText-username {color:#34bef7;}


 .discovery-section--interests {
    background-color: #34bef7;
    background-image: url(https://i.imgur.com/tD9LtiC.png);
    background-repeat: no-repeat;
    background-position: left bottom;
}
/* NOTIFICATIONS */
#event_display_body {
    background: #000;
}
#event_display_body .orbit_event {
    border-top: 1px solid #f30ac5;
}
#event_display_body .orbit_event:first-child {
    border-color: #f30ac5;
}
#event_display_body .orbit_event .info .user {
    color: #f30ac5;
}
#event_display_body .orbit_event .info .action {
    color: #00ff72;
}
/* PROFILE */

/* HEADERS */
 .profile2015-header, body.fullwidth #page .page-featured, .discovery-section--interests, .qm:before {
    background-color: #34bef7;
    background-image: url("https://i.imgur.com/tD9LtiC.png");
    background-repeat: no-repeat;
    background-position: left bottom;
}
.userinfo2015-basics-username {
    color:#f30ac5;
}
.userinfo2015-basics-username-online-icon.isOnline {
    background: #00ff72;
}
.userinfo2015-basics-asl {
    color: #000;
}
.profile-buttons-actions-action.profile-buttons-actions-pass {
    background: #ff3f3f;
}
.profile-buttons-actions-action.profile-buttons-actions-like {
    background:#00ff72;
    color:#000;
}
.profile-buttons-actions-action .okicon {
    color: #000;
}
/* PROFILE INTERIOR TEXT SUMMARIES */
.profile-essay {
    background: #000;
    border: 0px solid #e4e4e8;
}
h2.profile-essay-category {color:#34bef7;}
 .profile-essay-title, .profilesection-title, .morePhotos2015-title-btn {
    color:#f30ac5;
}
.essays2015-essay, .essays2015-essay-content {background:#000;}
.profile-essay-contents, .essays2015-essay-content {
    color:#fff;
}
.profile-essays-expander-text {
    color: #34bef7;
}
.whitebg .profile-essays-expander-bg {
    background-image: linear-gradient(hsla(0, 0%, 100%, 0), #232323 80%, #232323);
}
.essays2015--redux .essays2015-essay-title-text {
    border: none;
    background:#000;
}
.profile-essay-respond {
    border-top: 0px solid #e4e4e8;
}
.essays2015-essay-edit-action:before {
    color:#fdcd30;
}
.essays2015-essay-edit-action {
    padding:0 0 0 25px;
    margin:15px 0 0 0;
    border: none;
    color:#fdcd30;
}
.essays2015--redux .essays2015-essay-title-text .okicon {
    color: #f30ac5;
}
.essays2015-button--add-passion {
    color: #f30ac5;
    border: 1px solid #f30ac5;
}
/* PROFILE SIDEBAR */
 .sidebar-linked-account {
    background: #000;
    border-bottom: 0px;
    padding-top: 10px;
}
.sidebar-linked-account-image {
    top: 10px;
    left: 15px;
}
.whitebg .details2015-section .icon {
    fill: #f30ac5;
}
.sidebar-linked-account-text-label {
    color: #f30ac5;
}
.sidebar-linked-account-text-link {
    color:#e4edfd;
}
.matchprofile-details-text {color: #f30ac5 !important;}
.details2015-section-edit-button:hover {
    background: #000;
    box-shadow: 0 2px 4px rgba(201, 9, 163, 0.35);
}
.details2015-section-edit-button:hover .details2015-section-item {
    color:#c909a3;
}
.sidebarNudge2015-text {
    color: #00ff72;
}
.details2015-section-edit:before {
    color:#fdcd30 !important;
}
.details2015-section td {
    color: #34bef7 !important;
}
.lookingfor2015 {
    border:none;
    background: #000;
    color: #c909a3;
}
.lookingfor2015 i {
    color:#fdcd30;
}
.quickview-top {
    background: #34bef7;
}
.quickview-top-username {
    color: #c909a3;
}
.quickview-top-demo {
    color: #000;
}
/* CATEGORY CONTROL */
.essays2015-button--remove-passion {color: rgba(255,255,255,0.4);}
/* PROFILE MODAL */
.OkModalContent-main p {color:#fff !important;}
.essays2015 .flatbutton.blue {
    background-color: #c909a3;
    color:#fff;
}
.essays2015 .flatbutton.blue:hover, .essays2015 .flatbutton.blue:active {
    background-color: #34bef7;
    color:#fff;
}
.oknf--gray .oknf-label {
    color: #fff;
}
/*UNMATCH MODAL*/
#confirmationModal .layout-icon {
    background: #f30ac5;
}
/* FIRST MESSAGE - WHAT THEY SAID */
.firstmessage2017 {background-color:#34bef7 !important;}
.firstmessage2017:after, .firstmessage2017:before {
    border-top: 14px solid #34bef7 !important;
}
.firstmessage2017-body-time {
    color: #000 !important;
}
.firstmessage2017-body-flag {
    color: #000 !important;
}
/* PROFILE FOOTER */
 .genreblock-info {
    color:#f30ac5;
}
.traits2015-description, .traits2015-traits-trait {
    color: #34bef7;
}
/* FIRST MESSAGE */
 .firstmessage2017 {
    background-color: #000;
}
.firstmessage2017-heading {
    color: #f30ac5;
}
.firstmessage2017-body {
    color: #c909a3;
}
.firstmessage2017:after, .firstmessage2017:before {
    border-top: 14px solid #000;
}
/* PROFILE QUESTIONS */
.questionspage, .oknf--gray label {
    color: #fff;
}
.flatbutton.blue, .flatbutton.blue:active, .flatbutton.blue:hover, .flatbutton.blue:focus {
    background-color: #f30ac5;
}
 .questions-selfview-more a, .pageable-link {
    color: #fdcd30;
}
.questions-selfview-question {
    background:#000;
    color:#f30ac5;
}
.questions-selfview-question-skip {
    color:#fdcd30;
}
.flatbutton, .flatbutton.okblue {
    background-color: #f30ac5;
    color:#000;
}
.questions-selfview-question-text {
    color: #fff;
}
/* PROFILE PHOTOS */
.selfviewPhotos-breadcrumbs-album-title, .selfviewPhotos-caption-text {color:#fff;}
/* MATCHES */
 #match-filters .match-status .open-basic-filter {
    color: #000;
    border-bottom: 1px solid #e4edfd;
}
#match-tags .tag .clear-tag {
    color: #000;
}
#match-tags .clear-all {
    background: #000;
}
#match-tags .tag {
    background: #f30ac5;
}
#match-filters .match-status--advanced-filters-open .match-status-screen, #match-tags.advanced-filters-open .screen {
    opacity:0;
}
.match_card_wrapper .match_card, .usercard {
    background: #000;
}
.match_card_wrapper .image_wrapper .matchcard-image-indicator--likedyou {
    box-shadow: inset 0 -10px 0 #f30ac5;
}
.match_card_wrapper .image_wrapper .matchcard-image-indicator--likedyou:before {
    background: rgba(243,10,197,.8);
}
.match_card_wrapper .image_wrapper .matchcard-image-indicator-info {
    color: #34bef7;
}
#match-filters .match-filters-in-results .in-results-screen {
    background: rgba(35, 35, 35, 0.4);
}
div.profile_info .username .name, .userInfo-username {
    color: #f30ac5;
}
div.profile_info .userinfo, .userInfo-meta {
    color: #34bef7;
}
.match-info-percentage {border-radius: 0;}
.match_card_wrapper .match_card_text .percentages .percentage_wrapper.match {
    color: #00ff72;
}
.match_card_wrapper .match_card_text .percentages .percentage_wrapper.enemy {
    color: #ff3f3f;
}
.blank_state_wrapper .blank_state>h2, .blank_state_wrapper .blank_state>p {
    color:#e4edfd;
}
.binary_rating_button.flatbutton.silver.liked, .binary_rating_button.liked, .binary_rating_button.flatbutton.silver.liked:active, .binary_rating_button.liked:active {
    background: #f30ac5;
    border-color: #000;
    color: #fff;
}
/* Report */
.oknf-radio-label {color:#fff;}
.oknf--gray .oknf-radio input:not([disabled]):checked~.oknf-radio-label, .oknf--gray .oknf-radio input:not([disabled]):hover~.oknf-radio-label {color: #34bef7;}
/* Random Interest Lightbox Selector */
.reactmodal-header {
    background: #f30ac5 !important;
    border-bottom: 1px solid #fff !important;
    color: #fff !important;
}
.reactmodal-contents {
    color: #fff;
}
.reactmodal {
    background: #232323;
}
.gradient-overflow-container:after {
    box-shadow: none !important;
}
.passionsPickerModal-title {padding:30px 0;color: #f30ac5;}
.passionsPickerModal .passionsPicker-buttons-pickmanybuttons-button {
    color: #fff;
    border-color: #fff;
}
.passionsPickerModal .passionsPicker-button--next {
    background-color: #f30ac5;
    color: #fff;
}
.passionsPickerModal .passionsPicker-buttons-pickmanybuttons-button--selected {
    color: #fff;
    background: #f30ac5;
}
/* LIKES PAGE */
 body.fullwidth #page .page-featured h1 {
    color: #f30ac5;
}
#userRows-app {
}
.tabs-item.is-active a:after {
    border-bottom: 4px solid #f30ac5;
}
.tabs-item a {
    color: #000;
}
.userrow {
    border-top: 2px solid #f30ac5;
    background: #000;
}
.userrow-boost-heading, .userrow-bucket-heading, .userrow-username {
    color: #f30ac5;
}
.userrow-content, .userrow-hidden, .userrow-thumb {
    color: #34bef7;
}
.userrow-activity-hide, .userrow-activity-like, .userrow-activity-time {
    color: #00ff72;
}
.onlinedot {
    background: #00ff72;
}
.userrow-percentage {
    color: #f30ac5;
}
/* PRIVACY POLICY */
.monolith p, .monolith li {
    color: #fff;
}
/* FOOTER */
 body.fullwidth #page footer.page-section {
    background-color: #34bef7;
    padding: 50px 0;
    width: 100%;
}
#p_profile #main_content {
    background: #232323;
}
#p_profile footer {
    background: #34bef7;
}
.footer, .footer a {
    color: #e4edfd;
}
.footer .footer-social-circle {
    border: 1px solid #f30ac5;
    border-radius: 100%;
    color: #f30ac5;
}
/* DOUBLE TAKE */
 .qm {
    background:#232323;
}
.qmcard {
    background: #000;
}
.cardsummary, .qmcard-blurb {
    color: #f30ac5;
}
.qmessays-essay p {
    color: #fff;
}
.qmessays-essay h2 {
    color: #f30ac5;
}
.quickmatch-profiledetails-text {
    color: #34bef7;
}
.qm-lookingfor {
    background: #000;
    border: none;
}
.qm-lookingfor-intro, .qm-lookingfor p {
    color: #f30ac5;
}
.qm-yml-title {
    color: #f30ac5;
}
/* BOOKMARKS */
#userRows-app, #userRows-app h1, #userRows-app h2, #userRows-app h3, #userRows-app h4 {color: #f30ac5;}
/* PAID CONTENT */
.panel-boost {
    background: #f30ac5;
    -webkit-box-shadow: -2px 0px 5px -2px rgba(0,0,0,0.75);
-moz-box-shadow: -2px 0px 5px -2px rgba(0,0,0,0.75);
box-shadow: -2px 0px 5px -2px rgba(0,0,0,0.75);
}
.boost-report-viewers, .boost-running-viewers {
    color: #34bef7;
}
.boost-timer-bonus {
    background: #34bef7;
    color: #fff;
}
.boost-timer-bonus .tail {
    border-left: 8px solid #34bef7;
}
.boost-timer {
    background: #000;
}
/* MODERATOR MADNESS */
 #main_content {
    background: #000;
}
div.tabbed_heading ul.page_tabs li a {
    color: #f30ac5;
}
div.tabbed_heading ul.page_tabs li a:hover {
    color: #fff;
}
div.tabbed_heading ul.page_tabs li.active a, div.tabbed_heading ul.page_tabs li.active a:hover {
    color: #fff;
}
div.tabbed_heading ul.page_tabs li.active {
    color: #fff;
    border-bottom: 3px solid #fff;
}
.flag_footnote {
    background: #4fc5d0;
    padding: 10px;
    color: #fff;
}
#username a, .flag_text a {color: #f30ac5;}
.flag_text {color:#fff;}
#left_column .flag_count {
    color: #fff;
}
#left_column #reports {color:#fff;}
div.tabbed_heading h1 {
    color: #f30ac5;
}
aside.grayaside {
    background: #000;
}
#details strong, #flags strong {color:#fff;}
#details span, #flags span {color:#f30ac5;}
.comments strong {color:#f30ac5;}
.comments p {color:#fff;}
.first h3 {color:#f30ac5;}
aside.grayaside li, aside.grayaside p {
    color:#949aa6;
}
aside.grayaside h3 {
    color: #f30ac5;
}
.vote_buttons .flatbutton.blue:nth-child(1n) {
    background-color: #00d280;
}
.vote_buttons .flatbutton.blue:nth-child(2n) {
    background-color: #fdcd30;
}
.flatbutton.clear.light-bg:not([disabled]), .flatbutton.gray, .flatbutton.gray.hover, .flatbutton.gray:hover, .flatbutton.silver, .flatbutton.silver.hover, .flatbutton.silver:hover, .flatbutton.white, .flatbutton.white.hover, .flatbutton.white:hover {
    color: #949aa6;
}
/* YOU ARE WINNER */
#p_flagmod h2, #p_flagmod p {color:#fff;}
/* GUIDELINES */
.monolith #main_column h2, .monolith #main_column h3, .monolith #main_column h4 {color: #f30ac5;}
.monolith .help_section li, .monolith .help_section p, .help_section .padded_table {color:#fff;}

/* SETTINGS AND SUBSCRIPTION */
.settings-field-title, .settings-field-title.oknf-label, .settings-title {color: #f30ac5;}
.settings-subscriptions, .settings-table--3column .settings-table-cell, .settings-connections, .oknf-checkbox-label, .settings-field.settings-field--resetpassword, .settings-field-description {color:#fff;}
}
