css:
/*
 * Name:			Darker CoinMarketCap
 * Author:			Atx
 * Version:			v1.2
 * Latest update:		14.07.2018
 */

@-moz-document url-prefix("https://coinmarketcap.com/") {
body, .reddit-entry > .linkflairlabel, td.dropdown > button, .table-responsive > .table tbody tr[id], input#conversion-amount, .chosen-container > a, .chosen-container .chosen-results > li, .chosen-container .chosen-results

{
	background-color: #1C1C1C !important;
}

.coin-summary-item-header, .navbar-default, #currencies > thead > tr > th, .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus, #currencies.dataTable > tbody > tr:nth-child(2n), .toolbar-tool-icon, #at-expanded-menu-host .at-expanded-menu-load, .highcharts-menu, .floating-header thead > tr > th, #markets-table > tbody > .odd, textarea.form-control, .timeline-Widget, #reddit > .rembeddit, #historical-data > div > .table-responsive > table > thead, .table-responsive > table > tbody > tr:nth-child(2n), .ranges li, #at-expanded-menu-host .at-expanded-menu-close, .pagination > li > a, .pagination > li > span, .nav-tabs .section-heading, .form-control.tt-input, .tt-dataset-currencies, .tt-dataset-exchanges, #exchange-markets > tbody > tr:nth-child(2n), #currencies-all > tbody > tr.even,
.col-xs-12 > select.form-control, #assets_wrapper > #assets > tbody > tr.even, .trending-gainers-table > tbody > tr.even, .trending-losers-table > tbody > tr.even, #currencies-volume > tbody > tr.even, #trending-recently-added > tbody > tr.even, #calculator.well, .chosen-drop, .chosen-search > input[type="text"],
.active-result.group-option.highlighted
{
    background-color: #2C2C2C !important;
}

.sitenav .navbar-nav > li > a, .btn-default, .dropdown-menu, #currencies.dataTable > tbody > tr:nth-child(2n+1), .watchlist-star.is-deselected, .nav-tabs > li > a:hover, .nav-tabs > li > a:focus, .highcharts-menu .highcharts-menu-item:hover, #markets-table > tbody > .even, .table-responsive > table > tbody > tr:nth-child(2n+1), .pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus, .dropdown-toggle, .tt-dataset-currencies > .tt-suggestions > .tt-suggestion:hover, .tt-dataset-exchanges > span > div:hover, #exchange-markets > tbody > tr:nth-child(2n+1), #currencies-all > tbody > tr.odd, #assets_wrapper > #assets > tbody > tr.odd, .trending-gainers-table > tbody > tr.odd, .trending-losers-table > tbody > tr.odd, #currencies-volume > tbody > tr.odd, #trending-recently-added > tbody > tr.odd
{
    background-color: #3C3C3C !important;
}
.navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus, .dropdown-menu > li > a:hover, .sitenav--desktop .quick-search-container .quick-search-box, .highcharts-menu > hr, .dropdown-menu .divider, .col-md-12 > hr, .highcharts-container > .highcharts-loading, .col-lg-10 > .row > hr,  .col-xs-12.col-md-8 > hr
{
    background-color: #4C4C4C !important;
}

.alert-warning
{
    background-color: #A18912 !important;
    color: #000 !important;
    border-color: #74510B !important;
}

.reddit-header
{
    background-color: #16599c !important;
}

.ranges li.active
{
    background-color: #08C !important;
}

.col-lg-10 > .bottom-margin-2x
{
    margin-bottom: 5px !important;
}

.container.main-section
{
    margin-bottom: 0px !important;
}

.col-lg-10 > .vertical-spacer
{
    margin-top: 5px !important;
}

#charts > .padding-top-1x
{
    padding-top: 0px !important;
}

.list-unstyled.details-panel-item--links
{
    margin: -30px 0px 0px 0px !important;
}

.list-unstyled.details-panel-item--links > li
{
    margin: -1px 0px 0px 0px !important;
}

.highcharts-label-box.highcharts-tooltip-box, .highcharts-scrollbar-thumb, .highcharts-scrollbar-button
{
    fill: #2C2C2C !important;
}

[class^=" highcharts-button"], .highcharts-scrollbar-track, .highcharts-range-input > .highcharts-label-box
{
    fill: #3C3C3C !important;
}

[class^="highcharts-button highcharts-button-pressed"] > rect
{
    fill: #5C5C5C !important;
}

.highcharts-button > text, .highcharts-tooltip.highcharts-color-undefined > text, .highcharts-label.highcharts-range-input > text > tspan, .highcharts-label.highcharts-range-label > text, .highcharts-scrollbar-arrow
{
    fill: #CCC !important;
}

.highcharts-navigator-outline, .highcharts-axis-line
{
    stroke: #6C6C6C !important;
}

.highcharts-yaxis-grid > .highcharts-grid-line,
.highcharts-label-box.highcharts-tooltip-box, .highcharts-label-box, .highcharts-scrollbar-track, .highcharts-scrollbar-button, .highcharts-scrollbar-thumb, .highcharts-grid.highcharts-xaxis-grid.highcharts-navigator-xaxis > .highcharts-grid-line
{
    stroke: #4C4C4C !important;
}

.highcharts-crosshair.highcharts-crosshair-thin.undefined
{
    stroke: #F00 !important;
}

.highcharts-scrollbar-rifles
{
    stroke: #CCC !important;
}

.btn, .dropdown-menu, .form-control, .nav-tabs > li.active > a, .toolbar-tool-icon, .highcharts-menu, .reddit-entry > .linkflairlabel, #reportrange, .ranges li, .col-xs-12 > .border, .nav-tabs > li > a, .tt-dropdown-menu, #calculator.well, .chosen-container > a, .chosen-search > input[type="text"]
{
    border: 1px solid #4C4C4C !important;
}

.coinmarketcap-currency-widget > div
{
    border: 2px solid #4C4C4C !important;
}

.sorting_asc, .dataTable td.sorting_1, .sorting_desc
{
    border-left: 1px solid #4C4C4C !important;
}

.sorting_asc, .dataTable td.sorting_1,
.coinmarketcap-currency-widget > div:nth-child(1) > div:nth-child(2) > div:nth-child(1),
.coinmarketcap-currency-widget > div:nth-child(1) > div:nth-child(2) > div:nth-child(2), .footer-section, .sorting_desc
{
    border-right: 1px solid #4C4C4C !important;
}

#currencies.dataTable > thead > tr[role="row"], #markets-table > thead > tr, .coinmarketcap-currency-widget > div:nth-child(1) > div:nth-child(3), .coinmarketcap-currency-widget > div:nth-child(1) > div:nth-child(2), #historical-data > div > .table-responsive > table > thead > tr, #exchange-markets > thead > tr, #currencies-all > thead > tr, .table-condensed.border-top, #assets > thead > tr, .table.trending-gainers-table > thead > tr, .trending-losers-table > thead > tr, #currencies-volume > thead > tr, #trending-recently-added > thead > tr
{
    border-top: 1px solid #4C4C4C !important;
}

.floating-header thead tr th::after, .coin-summary-item-header, #at-expanded-menu-host .at-expanded-menu-search-label::before, .nav-tabs, .tt-suggestions > .tt-suggestion
{
    border-bottom: 1px solid #4C4C4C !important;
}

#at-expanded-menu-host .at-expanded-menu-search-label::after
{
    border-bottom: 2px solid #4C4C4C !important;
}

.nav-tabs > li > a:hover, .nav-tabs > li > a:focus, .pagination > li > a
{
    border-color: #4C4C4C !important;
}

body, .text-gray, #squote_price > .text-semi-bold, .details-panel-item--name, .coin-summary-item-header, .global-stats, .btn, .sitenav--desktop .quick-search-container .quick-search-box, .text-center.h2, .form-control-feedback, #at-expanded-menu-host .at-expanded-menu-load, #at-expanded-menu-host .at-expanded-menu-round .at-expanded-menu-button-label, #at-expanded-menu-host .at-expanded-menu-title, #at-expanded-menu-host .at-expanded-menu-page-title, #at-expanded-menu-host .at-expanded-menu-page-url, .highcharts-menu-item, h2, .text-gray-light, textarea.form-control, .reddit-entry > .linkflairlabel, .reddit-header > .reddit-title, #at-expanded-menu-host .at-expanded-menu-close:hover, #at-expanded-menu-host .at-branding-logo.at-expanded-menu-branding .at-branding-addthis, .col-lg-10 > .row.h3 > strong, .form-control.tt-input, .tt-suggestions > .tt-suggestion:hover, .col-sm-8 > .text-left > span > .h2, .col-lg-10 > .row > .col-sm-4 > h1, .col-sm-4.col-xs-6 > h3, .col-xs-12 > h1, .col-xs-12 > select.form-control, .table-responsive > .table tbody tr[id] > td > h3, .col-xs-12.col-md-8 > h3, input#conversion-amount, .chosen-search-input, .chosen-container .chosen-results > li,
.header-1x > .h2 > small, .coinmarketcap-currency-widget > div > div > div > span, .footer-header,
.timeline-Header-title
{
	color: #CCC !important;
}

.btn.btn-success, .reddit-title > a, .list-unstyled li span.label
{
    color: #000 !important;
}

#quote_price > span, .details-list-item-icon, #at-expanded-menu-host .at-expanded-menu-close
{
    color: #888 !important;
}

a
{
	color: #4285F4 !important;
}

.positive_change
{
	color: #009900 !important;
}

.negative_change
{
    color: #E60000 !important;
}

.sitenav, .global-stats
{
    box-shadow: 0 1px 2px 0 #4C4C4C !important;
}

footer
{
    box-shadow: 0 -1px 2px 0 #4C4C4C !important;
}

#currencies.dataTable > tbody > tr > td, #markets-table > tbody > tr > td, #historical-data > div > .table-responsive > table > thead > tr > th, #historical-data > div > .table-responsive > table > tbody > tr > td, #exchange-markets > tbody > tr > td, #currencies-all > tbody > tr > td, .table-responsive > .table-condensed > tbody > tr > td, .table-responsive > .table-condensed > tbody > tr > th, #assets_wrapper > #assets > tbody > tr > td, .trending-gainers-table > tbody > tr > td, .trending-losers-table > tbody > tr > td, #currencies-volume > tbody > tr > td, #trending-recently-added > tbody > tr > td
{
    box-shadow: inset 0 -1px 0 0 #4C4C4C !important;
}

.sitenav-block a img.alt-image.default
{
    display: none !important;
}

.sitenav-block a img.alt-image.inverse
{
    display: block !important;
}

.highcharts-button image.highcharts-button-symbol, .glyphicon-info-sign::before, li > a > .glyphicons::before, .footer-section > .margin-bottom--lv1 > .alt-image.default, .cmc-logo_color_black, .cmc-logo_color_grey
{
    filter: invert(100%) !important;
}

.alert-warning > .glyphicon.glyphicon-info-sign::before
{
    filter: invert(0%) !important;
}

[id^="div-gpt-ad-"]
{
    display: none !important;
}
}
