/* Changing html background behavior */
html {
    background-attachment: fixed !important;
}

/* Some re-layouting for chapter pages */
.cha-page-in {
    padding-left: 18px;
    padding-right: 18px;
}
.chapter-content {
    padding: 0;
    margin: 0;
}

.cha-modal {
    margin-left: 250px;
}

.cha-hr {
    display: none;
}
.cha-tit {
    border-radius: 12px 12px 0 0;
    margin: 46px 0 0 0;
    padding: 24px;
    background: none;
    background-color: rgba(191, 191, 191, 0.125);
    text-align: right;
    /*
    border-bottom: #888 1px dashed;
    */
}
.cha-tit h3 {
    font-size: 80%;
    line-height: 80%;
}
.cha-tit p {
    font-size: 70%;
    line-height: 80%;
}


/* Transparent cursor when hover on text */
.cha-content {
    cursor: var(--Triangle-Pointer);
}

/* 
=======IMPORTANT========
Turning the chapter texts into pages
Now pages are horizontal scroll
Hold the SHIFT key and scroll if you're using mouse that has no horizontal scroll
*/
.cha-words {
line-height:2.5ch;
    position: relative;
    display: block;
    border-radius: 0 0 12px 12px;
    height: 80vh;
    min-height: 480px;
    max-height: 1024px;
    border-bottom: solid transparent 16px;
    padding: 0 0 18px 0;
    margin: 0;
    background-color: #999;
    background: var(--Note-Stripe) 0 0/6.5ch 6.5ch scroll repeat, rgba(191, 191, 191, 0.125);
    overflow-y: hidden;
    -webkit-box-sizing: content-box;
    -moz-box-sizing: content-box;
    box-sizing: content-box;
    /*
    =======IMPORTANT========
    Pages are actually COLUMNS (Shyamalan!!!)
    Apparently blocks with columns treat it's content-box as a new "viewport"
    Setting column-width to 100vw will make the columns act like pages... somehow?
    */
    -ms-column-fill: auto;
    -ms-column-count: auto;
    -ms-column-width: 100vw;
    -ms-column-gap: -moz-calc(1em - 1em);
    -moz-column-fill: auto;
    -moz-column-count: auto;
    -moz-column-width: 100vw;
    -moz-column-span: all;
    -moz-column-gap: -moz-calc(1em - 1em);
    -webkit-column-count: auto;
    -webkit-column-width: 100vw;
    -webkit-column-span: all;
    -webkit-column-gap: -webkit-calc(1em - 1em);
    column-count: auto;
    column-width: 100vw;
    column-span: all;
    column-gap: calc(1em - 1em);
    column-rule: 0;
    column-fill: auto;
    /* Pages snap into position */
    /* Works on Chrome as of update 69! */
    scroll-snap-type: mandatory;
    scroll-snap-type: x mandatory;
    scroll-snap-destination: 0 0 !important;
    scroll-snap-points-x: repeat(100%) !important;
    /* Change overflow-X into "scroll" if scrollbar is "breaking" the column */
    overflow-x: hidden;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

/* Only show the scroll bar when mouse over or touch */
.cha-words:hover,
.cha-words:active {
    overflow-x: scroll;
    overflow-x: overlay;
}

/*
=======IMPORTANT========
Fix for new chapters that haven't been loaded
Sometimes the chapters are loaded inside an existing frame so...
*/
.cha-words .cha-words,
.cha-words .cha-words:hover,
.cha-words .cha-words:active {
    border: 0;
    padding: 0;
    background: none;
    overflow: visible !important;
    width: auto !important;
}
/* "Highlighter", to keep track of your reading */
.cha-words:active p {
    opacity: 1;
}
/* Paragraph you're touching/clicking are highlighted while paragraph next to it are not. */
.cha-words:active p:hover ~ p {
    opacity: .4;
}

/* Paragraph "beautifier" */
.cha-words p+p {
    margin-top: 0;
}
.cha-words p {
    transition: all 0.2s;
    width: 96%;
margin-top:0 important;
    margin: 0 auto;
    text-rendering: geometricPrecision;
    text-indent: 3ch;
    display: block;
    text-align: justify;
    /* Works on chrome 69 */
    scroll-snap-align: center;
    scroll-padding: 0;
    scroll-margin: 0;
}
/* Prevent content inside paragraph from inheriting paragraph indentation */
.cha-words anno sup,
.cha-words p > * {
    text-indent: 0;
}
/* Comments and Rating relayout*/
.j_bottom_comment_area {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    float: left;
    margin: 4px 0;
    width: calc(50% - 2px);
    padding: 12px;
    border-radius: 6px;
    background: var(--Note-Stripe) 0 .85em/5em 4.475em scroll repeat, rgba(127, 127, 191, 0.125);
}
.cha-bts a {}
.pb48 {
    padding-bottom: 4px;
}
.mb48 {
    margin-bottom: 4px;
}

.user-links-wrap {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    ;
    float: right;
    width: calc(50% - 2px);
    margin: 4px 0;
    padding: 0;
    font-size: inherit;
}
.user-links-wrap .user-links {
    padding: 6px;
    background: var(--Note-Stripe) 0 .85em/5em 4.475em scroll repeat, rgba(255, 191, 127, 0.125);
    border-radius: 6px;
}


.user-links-wrap .user-links .user-link {
    width:80%;
}
.user-links-wrap .user-link a {
    display: block;
    margin: 6px auto;
    padding: 6px;
    border-radius: 4px;
    color: #face6e;
    border: #face6e 1px solid;
    transition: all .25s;
}
.user-links-wrap .user-link span {
    color: #face6e;
}

.user-links-wrap .user-link a[title="Vote with Power Stone"] span {

    color: #fa886e;
}
.user-links-wrap .user-link a[title="Vote with Power Stone"] {

    color: #fa886e;
    border-color: #fa886e;
}

.user-links-wrap .user-link .c_s {
    color: inherit;
}

.user-links-wrap .user-link a:hover {
    text-decoration: none;
    -webkit-box-shadow: -1px 1px 3px 21x rgba(0, 0, 0, 0.15);
    -moz-box-shadow: -1px 1px 3px 2px rgba(0, 0, 0, 0.15);
    box-shadow: -1px 1px 3px 2px rgba(0, 0, 0, 0.15);
}




/* A pseudo backround, only when it's visible*/
@media only screen and (min-width: 960px) {
    .cha-page::before {
        content: "RITE";
        color: rgba(116, 112, 109, 1);
        font-family: "Courier New", Courier, monospace;
        font-size: 4vw;
        padding-top: calc(80px + 3vw);
        padding-left: 66px;
        opacity: 1;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        position: fixed;
        display: block;
        pointer-events: none;
        z-index: -2;
    }
    .cha-page::after {
        content: "SWIPE";
        color: rgba(116, 112, 109, 1);
        font-family: "Courier New", Courier, monospace;
        font-size: 3.2vw;
        padding-top: 80px;
        padding-left: 66px;
        opacity: 1;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        position: fixed;
        display: block;
        pointer-events: none;
        background: /* var(--Cat--) calc(42% - 400px) calc(100% - (.5vw / 1.233))/14% auto fixed no-repeat , */
        var(--Note-Stripe) center/100px 100px local repeat, var(--Note-1), var(--Note-2);
        z-index: -2;
    }
}





/* Some tweakings, so things doesn't "break" when things happen */
html {
    width: 100vw;
    overflow-y: auto;
    overflow-y: overlay;
    overflow-x: hidden;
    margin: 0;
    right: 0;
    height: auto;
}

@media only screen and (min-width: 960px) {
    body {
        height: auto;
        width: 100vw;
        overflow: visible;
    }
}
* {
    transition: width 0.125s, height 0.125s, opacity 0.125s, background 0.125s;
}
}
