css:
/* i really want this to be global */

body {
	font-size:11px;
	line-height:1.4em;
}

.col-xs-9 {
	width: 83%;
}

.col-lg-7 {
	width: 63.3%;
}

rm-save-button .btn-save.dropdown-toggle {
	display:none;
}

rm-save-button .dropdown-menu {
	display:inline-block;
	position:relative;
	margin:0;
	margin-top: 1px;
	margin-left: 4px;
	padding:0 0!important;
	box-shadow:none;
	border: 1px solid #ddd;
	min-width:100px;
}

rm-save-button .dropdown-menu>li {
	margin: 4px;
	padding: 0;
}

rm-save-button .dropdown-menu>li>a {
	line-height:1.5;
	padding: 0;
}

.grid1527026389619 .ui-grid-render-container-body .ui-grid-header-canvas {
	height:24px;
}

.grid1527026389619 .ui-grid-render-container-left .ui-grid-header-canvas {
	height:24px;
}

.rm-toolbar {
	padding-bottom:4px;
}

.p-t-sm {
	padding-top:4px!important
}

.navbar-minimalize {
	display:none;
}

.navbar-top-links li a {
	min-height:10px;
	padding:5px;
}

.navbar {
	min-height:10px;
}

.ibox-content {
	padding:4px;
}

[rm-subprojects] {
	width:80px;
	padding-left:4px;
	padding-right:4px;
}

[rm-project-view] {
	padding-right:0;
	padding-left:0;
}

[rm-project-view].col-xs-10 {
	width:calc(100% - 80px);
}

[rm-project-view].col-xs-7 {
	width:calc(80% - 80px);
}

[rm-widget-sidebar].col-xs-3 {
	width:20%;
}

[rm-widget-sidebar="::['Offerte', 'edit']"].col-xs-3 {
	width:17%;
}

.subproject-list>li div.title-container {
width: 100%;
overflow: hidden;
text-overflow: ellipsis;
display: inline-block;
margin-left: 0;
}

.subproject-list>li {
	margin-left:0px!important;
}
