css:
@-moz-document domain("community.blackdesertonline.com") {
.messageList .message {
    background: #333333;
}
body {
    color: rgb(233, 233, 233);
    background: #000 !important;
}
.message .likesSummary {
    color: rgb(111, 111, 111);
    background-color: rgb(30, 30, 30);
    padding: 15px;
    margin-top: 15px;
    border: 1px solid rgb(83, 83, 83);
    border-radius: 0;
}
.messageUserBlock div.avatarHolder {
    background-color: #333;
}
.messageUserBlock {
    background-color: #333;
}
.messageUserBlock h3.userText {
    background-color: #333;
}
a:link,
a:visited {
    color: rgb(198, 55, 52);
    text-decoration: none;
}
a[href]:hover {
    color: rgb(255, 154, 152);
    text-decoration: none;
}
.PageNav a {
    color: rgb(255, 255, 255) !important;
    text-decoration: none;
    background-color: rgb(41, 1, 0);
    border: 1px solid rgb(41, 1, 0);
}
.PageNav a:hover,
.PageNav a:focus {
    color: rgb(255, 255, 255);
    text-decoration: none !important;
    background-color: rgb(113, 5, 3);
    border-color: rgb(133, 5, 2);
}
.messageUserBlock a.username {
    color: rgb(198, 55, 52);
}
#pageDescription {
    color: rgb(87, 87, 87);
}
.quickReply {
    background-color: #333;
    border: 1px solid rgb(72, 72, 72);
}
.quickReply .messageUserBlock div.avatarHolder {
    background-color: #0e0e0e;
}
.redactor_box .placeholder span {
    color: rgb(41, 41, 41);
}
.nodeList .categoryStrip {
    color: #FFFFFF;
    background-color: #333;
}
.nodeList .categoryStrip .nodeTitle a {
    color: rgb(255, 255, 255);
}
.categoryStrip__collapseToggle {
    color: rgb(255, 255, 255);
}
.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo {
    background-color: rgb(18, 18, 18);
    border-color: rgb(60, 60, 60);
    border-top-color: rgb(60, 60, 60);
    border-bottom-color: rgb(60, 60, 60);
    border-radius: 0;
}
.node.level_2 .nodeInfo {
    border-top: 1px solid rgb(60, 60, 60);
}
.node .subForumList {
    line-height: 20px;
    padding: 10px 20px;
    background: #202020 none repeat scroll 0% 0%;
    border-top-left-radius: 20px;
    border-bottom-left-radius: 20px;
    box-shadow: 0 -2px 0 rgba(0, 0, 0, 0.02);
    margin-top: 0;
}
.uix_nodeTitle_status {
    color: rgb(255, 255, 255);
    background: rgba(117, 117, 117, 0.2);
}
.subForumList .nodeTitle a {
    color: rgb(206, 33, 33) !important;
}
.mainContainer .mainContent,
.mainContainer_noSidebar {
    background-color: #333;
}
.forum_view #forums.nodeList {
    border-top: 1px solid rgb(57, 57, 57);
}
.forum_list .nodeList .nodeList,
.forum_view .nodeList,
.category_view .nodeList,
.watch_forums .nodeList {
    border: 1px solid rgb(60, 60, 60);
    border-top-width: 1px;
    border-right-width: 1px;
    border-bottom-width: 1px;
    border-left-width: 1px;
    border-top-style: solid;
    border-top-color: rgb(60, 60, 60);
}
.threadListSeparator {
    background-color: rgb(133, 5, 2);
    color: rgb(231, 231, 231);
    background-color: rgb(77, 5, 4);
    border-color: rgb(226, 226, 226);
}
.discussionList .discussionListItem.sticky {
    background-color: #141414;
}
.discussionListItem {
    border-style: solid;
    border-bottom-style: solid;
    border-color: rgb(47, 47, 47);
    border-bottom-color: rgb(47, 47, 47);
    border-top-width: 0;
    border-right-width: 1px;
    border-bottom: 1px solid rgb(47, 47, 47);
}
.discussionListItems .unread .title a {
    color: rgb(198, 55, 52);
}
.discussionListItems .unread .lastPostInfo .username {
    color: rgb(198, 55, 52);
}
.discussionListItem .title a {
    color: #474747;
}
.discussionListItem {
    background-color: #141414;
    border-bottom: 1px solid rgb(47, 47, 47);
}
.discussionListItem:nth-child(2n) {
    background-color: #1a1a1a;
}
.sectionFooter {
    color: rgb(194, 194, 194);
    background-color: rgb(51, 51, 51);
}
#DiscussionListOptionsHandle a {
    color: rgb(187, 187, 187);
    background-color: #850502;
    border: 1px solid rgb(133, 5, 2);
    border-top-width: 1px;
    border-top-style: solid;
    border-top-color: rgb(133, 5, 2);
    border-top: 1px none black;
}
.secondaryContent {
    background-color: #333;
    border-bottom: 1px solid rgb(226, 226, 226);
}
.profilePage .tabs.mainTabs {
    background: #121212;
}
.primaryContent {
    background-color: #333;
    border-bottom: 1px solid rgb(226, 226, 226);
}
.textCtrl {
    color: #b9b9b9;
    background-color: #181818;
    border: 1px solid rgb(89, 89, 89);
}
.profilePage .mast .section.infoBlock {
    color: #FFFFFF;
    background-color: rgb(18, 18, 18);
}
.tabs li:hover a,
.tabs.noLinks li:hover {
    color: rgb(174, 174, 174);
    text-decoration: none;
    background-color: transparent;
}
.tabs li a,
.tabs.noLinks li {
    font-weight: normal;
    color: rgb(215, 215, 215);
}
.tabs li.active a,
.tabs.noLinks li.active {
    font-weight: normal;
    color: rgb(198, 55, 52);
    padding-bottom: 1px;
    border-bottom: 1px solid rgb(198, 55, 52);
    box-shadow: 0 1px 0 rgb(198, 55, 52);
}
.primaryContent {
    background-color: #333;
    border-bottom: 1px solid rgb(83, 83, 83);
}
.tabs {
    border-bottom: 2px solid rgb(105, 105, 105);
}
.eventList li,
html .searchResult {
    border-bottom: 1px solid rgb(83, 83, 83);
}
#pageDescription {
    color: rgb(168, 168, 168);
}
.Menu .primaryContent {
    background-color: rgb(20, 20, 20);
}
.Menu .secondaryContent {
    background-color: rgb(20, 20, 20);
}
.Menu {
    background-color: #FFFFFF;
    font-size: 15px;
    background-color: #000;
    border: 1px solid rgb(133, 5, 2);
    border-top-width: 1px;
    border-top-style: solid;
    border-top-color: rgb(133, 5, 2);
    border-top: 2px solid rgb(133, 5, 2);
    overflow: hidden;
    box-shadow: 0px 3px 0 rgba(0, 0, 0, 0.2);
    min-width: 200px;
    *width: 200px;
    position: absolute;
    z-index: 7500;
    display: none;
}
.secondaryContent {
    background-color: #333;
    border-bottom: 1px solid rgb(133, 5, 2);
}
.bbCodeQuote .attribution {
    color: rgb(144, 144, 144);
    background-color: #1e1e1e;
    border-bottom: 1px solid rgb(138, 138, 138);
    border-left: 2px solid #1e1e1e;
}
.bbCodeQuote .quoteContainer {
    background-color: rgb(92, 92, 92);
    border-left: 2px solid rgb(133, 5, 2);
}
.bbCodeQuote {
    border-color: rgb(117, 117, 117);
}
.bbCodeQuote .quoteContainer .quoteExpand {
    color: rgb(212, 212, 212);
    background: -webkit-linear-gradient(top, rgba(216, 226, 231, 0) 0%, rgb(216, 226, 231) 80%);
    background: -moz-linear-gradient(top, rgba(216, 226, 231, 0) 0%, rgb(216, 226, 231) 80%);
    background: -o-linear-gradient(top, rgba(216, 226, 231, 0) 0%, rgb(216, 226, 231) 80%);
    background: linear-gradient(to bottom, rgba(216, 226, 231, 0) 0%, rgb(48, 48, 48) 80%);
}
.Menu .sectionFooter {
    background-color: rgb(20, 20, 20);
}
.larger.textHeading,
.xenForm .sectionHeader,
.larger.textHeading a,
.xenForm .sectionHeader a {
    color: rgb(177, 177, 177);
}
.textHeading,
.xenForm .sectionHeader {
    color: rgb(85, 92, 100);
    border-bottom: 1px solid rgb(56, 56, 56);
}
.xenPreviewTooltip {
    background-color: #141414;
    border: 1px solid rgb(47, 47, 47);
    border-radius: 0;
    position: relative;
    width: 400px;
    display: none;
    z-index: 15000;
    cursor: default;
}
.xenPreviewTooltip .arrow {
    border-top: 15px solid rgb(47, 47, 47);
    border-right: 15px solid transparent;
    border-bottom: 1px none black;
    border-left: 15px solid transparent;
}
.xenPreviewTooltip .arrow span {
    border-top: 15px solid #141414;
    border-right: 15px solid transparent;
    border-bottom: 1px none black;
    border-left: 15px solid transparent;
    position: absolute;
    top: -17px;
    left: -15px;
}
.ugc a:link,
.ugc a:visited {
    color: rgb(231, 83, 79);
}
.gridSection {
    background-color: #333;
}
.mediaContainer {
    border: 1px solid rgb(87, 87, 87);
}
.mediaContainer .titleSection {
    padding: 8px;
    background: #232323;
}
.xengallerySideBarContainer .xengallerySideBar .section .secondaryContent {
    background-color: rgb(23, 23, 23);
    border-style: none;
}
.node .unread .nodeText .nodeTitle {
    color: rgb(181, 21, 23);
}
.sidebar .visitorPanel .username {
    color: rgb(181, 21, 23);
}
.sectionMain {
    background-color: #0e0e0e;
    border: 1px solid rgb(101, 101, 101);
}
.xenForm fieldset, .xenForm .formGroup, .dataTable tr.dataRow td {
    border-color: rgb(35, 35, 35);
}
.navigationSideBar > ul {
    border: solid 1px rgb(51, 51, 51);
        border-top-width: 1px;
        border-right-width: 1px;
        border-bottom-width: 1px;
        border-left-width: 1px;
    border-width: 0 0 1px 1px;
    margin-bottom: 10px;
    background-color: #333;
}
.navigationSideBar a {
    color: rgb(255, 255, 255);
    text-decoration: none !important;
}
.navigationSideBar a:hover {
    background: rgb(14, 14, 14);
    color: rgb(187, 187, 187);
    text-decoration: none !important;
}
.secondaryContent {
    background-color: #0e0e0e;
    border-bottom: 1px solid rgb(255, 255, 255);
}
.navigationSideBar .secondaryContent {
    color: rgb(255, 255, 255);
}
.navPopup .listItem {
    border-bottom: 1px solid #535353;
}
.navPopup .PopupItemLinkActive:hover {
    background-color: rgb(29, 29, 29);
}
.secondaryContent {
    border-bottom: 1px solid #333333;
}
.blockLinksList {
    font-size: 12px;
    letter-spacing: .007em;
    font-weight: normal !important;
    line-height: 13px;
}
.navPopup a:hover, .navPopup .listItemText a:hover {
    text-decoration: none;
}
.navTabs .navTab.selected .tabLinks a {
    padding: 15px 10px;
}
.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    text-decoration: none;
    color: #ff4040;
}
.pollBlock .pollOption label:hover {
    background: rgb(51, 51, 51);
}
.pollBlock .pollOptions, .pollBlock .pollResults {
    border-top: 1px solid rgb(51, 51, 51);
    border-bottom: 1px solid rgb(51, 51, 51);
}
.pollBlock .question .questionText {
    color: rgb(255, 255, 255);
}
.pollResult .bar {
    background: rgb(133, 5, 2);
}
.pollResult .barContainer {
    border-color: rgb(133, 5, 2);
}
.hasJs .bbCodeSpoilerText {
    background-color: #202020;
    padding: 30px;
    border: 1px solid rgb(77, 77, 77);
    border-radius: 5px;
}
}
