css:
@-moz-document domain("tugaflix.com") {
div#movie-info {
    background: #000000a6;
    padding: 25px;
    border: 4px solid #fff;
    margin-bottom: 10px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    transition: border .15s ease-in-out;
    -moz-transition: border .15s ease-in-out;
    -webkit-transition: border .15s ease-in-out;
}

.main-content {
    background-color: transparent !important;
}

body {
    background-image: url(https://i.imgur.com/FNK4Oqh.jpg) !important;
    background-repeat: no-repeat !important;
    background-position: right top !important;
    background-attachment: fixed !important;
}

div#movie-sub-info {
    background: #000000a6;
    padding-bottom: 15px;
    border: 4px solid #fff;
    margin-bottom: 10px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    transition: border .15s ease-in-out;
    -moz-transition: border .15s ease-in-out;
    -webkit-transition: border .15s ease-in-out;
    margin-top: 30px;
}

#movie-sub-info p {
    color: #ffffff!important;
    line-height: 24px;
}

.browse-content {
    background-color: #1d1d1d00!important;
}

.browse-movie-bottom {
    margin-top: 5px;
    background: #000000a6;
    border: 4px solid #fff;
    margin-bottom: 10px;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    transition: border .15s ease-in-out;
    -moz-transition: border .15s ease-in-out;
    -webkit-transition: border .15s ease-in-out;
}

.browse-movie-year {
    color: #ffffff94 !important;
}
.nav-bar {
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px;
}
ul.tsc_homemoreA0 li a, .homemore-bordered {
    border: 1px solid #d4d1d1 !important;
    background: #000000c4 !important;
}

.alert-info {
    background: #191818 !important;
    background-image: none !important;
    width: 700px;
    margin-left: 265px;
    border: none !important;
    box-shadow: none !important;
}

.alert {
    color: #fff !important;
}
}
