@-moz-document url-prefix("https://www.ecosia.org"), url-prefix("https://info.ecosia.org") {

:root {
    --colorAccentPrimary: #5ba9b2;
    --colorAccentSecondary: #a9b64f;
    --colorAccentHover: #915bb2;
    --colorText: #C0C0C0;
    /* --colorTextTitle: #9b9b9b; */
    --colorText: #C0C0C0;
    --colorTextSub: #A0A0A0;
    /* --colorBgDarkest: #0f0f0f; */
    --colorBtn: #282E33;
    --colorBtnDisabled: #282E33;
    --colorShadow: #282E33;
    --colorBgDark: #1D2327;
    
    /* --colorShadow2: #444444; */
    
    /* --colorTextLink: #848484; */
    --colorBgDarker: #1D2327;
}
html {
    font-size: 1em;
    line-height: 1.4;
    color: var(--colorText)
}

::-moz-selection {
    text-shadow: none;
    background: var(--colorAccentPrimary);
    color: var(--colorBgDark);

}

::selection {
    text-shadow: none;
    background: var(--colorAccentPrimary);
    color: var(--colorBgDark);
}

audio,canvas,iframe,img,svg,video {
    vertical-align: middle
}

textarea {
    resize: vertical
}

/*! normalize.css v3.0.1 | MIT License | git.io/normalize */
html {
