css:
a {
    color: #ad81ff;
}
.CodeMirror.cm-s-graphiql {
    background: #282828;
}

.graphiql-container .topBar,
.graphiql-container .docExplorerShow,
.graphiql-container .historyPaneWrap,
.graphiql-container .doc-explorer-title-bar,
.graphiql-container .history-contents
{
    background: #323438;
    color: #ffffff;
    border: none;
}
.graphiql-container .doc-explorer-back 
{
  color: #ffffff;
}
.graphiql-container .doc-explorer-back:before,
.graphiql-container .docExplorerShow:before
{
    border-left: 2px solid #fff;
    border-top: 2px solid #fff;
}
.graphiql-container .doc-explorer-contents,
.graphiql-container .history-contents {
    background-color: #282828;
    border-top: none;
    color: #e0e0e0;
}
.graphiql-container .history-contents p {
    font-size: 12px;
    border-bottom: none;
}
.graphiql-container .history-contents p:hover {
    background: #5b5b5b;
}

.graphiql-container .docExplorerResizer {
    background: rgba(255, 255, 255, 0.06);
}
.graphiql-container .keyword {
    color: #ffffff;
}
.cm-property {
    color: #fff;
}
.cm-attribute {
    color: #efdc0c;
}
.cm-keyword {
    color: #da00ff;
}
.cm-atom {
    color: #00d2ff;
}
.cm-punctuation {
    color: #b3b3b3;
}
.cm-variable {
    color: #c678dd;
}
.cm-number {
    color: #0ab7ca;
}
.cm-string {
    color: #8aa2ff;
}
.cm-def {
    color: #33d741;
}
.cm-ws {
    color: #b3b3b3
}
.graphiql-container .field-name {
    color: #ffffff;
}
.graphiql-container .type-name
{
    color: #00d2ff;
}
.graphiql-container .arg-name {
    color: #cb60ff;
}
.graphiql-container .doc-category-item {
    margin: 12px 0;
    color: #d6d6d6;
}
.graphiql-container .variable-editor-title {
    background: #383838;
    border: none;
}
.CodeMirror-matchingbracket {
    color: #00c2ff !important;
    text-decoration: none !important;
    font-style: italic;
}
.CodeMirror-gutters {
    background-color: #333333;
    border: none;
}
.CodeMirror-search-label {
    color: #ffffff;
}
.CodeMirror-search-field {
    color: #ff9d00 !important;
}
.graphiql-container .result-window .CodeMirror-gutters {
    background-color: #333333;
    border: none;
}
.graphiql-container .resultWrap {
    border: none;
}
.CodeMirror-cursor {
    border-left: 2px solid #fff !important;
}
/* Selection Highlight */

.CodeMirror-selected {
    background: rgba(177, 177, 177, 0.3);
}
.CodeMirror-focused .CodeMirror-selected {
    background: rgba(177, 177, 177, 0.2);
}

.graphiql-container ::-webkit-scrollbar {
    width: 10px;
    background: #4a4a4a; /* make scrollbar transparent */
}

.graphiql-container ::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
}
.graphiql-container ::-webkit-scrollbar-thumb {
  background-color: darkgrey;
  outline: 1px solid slategrey;
}

/* insertionFade */

/* i really want this to be global */

@-webkit-keyframes insertionFade {
  from, to {
    background: rgba(255, 255, 255, 0);
    border-color: rgba(255, 255, 255, 0);
  }

  15%, 85% {
    background: #9898989c;
    border-color: #9898989c;
  }
}

@keyframes insertionFade {
  from, to {
    background: rgba(255, 255, 255, 0);
    border-color: rgba(255, 255, 255, 0);
  }

  15%, 85% {
    background: #9898989c;
    border-color: #9898989c;
  }
}

/* Search */
.graphiql-container .search-box input {
    background: none;
    color: white
}

.graphiql-container .search-box .search-box-clear {
    background: none;
}
