css:
@-moz-document url-prefix("https://docs.google.com/document/d/") {
#docs-chrome{background: #333 none repeat scroll 0% 0% !important; color: #DDD !important;}
  
  #gb#gb a.gb_b, /* Account email in top right corner */
  .docs-presence-plus-widget-status /* x total viewers text */
  {color: #DDD !important;}
  
  /* Document title box, TOP HEADER. CHANGE THIS + SHARE BUTTON AREA FOR FULL HEADER. */
  .docs-title-input{
    background-color: #333333 !important;

    font-style: normal !important;
  }
  .docs-title-input:focus{
    color: white !important;

  }

goog-inline-block jfk-button jfk-button-standard docs-titlebar-button{
background-color: white}

.docs-material-appbar #docs-header .docs-titlebar-buttons{
background-color: #333333
}
  .docs-title-untitled{display: none !important;}
  
  /* Fix for menu bar text when hover */
  .goog-control-hover{color: black !important;}
  
  /* Main toolbar */
  #docs-toolbar-wrapper{
    border-top: 1px solid #151516 !important;
    border-bottom: 1px solid #040404 !important;
    background-image: linear-gradient(#333333, #313131) !important;
    box-shadow: inset 0 1px rgba(255,255,255,0.15) !important;
  }
  /* Toolbar's vertical dividers */
  .goog-toolbar-separator.goog-inline-block{border-left: 1px solid #222 !important;}
  
.navigation-widget-hat-title {
color: white}

  /* A very simple/lazy fix to get toolbar item colors to look good on a dark background, INVERTS THE COLORS */
  .goog-toolbar-button,
  .goog-toolbar-toggle-button,
  .goog-toolbar-menu-button,
  .goog-toolbar-combo-button,
  .docs-icon-folder-solid{
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
}
  /* Share buttom, User Icon. ADDS THE FIX FOR WHITE BLOB BEHIND SHARE */

.docs-title-input,
.docs-titlebar-buttons {
    background-color: #333333 !important;
    border-bottom: 1px solid #333333 !important;
    border-radius: 2px !important;
    border-top-color: #333333 !important;
    box-shadow: 0 0px rgba(255, 255, 255, 0.15) !important;
    font-style: normal !important;
}
  /* Highlighted selection and FIXES TEXT COLOR INVERSER */

.goog-menuitem.goog-menuitem-highlight,
.goog-menu.goog-menu-vertical.goog-menu-noicon.goog-menu-noaccel.goog-menuitem-highlight,
.goog-menuitem.goog-option.goog-option-selected.goog-menuitem-highlight,
.goog-menuitem.goog-option.goog-option-selected.goog-menuitem-selected,
.goog-menuitem.goog-option.goog-option-selected.goog-menuitem-hoover, 
.goog-color-menu-button-indicator,
.docs-icon-img-container.docs-icon-img.docs-icon-text-color,
.docs-icon-img-container.docs-icon-img.docs-icon-text-bgcolor{
    -webkit-filter: invert(1);
    filter: invert(1);
}
  /* Fixes for suggestion mode when editing own doc, viewing mode when editing others' docs */
  #docs-toolbar-mode-switcher, #docs-access-level-indicator{background-color: transparent !important; color: black !important;}
  .docs-icon-mode-review-white, .docs-icon-mode-view-white{filter: invert(1) !important;}
  
  /* Background behind the document, BACKGROUND COLOR CHANGE HERE*/
  #docs-editor{background: #404143 none repeat scroll 0% 0% !important;}

/* BEGIN Paper Background CHANGE SEPIA COLOR HERE*/

  .kix-page,
  .kix-page-compact,
  .kix-page-content-wrapper,
  .kix-paginateddocumentplugin,
  .kix-paginateddocumentplugin-compact-mode
  .docs-ui-unprintable {
    background-color: #ffffff !important;
  }

  .kix-paginateddocumentplugin-background {
    display: none;
  }

/* END Paper Background */
  
  /* Document drop-shadow removal */
/* .kix-page-paginated{box-shadow: none !important;} */
/* Kyle Li Says Hi! :D */
}
