*/


.main-container::before {
    background: #1d2024;
}

.c-exercise-item__column-container, .c-exercise-container__content,
.c-exercise-modal-main, .c-zoom-rectangle, .c-exercise-container,
.c-question-container, .c-exercise-container,
.page-content, .overview{
    background: #1d2024;
}
[class*=c-question-type-1],
[class*=c-question-type-5]{
	background: #2d3034;
}
.exercises-container .exam-part[disabled] {
	background: #1d2024 !important;
}

.exercises-container .links-downloads-container{
	border-bottom: #d7d7d7 1px solid;
    border-bottom-left-radius: 2px;
	border-bottom-right-radius: 2px;
}
.c-exercise-result{
	background: #2d3034;
    border-bottom: 2px solid #00ADFF;
    padding-left: 10%;
}
.c-exercise-result__buttons{
	margin-left: -10%;
}
.c-exercise-container__content {
	max-width: 100vw;
} 
.c-exercise-item {
	max-width: 1240px;
    margin-left: auto;
    margin-right: auto;
    width: 100%;    
}
.c-exercise-container {    
    color: #ffffff;
}
.c-exercise-input input {
    color: #001d49;
}
body, html {
    background: #1d2024;
    color: white;
}

.c-exercise-top-bar, .c-exercise-top-bar__button {
    background-color: #00ADFF;
}

.c-question-button{
    background-color: #397cc6;
    color: white;
}

.c-exercise-item__column.-wide-column .c-question{
    padding-top: 10px;
    padding-bottom: 10px;
    background-color: #2d3034;
}
.c-mc-container{
    background-color: #2d3034;
    border-radius: 23px;
}

/*opdrachtlijst header*/
.c-widget-header {
    background-color: #505050;
    color: #00ADFF;
}
/*opdrachtenlijst*/

.c-widget-box__list-item,
.c-widget-box__list-item a,
.c-exercise-table__cell.-lock:not(.-transparent){
    background-color: #2d3034;
    color: #00ADFF;
}

/*gemaakte opdrachten lijst*/
.c-widget-box__list-item.-exercise-made,
.exercises-container .last-made>div
{
    background-color:  #2d3034;
    color: #FFFFFF;
}

/*highlighted text in exercise*/
.e-content-highlight-yellow{border-radius: 2px;}

/*exercise input field, non focused*/
.c-exercise-input__input.ng-empty:not(:focus){
    background-color: #606060;
}

/*nav bar*/
.breadcrumbs, .c-breadcrumbs{
    background-color: #292d33;
}
.breadcrumb>li, .breadcrumb>li.active {
    color: #FFFFFF !important;
}
.breadcrumb>li>a {
    color: #00ADFF
}
.c-breadcrumb-item {
	color: #FFFFFF;
}

/*side bar*/

.no-skin .sidebar {
    background-color: #1d2024;
    transition-duration: 300ms;
}

.no-skin .nav-list>li>a,
.no-skin .sidebar-toggle{
    background-color: #1d2024;
}
.no-skin .nav-list>li>a {
    color: #FFFFFF;
    transition-duration: 300ms;
}

.no-skin .nav-list>li.active>a {
    color: #FFFFFF;
}

/*studiemeter blauw*/
.studiemeter-blue {
	color: #00ADFF !important;
}

/*opdrachten lijst*/
.dd-handle {
    background-color: #292d33;
    color: #00ADFF;
    transition-duration: 300ms;
}
.dd-handle a, .dd-handle .sm-exercise-state-label {
    color: #00ADFF;
    transition-duration: 300ms;
}
.dd-handle:hover{
	background-color:#2e333a;
}
.dd-handle:hover a,
.dd-handle:hover .sm-exercise-state-label{
    color: #FFFFFF;
}

/* finished circle color */
.c-exercises-list-icon.-required.-finished {
	color: #5db75f;
}

/* exercise locked */
.exercises-container [disabled] svg {
	color: #e54949;
}

/* login window */
.login-layout {
	background: #1d2024;
}
.login-box, .widget-main {
    background: #383838;
    color: white;
}
#login-box .widget-body .widget-main {
    background: #383838;
    color: white;
}

/*OTHER*/
.nav-dots {
	background: white;
}

.exercises-container .slider-wrapper{
	border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}

.exercises-container img,
.exercises-container .slide{
	border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}

.studiemeter-contact-servicedesk {
	color: #000000;
}

.own-user-management-container,
.modal .page-footer,
.modal .modal-header{
	background-color:#2e333a;
}

.c-exercise-text {
	background-color: #2e333a;
    border-radius: 23px;
}

.btn.btn-xs {
    color: white !important;
    opacity: 1;
}

.c-question-controls__preferences {
	background-color: #2d3034 !important;
}

.c-button {
	border-radius: 20px;
    transition-duration: 350ms;
}
.c-button:enabled:hover:not(.-disabled){
	border-radius: 20px;
    border: #00ff00 solid 1px;
}

.modal-body, .modal-footer{ 
	background-color: #2d3034 !important;
}

.c-question-panel--primary {
	border-radius: 23px;
}


