css:
@-moz-document domain("lichess.org")
{
  piece {
    background-size: 92%;
    background-repeat: no-repeat;
    background-position: center;
  }
  piece.pawn.black
  {
    background-image: url("https://discordapp.com/assets/ced0c08553c2ade6cbeee29a40f4ac8c.svg")!important;
  }

  piece.knight.black
  {
    background-image: url("https://discordapp.com/assets/fa4f3767ca6e775a52e2074c7cf593d7.svg")!important;
  }

  piece.bishop.black
  {
    background-image: url("https://discordapp.com/assets/02cc3a7f831134d0d75eb8d700ec8b64.svg")!important;
  }

  piece.rook.black
  {
    background-image: url("https://discordapp.com/assets/0d9ad62c34798452bbc96cd531f91258.svg")!important;
  }

  piece.queen.black
  {
    background-image: url("https://discordapp.com/assets/08c483393c3109ec7e69f7e3c47336a0.svg")!important;
  }

  piece.king.black
  {
    background-image: url("https://i.imgur.com/qQui6sx.png")!important;
    background-size: 75% !important;
  }

  piece.pawn.white
  {
    background-image: url("https://discordapp.com/assets/ac68e5f170ef78d8bb8b7fae73d68a53.svg")!important;
  }

  piece.knight.white
  {
    background-image: url("https://discordapp.com/assets/bffd9a1668dbc714adda404b93489286.svg")!important;
  }

  piece.bishop.white
  {
    background-image: url("https://discordapp.com/assets/d182716edc150e63958601807ae10a7a.svg")!important;
  }

  piece.rook.white
  {
    background-image: url("https://discordapp.com/assets/cf609ce2c1dd8d2ac6ec799d6bcc19ab.svg")!important;
  }

  piece.queen.white
  {
    background-image: url("https://discordapp.com/assets/d59493c473541bf5d036c56e996b152b.svg")!important;
  }

  piece.king.white
  {
    background-image: url("https://cdn.discordapp.com/emojis/408690409313665034.png?v=1")!important;
  }

  .cg-square.last-move
  {
    background-color: rgba(255, 255, 0, 0.141) !important;
  }

.is2d .cg-board
  {
    background-image: url("https://i.imgur.com/IQbQgwe.png")  !important;
  }
}
