css:
@-moz-document domain('somtoday.nl') {
    /* The yellow ribbon tag that contains icons such as messages or the back/forward buttons in the rooster tab*/
    .yellow.ribbon {
        background:url('https://wouter.pw/stylish/som_dark_turqoise.png') no-repeat left 0;
        position:relative;
        z-index:2
    }
    .yellow.ribbon p {
        background:url('https://wouter.pw/stylish/som_dark_turqoise.png') no-repeat right 0
    }
    #main-menu-bottom {
        background-color:#00CED1;
    }
    #main-menu a.active, #main-menu a.active:hover {
        background:#00CED1;
    }
    #main-menu a:hover {
        background:#00CED1;
    }
    #dialog {
        background:#00CED1;
    }
    .IconFont.b-alert {
        color:#00CED1;
    }
    .today {
        border-bottom:solid 3px #00CED1;
    }
    .stpanel .form--input i {
        color:#00CED1
    }
    .stpanel .form--select i:after {
        border-top:.3rem solid #00CED1
    }
    .stpanel .form--select i:before {
        border-bottom:.3rem solid #00CED1
    }
    .stpanel .form--input.state-disabled:hover i {
        color:#00CED1 !important
    }
    .icon-toets, .icon-huiswerk, .icon-studiewijzer, .icon-leermiddelen, .icon-geen-van-beide, .icon-maatregel {
        background:#00CED1;
    }
    .stpanel section form {
        background:#00CED1;
    }
    .stpanel--meldingen {
        background:#00CED1;
    }
    .stpanel--meldingenpage {
        background-color:#00CED1
    }
    .stpanel--screensaver {
        background:#00CED1 !important
    }
    .stpanel--screensaver .form--input i {
        color:#00CED1
    }
    .stpanel--screensaver .form--select i:after {
        border-top:.3rem solid #00CED1
    }
    .stpanel--screensaver .form--select i:before {
        border-bottom:.3rem solid #00CED1
    }
    .stpanel--screensaver .form--input.state-disabled:hover i {
        color:#00CED1 !important
    }
    .stpanel--screensaver section form {
        background:#00CED1;
    }
}
