@-moz-document url-prefix("https://keep.google.com") {
/*--------------------------------------------------------------
>>>	BODY
--------------------------------------------------------------*/
body {
    background: #111 !important;
    color: #ccc !important;
    caret-color: #fff !important;
}

input {
    color: #999;
}

a[href] {
    color: #FFCD35 !important;
}


/*--------------------------------------------------------------
>>>	SCROLLBAR CHROME
--------------------------------------------------------------*/
*::-webkit-scrollbar {
    width: 6px !important;
    height: 6px !important;
}
*::-webkit-scrollbar-track,
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track-piece {
    border: 0 !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
    outline: none !important;
}
*::-webkit-scrollbar-thumb {
    background: #555 !important;
}
*::-webkit-scrollbar-track-piece {
    background: none !important;
}



/*--------------------------------------------------------------
>>>	TOPBAR
--------------------------------------------------------------*/
/* Top bar color */
/* .gb_Wa { Try role to avoid constant class name changes*/
header[role="banner"] {
    border-bottom: None !important;
}
/* .gb_Wa .gb_Xd { Try using the style selector to avoid constant class name changes */
header[role="banner"] > div:nth-child(2){
    background-color: #FFBA3C !important;
}


/* Top Bar Icons */
header[role="banner"] > div:nth-child(2) [role="button"] svg,
header[role="banner"] > div:nth-child(2) [type="button"] svg{
    color: rgba(0,0,0,0.6);
}

/* HIDE GOOGLE */
/* .gb_Xd .gb_lc .gb_Ee { Try aria-labels to avoid constant class name changes*/
a[aria-label="Keep"] {
    visibility: hidden;
    pointer-events: none;
}


/* Top bar search box */
header[role="banner"] > div:nth-child(2) form[method="get"]{
    background-color: rgba(0,0,0,.1);
}
header[role="banner"] > div:nth-child(2) form[method="get"].gb_ne{
    background-color: #f1f3f4;
}

/* Top bar search cursor */
/* .gb_Nf input { Try aria-labels to avoid constant class name changes*/ 
input[aria-label="Search"] {
    caret-color: black;
}

