css:
@-moz-document domain("v3rmillion.net")
{
  #logo img
  {
	  content: url("https://imgur.com/czYPbyJ.png") !important;
  }
  .logo
  {
	  content: url("https://imgur.com/czYPbyJ.png") !important;
  }
  #bridge
  {
	  background: #808080 !important;
	  border-bottom: 5px solid #2E3E49 !important; 
  }
  #panel .links a, .panel_buttons
  {
	  background: #808080 !important;
  }
  .pm_new, .pm_count, .alert_new
  {
	  background: #252526 !important;
  }
  ul.menu li ul, ul.menu li a.active
  {
	  border: 1px #808080 !important;
	  background: #2E3E49 !important;
  }
  .buttons
  {
	  border: 2px !important;
  }
  .buttons, button, a.button.new_thread_button, a.button, a.button:active, input.button
  {
	  border: #808080 !important;
	  background: #252526 !important;
  }
  a.button.new_thread_button:hover, a.button:hover, input.button:hover, button:hover
  {
	  border: #2E3E49 !important;
	  background: #808080 !important;
  }
  .trow1
  {
	  border-top: 2px #808080 !important;
	  border-bottom: 2px #808080 !important;
  }
  .trow2
  {
	  border-top: 2px #808080 !important;
	  border-bottom: 2px #808080 !important;
  }
  .thead, .thead input.textbox, .thead select
  {
	  border: #2E3E49 !important;
	  background: #808080 !important;
  }
  .pagination_current
  {
	  background: #2E3E49 !important;
  }
  .pagination_page, .pagination_last, .pagination_next, .pagination_previous, .go_page
  {
	  border: 2px #2E3E49 !important;
	  background: #808080 !important;
  }
  .popup_menu, .popup_item:hover
  {
	  border: 2px #808080 !important;
	  color: #2E3E49 !important;
  }
  a.postbit_email, 
  a.postbit_website,
  a.postbit_pm, 
  a.postbit_find,
  a.postbit_reply_pm, 
  a.postbit_forward_pm,
  a.postbit_delete_pm,
  a.postbit_qdelete,     
  a.postbit_edit, 
  a.postbit_quote, 
  a.postbit_multiquote, 
  a.postbit_multiquote_on, 
  a.postbit_report
  {
	  border: 2px #808080 !important;
	  background: #2E3E49 !important;
  }
  .red_alert
  {
	  background: #2E3E49 !important;
  }
  .red_alert:hover
  {
	  background: #808080 !important;
  }
  input.textbox:focus
  {
	  border: 2px #808080 !important;
	  background: #2E3E49 !important;
	  box-shadow: 8px #808080;
  }
  table
  {
	  font-size: 16px !important;
	  color: #808080 !important;
  }
  .reputation_positive
  {
	  color: #3ee07a !important;
  }
  .reputation_neutral
  {
	  color: #2d91c6 !important;
  }
  .reputation_negative
  {
	  color: #f42e87 !important;
  }
  #container
  {
	  background: #404042 !important;
  }
  #content, .content
  {
	  background: #252526 !important;
  }
   .trow1.forumdisplay_sticky a, .trow2.forumdisplay_sticky a, .trow1.forumdisplay_regular a, .trow2.forumdisplay_regular a
  {
	  color: #efeded !important;
  }
  .trow1.forumdisplay_sticky a:visited, .trow2.forumdisplay_sticky a:visited, .trow1.forumdisplay_regular a:visited, .trow2.forumdisplay_regular a:visited
  {
	  color: #808080 !important;
  }
  .subject_old a, .subject_new a
  {
	  color: #efeded !important;
  }
  .subject_old a:visited, .subject_new a:visited 
  {
	  color: #808080 !important;
  }
  .trow1 .author a
  {
	  color: #efeded !important;
  }
  .trow2 .author a
  {
	  color: #efeded !important;
  }
  a span[style*="#FFC800"]
  {
	  color: #e7ff32 !important;
  }
  a span[style*="#29CB09"]
  {
	  color: #3ee07a !important;
  }
  a span[style*="#CD1818"]
  {
	  color: #f42e87 !important;
  }
  .forum_on
  {
	  color: #808080 !important;
  }
  .thread_head
  {
	  border: 5px #2E3E49 !important;
	  background: #808080 !important;
  }
  .thead
  {
	  border: 4px #2E3E49 !important;
	  background: #808080 !important;
  }
  .tfoot
  {
	  border: 4px #2E3E49 !important;
	  background: #808080 !important;
  }
  .header
  {
	  background: #000000 !important;
  }
  #footer
  {
	  border-top: 5px solid #808080 !important;
	  background: #252526 !important;
  }
  .cRem
  {
	  color: #808080 !important;
	  background: #252526 !important;
  }
  .progress
  {
	  background: #2E3E49 !important;
  }
}
