css:
@-moz-document domain("sfflab.com") {
body {
    background-color: #0c0c0c;
}

.product-section-wrapper {
    background: linear-gradient(to bottom, #0a0a0a 0%, #0c0c0c 100%);
}

.logo {
    filter: drop-shadow(0px 0px 10px rgba(255,255,255,0.15));
}

select {
    background-color: #575757;
}

.select-wrapper, .newsletter-subscription form, input.s4com-input, input[type="text"], input[type="email"], input[type="password"], input[type="search"], input[type="telephone"], input[type="tel"], input[type="number"], textarea {
    border: 1px solid #333;
    background: #444;
}

input.header-search-input {
    border: 1px solid transparent;
    background: none;
}

input.newsletter-email {
    background: #444;
    border: none;
}

input.newsletter-submit {
    color: #333;
}

.button:hover, .submit:hover, button:hover, input[type="submit"]:hover, input[type="button"]:hover {
    color: #b5b5b5
}

.share-buttons a, .s4com-border-box {
    border: 1px solid #303030;
    background: #5a5a5a1c;
}

input.header-search-input:focus, li.mega-nav.active>a, ul.mega-nav-dropdown, .navigation-first-tier>.has-dropdown.active>a, .regular-nav ul a, .regular-nav ul {
    background: #757575;
    border-color: #5a5a5a;
}

li.mega-nav-item>ul {
    border-top: 1px solid #303030
}

img, .slide1, .slide2, .slide3, .slide4, .slide5 {
    filter: brightness(0.85);
}

input[type="email"], input[type="text"] {
    color: #000;
}

input.header-search-input {
    color: #303030;
}

.breadcrumbs .divider, .s4com-panel input {
    color: #5a5a5a;
}

.s4com-border-bottom, .s4com-panel {
    border-color: #5a5a5a;
}

.s4com-panel {
    background:#5a5a5a1c;
}

.tt-menu {
    background-color: #757575
}

.selected-text {
    color: #757575;
}

.alert-container-wrapper {
    background-color: #c1006ecc;
}

#product-section-wrapper-social-proof h1, #product-section-wrapper-social-proof h2, #product-section-wrapper-social-proof h3, #product-section-wrapper-social-proof h4, #product-section-wrapper-social-proof h5, #product-section-wrapper-social-proof p, #product-section-wrapper-social-proof ul, #product-section-wrapper-social-proof li, #product-section-wrapper-social-proof p, .alert-container-title, .alert-container-message, #product-section-wrapper-ncase h1, #product-section-wrapper-ncase h2, #product-section-wrapper-ncase h3, #product-section-wrapper-ncase h4, #product-section-wrapper-ncase h5, #product-section-wrapper-ncase p, #product-section-wrapper-ncase ul, #product-section-wrapper-ncase li, #product-section-wrapper-ncase p, #product-section-wrapper-nfc h1, #product-section-wrapper-nfc h2, #product-section-wrapper-nfc h3, #product-section-wrapper-nfc h4, #product-section-wrapper-nfc h5, #product-section-wrapper-nfc p, #product-section-wrapper-nfc ul, #product-section-wrapper-nfc li, #product-section-wrapper-nfc p, #product-section-wrapper-dan h1, #product-section-wrapper-dan h2, #product-section-wrapper-dan h3, #product-section-wrapper-dan h4, #product-section-wrapper-dan h5, #product-section-wrapper-dan p, #product-section-wrapper-dan ul, #product-section-wrapper-dan li, #product-section-wrapper-dan p, #product-section-wrapper-wallye h1, #product-section-wrapper-wallye h2, #product-section-wrapper-wallye h3, #product-section-wrapper-wallye h4, #product-section-wrapper-wallye h5, #product-section-wrapper-wallye p, #product-section-wrapper-wallye ul, #product-section-wrapper-wallye li, #product-section-wrapper-wallye p, #missionWrapper {
    color: #dadada;
}

#mc_embed_signup #mc-embedded-subscribe {
    background-color: #dadada;
}
}
