css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("qt.io")
{

  body, .sidebar-content
  {
    background-color: #000 !important;
    color: #BBB !important;
  }

  body li, body a
  {
    color: #888 !important;
  }

  div.table td
  {
    border: 1px solid #AA9 !important;
  }

  div.table th
  {
    border: 1px solid #AA9 !important;
    background-color: #666 !important;
  }

  div.table .odd
  {
    background-color: #444 !important;
  }

  div.table .preprocessor
  {
    color: #bd63c5 !important;
  }

  #mainmenu li a
  {
    color: #BBB !important;
  }

  #mainmenu li a:hover, body a:hover
  {
    color: #5CAA15 !important;
  }

  .container
  {
    background-color: #222 !important;
    color: #FFF !important;
  }

  .clearfix
  {
    color: #ABC !important;
  }

  .context, .context p, div#___gcse_1
  {
    background-color: #222 !important;
    color: #BBB !important;
  }

  .context a, .toc ul li a, .sectionlist ul li a
  {
    color: #46a2da !important;
  }

  .context a:hover, .toc ul li a:hover, .sectionlist ul li a:hover
  {
    color: #5CAA15 !important;
  }

  .context .title, h1, h2, h3, h4, h5
  {
    color: #AA9 !important;
  }

  h3.fn code
  {
    background-color: #222 !important;
  }

  .toc, .sectionlist
  {
    background-color: #222 !important;
  }

  .toc h3 a
  {
    color: #AA9 !important;
  }

  .cpp
  {
    background-color: #000 !important;
    color: #CCC !important;
  }

  .cpp .comment
  {
    color: #57A64A !important;
  }

  .cpp .type
  {
    color: #008080 !important;
  }

  .cpp .keyword
  {
    font-weight: bold !important;
    color: #008080 !important;
  }
  
  .flowList.odd {
    background-color: rgb(34, 34, 34);
}
#navbar {
    background-color: rgb(34, 34, 34);
}
  
}
