css:
@-moz-document domain("mod-one.com") {
/* // Main Page colors \\ */

.page {
    background-color: #333;
}

.header {
    background: #5d5d5d;
}

.owl-theme .owl-controls .owl-page {
    background: #ccc;
}

.footer-main {
    background: #5d5d5d;
    border-top: 1px solid #808080;
}

/* // Title and text colors \\ */

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6 {
    color: #000;
}

.Block h2 {
    color: #000;
    border-bottom: 1px solid #000000;
}

a, #LayoutColumn2 .YourAccount .list a, #LayoutColumn2 .Breadcrumb li a, .header-logo a, .Left li li a, .Left #SideCategoryList li a, .Left .slist li a, p a, .Left .afterSideShopByBrand a, .Left #GiftCertificatesMenu li a, .Left #SideAccountMenu li a, .Left #SideCategoryList li li a, .Left .slist li li a, .TabNav li.Active a, .ModalContent h4 a:hover, .footer a:hover {
    color: #0c0c0c;
}

body, input, select, textarea, input.Textbox, input[type="text"], #SearchForm input, .HorizontalFormContainer dd .selector span, .fastCartTop div, .list-icon li {
    color: #0c0c0c;
}

/* // Button and Arrow color and inversion settings \\ */

.Block h2 .arrow, .customNavigation a.prev, .customNavigation a.next {
    filter: invert(.8);
}

.customNavigation a.prev:hover, .customNavigation a.next:hover {
    filter: invert(0) brightness(0.25);
}

.owl-theme .owl-controls .owl-page.active span, .owl-theme .owl-controls.clickable .owl-page:hover span {
    background: none repeat scroll 0 0 #ccc;
    border: 2px solid #adadad;
}

.owl-theme .owl-controls .owl-page span {
    border: 2px solid #adadad;
    background: none repeat scroll 0 0 #adadad;
}

.owl-theme .owl-controls {
    filter: invert(1);
}

/* // Logo fix \\ */

.header-logo {
    filter:drop-shadow(1px 0px 1px #333) drop-shadow(-1px 0px 1px #555);
}

/* // Menu adjustments \\ */

.PageMenu {
    border-top: 1px solid #353535;
    box-shadow: 0 2px 4px -1px #353535;
}

.PageMenu #SideCategoryList li a:hover {
    color: #989898;
}

.PageMenu li:hover, .PageMenu .sf-menu li:hover {
    border-top: 1px solid #353535;
}

.PageMenu #SideCategoryList li li a:hover {
    background-color: #3a3a3a;
}

.PageMenu ul li ul li, .PageMenu .sf-menu li ul li {
    border-bottom: 1px solid #4a4a4a;
}

.PageMenu li ul {
    border: 1px solid #4a4a4a;
    background: none repeat scroll 0 0 #5d5d5d;
    box-shadow: 0 4px 4px -3px #4a4a4a inset;
}

.PageMenu li ul ul {
    border: 1px solid #4a4a4a !important;
}

.TopMenu li {
    border-left: 2px solid #4a4a4a;
}

.Search {
    border-right: 2px solid #4a4a4a;
}
    
.Search, .icon {   
    filter: brightness(0.5);
}

.searchtoggle {
    background: none repeat scroll 0 0 #adadad;
    border: 1px solid #777777;
}

input.Textbox, textarea, input[type="text"], input[type="password"] {
    background: #adadad;
}

.PageMenu.fixed {
    background: none repeat scroll 0 0 #5d5d5d;
}

.footer-links, .flexslider {
    background: #333;
}

.footer-links .inner {
    filter: invert(.8);
}

.footer-links p {
    background: none repeat scroll 0 0 #ffffff;
}

.ProductList .ProductImage, .brand-img, #ProductDetails .ProductThumbImage {
    border: 1px solid #202020;
}

.CurrencyList dd, #SearchForm .Button, #SearchForm input.Textbox, select, hr, input.Textbox, textarea, input[type="text"], input[type="password"], .QuickSearch, .SharePublicWishList, .ExpressCheckoutBlock .ExpressCheckoutTitle, .HowToPay, .NotifyMessage, .QuickSearchResult .QuickSearchResultImage, .CurrencyChooser, .selected-currency, div.uploader, div.selector, .recaptchatable {
    border: 1px solid #4a4a4a;
}

.Left .CategoryList .SideCategoryListFlyout li ul, .Right .CategoryList .SideCategoryListFlyout li ul {
    background: none repeat scroll 0 0 #5d5d5d !important;
    border: 1px solid #4a4a4a !important;
}

/* // Testimonials \\ */

.testimonial-description {
    background: none repeat scroll 0 0 #383838;
}

.testimonial .product-block a {
    filter: invert(0.8);
    color: #959595;
}


/* // Image darken \\ */

img {
    filter: brightness(0.85);
}

/* // OTHER \\ */

.Left h2, .Right h2 {
    border-bottom: 1px solid #4a4a4a;
}

.Left #SideCategoryList li a, .Left .slist li a, .Left .BlockContent li a, .Left .afterSideShopByBrand a, .Left #GiftCertificatesMenu li a, .Left #SideAccountMenu li a {
    filter: brightness(0.65);
}

.product-nav, .CategoryPagination .PagingList li, .Block.Moveable.Panel.PagingBottom, .nav-prev, .nav-next, .productOptionViewProductPickListWithImage td, .ProductDetailsGrid .DetailRow.PriceRow, .ProductDetailsGrid .DetailRow.qty, .CreateAccount, .AccountLogin, .borderB, table.General, table.CartContents, table.General, table tbody tr td, table.General tbody tr td, table.CartContents tbody tr td, .li-list {
    border-color: #4a4a4a;
}

.productOptionViewProductPickListWithImage {
    border-color: #4a4a4a !important;
}

div.selector {
    filter: invert(0.75);
    border: 1px solid #EEEEEE;
}

div.selector select {
    background-color: #505050 !important;
}

.productOptionViewRectangle .option label {
    background-color: #5e5e5e;
}

.productOptionPickListSwatch .textureContainer, .productOptionPickListSwatch .swatchColours, .productOptionViewRectangle .option label, .recaptchatable #recaptcha_response_field, .zoomWrapper, #ProductDetails .ProductTinyImageList ul li.selected {
    border: 1px solid #4a4a4a !important;
}

html.javascript .productOptionViewRectangle label {
    color: #000;
}

.SuccessMessage {
    background: #223e19;
    border-color: #568844;
}

.CartContents thead {
    background: #4a4a4a;
}

table thead tr th, table.General thead tr th, table.CartContents thead tr th, table tbody tr td, table.General tbody tr td, table.CartContents tbody tr td {
    color: #909090;
}
}
