@-moz-document domain("ritaj.birzeit.edu") {
/*Style by salah aldin*/

/*Cursor by sneekzxy*/

/* Start http://www.cursors-4u.com */
 * {
    cursor: url(http://cur.cursors-4u.net/cursors/cur-2/cur138.cur), auto !important;
}
/* End http://www.cursors-4u.com */
 body {
    font-size:14px;
    background-color:white;
    margin: 0 auto;
}
table {
    margin:0 auto;
}
.b {
    text-align:center;
    font-size: 16px;
}
table td {
    font-weight: bold;
    font-size:13px;
}
td b {
    text-align:center;
    font-size: 16px;
}
a:link, a:visited {
    font-size:14px;
    background-color: white;
    color: black;
    border: 2px solid green;
    padding: 6px 8px;
    margin:2px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    position:relative;
    margin-left:auto;
    margin-right:auto;
}
a:hover, a:active {
    background-color: green;
    color: white;
}

#show_hide_courses {
    display:none;
}

.ritaj-logo:link, .ritaj-logo:visited {
    font-size:14px;
    background-color: transparent;
    color: black;
    border: none;
    padding: 6px 8px;
    margin:2px;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    position:relative;
    margin-left:auto;
    margin-right:auto;
}
.ritaj-logo:hover, .ritaj-logo:active {
    background-color: transparent;
    color: green;
}
#course_menu li {
    list-style-type:none;
}


.wbr, sgcell {
    font-weight: bold;
    font-size: 17px;
}
/*Change the style of the schedule*/
 div {
    margin:0;
    margin-left:auto;
    margin-right:auto;
}
/*Change the style of register schedule*/
 .sched div {
    font-size:20px;
}
.hcell {
    font-size:15px;
}
.fcell {
    font-size:10px;
}
.site-name::after {
    content:"(❤❤❤_____(صلاح الدين) Style by Salah-Aldin Amera_____❤❤❤)"
}
.footer_container div {
    display:none;
}
.footer_container::before {
    content:"FB-account : @salah.amera";
}
.footer_container {
    padding: 6px 8px;
    position:relative;
    width:240px;
    margin:0 auto;
}
.footer_container hr {
    visibility:hidden;
}
}

