/*Created by Eddie Lucas - DO NOT REDISTRIBUTE*/

@-moz-document domain("pastebin.com")
{
  body, #main_frame
  {
    background: rgb(25,25,25)!important;
    color: #CCC!important;
  }

float-box-3 
{
width:330px;
height:90px;
/*background:#fff;*/
padding:10px;
margin-top:10px;
border-radius:4px;
box-shadow:rgba(0,0,0,.168627) 3px 3px 3px 0;
position:relative;
}

/* Display for detecting AdBlock */
  #abrpm
  {
    display: none!important;
  }

/* Back to top button */
  .cd-top
  {
    background-color: rgb(45,45,45)!important;
    border: 1px solid #000!important;
  }

  #header a
  {
    color: #FFF!important;
  }

  #header a:hover, .content_right_title a:hover
  {
    color: #6987AB!important;
  }

  a, .paste_box_info h1, .content_title
  {
    color: #BBB!important;
  }

  a:hover
  {
    color: #FFF!important;
  }

  .paste_box_icon, .form_avatar
  {
    border: 1px solid #000!important;
    padding: 2px!important;
    background: #333!important;
  }

  .maintable th, .maintable td
  {
    border-bottom: 1px solid #999!important;
  }

  .maintable tr:hover
  {
    background-color: rgb(45,45,45)!important;
  }

  .buttonsm
  {
    background-color: rgb(16,16,16);
    border-bottom: 2px solid #000;
    border-radius: 3px;
    color: #000;
    opacity: .75;
  }

  #monster_frame
  {
    background-color: rgb(16,16,16)!important;
    -moz-box-shadow: none!important;
    -webkit-box-shadow: none!important;
    box-shadow: none!important;
    border: 1px solid rgb(0,0,0)!important;
    -moz-border-radius: 0px!important;
    -webkit-border-radius: 0px!important;
    border-radius: 0px!important;
  }

  #code_frame2
  {
    border: none!important;
  }

  #code_frame, ol
  {
    background-color: rgb(45,45,45)!important;
  }

  #code_buttons
  {
    color: #BBB!important;
    background: rgb(65,65,65)!important;
    border-bottom: 1px solid #000!important;
  }

  .de1, .de2
  {
    color: #BBB!important;
    border-left: 1px solid #000!important;
    background: rgb(25,25,25)!important;
  }

  .textarea_border, #notice
  {
    background: rgb(45,45,45)!important;
    border: 1px solid #000!important;
    color: #BBB!important;
  }

  textarea.paste_code, textarea.paste_textarea
  {
    border: none!important;
  }

  .select2-container--default .select2-selection--single
  {
    background-color: rgb(36,36,36)!important;
    border-color: #000!important;
  }

  .select2-container--default .select2-selection--single .select2-selection__rendered
  {
    color: #CCC!important;
  }

  .select2-container--default .select2-results__option[aria-selected=true]
  {
    background-color: rgb(76,76,76)!important;
    color: #BBB!important;
  }

  .select2-container--default .select2-results__option--highlighted[aria-selected]
  {
    background-color: rgb(56,56,56)!important;
  }

  .select2-dropdown
  {
    background-color: rgb(36,36,36)!important;
    border: 1px solid #000!important;
  }

  #header
  {
    border: 1px solid #000!important;
    border-top: none!important;
    -moz-border-radius: 0px!important;
    -webkit-border-radius: 0px!important;
    border-radius: 0px!important;
  }

  .search_input, input.post_input, select.post_select, .button1
  {
    background: rgb(45,45,45)!important;
    color: #DDD!important;
    border: 1px solid #000!important;
    -webkit-border-radius: 3px;
    border-radius: 0px;
  }

  textarea, input, #footer_top, input.post_input, select.post_select
  {
    background: rgb(45,45,45)!important;
    color: #DDD!important;
  }

  textarea:focus, input:focus, select:focus
  {
    outline: none !important;
    border-color: #000!important;
    box-shadow: 0px 0px 5px 2px rgb(0,0,0)!important;
  }

  #footer_top
  {
    border-top: 1px solid #000!important;
    margin: 0px!important;
  }

  #footer_links, #footer_links a, #footer_top a, .footer_top_title, a
  {
    text-shadow: none!important;
  }

  div#content_right
  {
    background: none!important;
  }

/* mIRC */

  .de1, .de2
  {
    color: #BBB!important;
    background: rgb(25,25,25)!important;
    border-left: 1px solid #000!important;
  }

  .re1, .sy0
  {
    color: #BBB!important;
  }

  .re2, .re0, .st0
  {
    color: #08F!important;
  }

  .re3, .re4
  {
    color: #BB0!important;
  }

  .me0, .sc3, .re2
  {
    color: #0B0!important;
  }

  .kw1
  {
    color: #B77!important;
  }

  .kw2
  {
    color: #FFF!important;
  }

  .kw3
  {
    color: #B00!important;
  }
}
